const resizeDirection = ref<string | null>(null)
const resizeStartPos = ref({ x: 0, y: 0 })
const resizeStartSize = ref({ width: 0, height: 0 })

const startResize = (direction: string, e: MouseEvent | TouchEvent) => {
  if (!props.enableResize) return

  isResizing.value = true
  resizeDirection.value = direction
  resizeStartSize.value = { ...size.value }

  triggerHaptic({ intensity: 15 })

  const onMouseMove = (e: MouseEvent) => handleResize(e.clientX, e.clientY)
  const onMouseUp = () => {
    isResizing.value = false
    resizeDirection.value = null
    document.removeEventListener('mousemove', onMouseMove)
    document.removeEventListener('mouseup', onMouseUp)
  }

  resizeStartPos.value = {
    x: e instanceof MouseEvent ? e.clientX : (e instanceof TouchEvent && e.touches[0] ? e.touches[0].clientX : 0),
    y: e instanceof MouseEvent ? e.clientY : (e instanceof TouchEvent && e.touches[0] ? e.touches[0].clientY : 0)
  }

  document.addEventListener('mousemove', onMouseMove)
  document.addEventListener('mouseup', onMouseUp)
}

const handleResize = (clientX: number, clientY: number) => {
  if (!resizeDirection.value) return

  const deltaX = clientX - resizeStartPos.value.x
  const deltaY = clientY - resizeStartPos.value.y

  const dir = resizeDirection.value
  const minWidth = 280
  const minHeight = 200
