// Resize functionality
const resizeDirection = ref<string | null>(null)
const resizeStartPos = ref({ x: 0, y: 0 })
const resizeStartSize = ref({ width: 0, height: 0 })

const startResize = (direction: string, e: MouseEvent | TouchEvent) => {
  if (!props.enableResize) return

  isResizing.value = true
  resizeDirection.value = direction
  resizeStartSize.value = { ...size.value }

  triggerHaptic({ intensity: 15 })

  const onMouseMove = (e: MouseEvent) => handleResize(e.clientX, e.clientY)
  const onMouseUp = () => {
    isResizing.value = false
    resizeDirection.value = null
    document.removeEventListener('mousemove', onMouseMove)
    document.removeEventListener('mouseup', onMouseUp)
  }

  resizeStartPos.value = {
    x: e instanceof MouseEvent ? e.clientX : (e instanceof TouchEvent && e.touches[0] ? e.touches[0].clientX : 0),
    y: e instanceof MouseEvent ? e.clientY : (e instanceof TouchEvent && e.touches[0] ? e.touches[0].clientY : 0)
  }

  document.addEventListener('mousemove', onMouseMove)
  document.addEventListener('mouseup', onMouseUp)
}

const handleResize = (clientX: number, clientY: number) => {
  if (!resizeDirection.value) return

  const deltaX = clientX - resizeStartPos.value.x
  const deltaY = clientY - resizeStartPos.value.y

  const dir = resizeDirection.value
  const minWidth = 280
  const minHeight = 200
  const maxWidth = window.innerWidth - 40
  const maxHeight = window.innerHeight - 80

  let newWidth = resizeStartSize.value.width
  let newHeight = resizeStartSize.value.height
  let newX = dragX.value
  let newY = dragY.value

  // East/West resizing
  if (dir.includes('e')) {
    newWidth = Math.max(minWidth, Math.min(maxWidth, resizeStartSize.value.width + deltaX))
  }
  if (dir.includes('w')) {
    const proposedWidth = Math.max(minWidth, Math.min(maxWidth, resizeStartSize.value.width - deltaX))
    const widthDiff = proposedWidth - newWidth
    newWidth = proposedWidth
    newX = position.value.x - widthDiff
  }

  // North/South resizing
  if (dir.includes('s')) {
    newHeight = Math.max(minHeight, Math.min(maxHeight, resizeStartSize.value.height + deltaY))
  }
  if (dir.includes('n')) {
    const proposedHeight = Math.max(minHeight, Math.min(maxHeight, resizeStartSize.value.height - deltaY))
    const heightDiff = proposedHeight - newHeight
    newHeight = proposedHeight
    newY = position.value.y - heightDiff
  }

  size.value = { width: newWidth, height: newHeight }

  // Update position for west/north resizing
  if (dir.includes('w') || dir.includes('n')) {
    dragX.value = newX
    dragY.value = newY
    position.value = { x: newX, y: newY }
  }
}

