   137‚Üí<script setup lang="ts">
   138‚Üíimport { ref, computed, capitalize } from "vue";
   139‚Üíimport { Icon } from "@iconify/vue";
   140‚Üíimport { useMounted, computedAsync } from "@vueuse/core";
   141‚Üíimport { DateTime } from "luxon";
   142‚Üíimport MediaViewer from "@vue/display/MediaViewer.vue";
   143‚Üíimport PrimaryButton from "@vue/buttons/PrimaryButton.vue";
   144‚Üíimport { useNotification } from "@/utils/useNotification";
   145‚Üíimport { updatePostContainer } from "@/stores/postContainerStore";
   146‚Üíimport type { PostContainer } from "@appwrite/schemas/postContainer";
   147‚Üíimport type { GenericPost } from "@/schemas/posts/genericPost";
   148‚Üíimport ProviderCard from "./ProviderCard.vue";
   149‚Üíimport type { OAuthAccount } from "@appwrite/schemas/oAuthAccount";
   150‚Üíimport { getAccount } from "@/stores/oauthStore";
   151‚Üíimport type { POST_TYPES } from "@/constants/providerConstants";
   152‚Üíimport { getProviderIcon, formatProviderName } from "@/utils/providerUtils";
   153‚Üíimport type { Models } from "appwrite";
   154‚Üí
   155‚Üíconst props = defineProps<{
   156‚Üí  post: PostContainer;
   157‚Üí  teamId: string;
   158‚Üí  oAuthAccount?: OAuthAccount;
   159‚Üí  expandable?: boolean;
   160‚Üí  jwt?: Models.Jwt;
   161‚Üí}>();
   162‚Üí
   163‚Üíconst expanded = ref(!props.expandable);
   164‚Üíconst mounted = useMounted();
   165‚Üíconst loading = ref(true);
   166‚Üíconst isRetrying = ref(false);
   167‚Üíconst { confirm, success, error: showError } = useNotification();
   168‚Üí
   169‚Üíconst oAuthAccount = computedAsync(
   170‚Üí  async () => {
   171‚Üí    if (!mounted.value) return undefined;
   172‚Üí    if (!props.oAuthAccount) {
   173‚Üí      return await getAccount(props.post.oauthAccountId!);
   174‚Üí    }
   175‚Üí    return props.oAuthAccount;
   176‚Üí  },
   177‚Üí  undefined,
   178‚Üí  loading
   179‚Üí);
   180‚Üí
   181‚Üíconst genericPost = computed<GenericPost>(() => {
   182‚Üí  const postParsed = JSON.parse(props.post.postData);
   183‚Üí  console.log(postParsed);
   184‚Üí  return postParsed as GenericPost;
   185‚Üí});
   186‚Üí
   187‚Üíconst platformIcon = computed(() => {
   188‚Üí  return getProviderIcon(props.post.platform?.toLowerCase());
   189‚Üí});
   190‚Üí
   191‚Üíconst handleExpand = () => {
   192‚Üí  if (props.expandable) {
   193‚Üí    expanded.value = true;
   194‚Üí  }
   195‚Üí};
   196‚Üí
   197‚Üíconst retryIfFailed = async (post: PostContainer) => {
   198‚Üí  if (post.status === "failed" && post.executionId && post.needsUpdate === false && !isRetrying.value) {
   199‚Üí    try {
