   111‚Üíconst getSocialIcon = (platform?: string) => {
   112‚Üí  const icons = {
   113‚Üí    twitter: "mdi:twitter",
   114‚Üí    linkedin: "mdi:linkedin",
   115‚Üí    instagram: "mdi:instagram",
   116‚Üí    facebook: "mdi:facebook",
   117‚Üí    tiktok: "ic:baseline-tiktok",
   118‚Üí  };
   119‚Üí  return platform ? icons[platform as keyof typeof icons] || "mdi:account" : "mdi:account";
   120‚Üí};
   121‚Üí
   122‚Üíconst formatFollowers = (count: number) => {
   123‚Üí  if (count >= 1000000) {
   124‚Üí    return `${(count / 1000000).toFixed(1)}M`;
   125‚Üí  } else if (count >= 1000) {
   126‚Üí    return `${(count / 1000).toFixed(1)}K`;
   127‚Üí  }
   128‚Üí  return count.toString();
   129‚Üí};
