   914‚Üí  color: white;
   915‚Üí}
   916‚Üí
   917‚Üí/* Performance optimizations */
   918‚Üí.will-change-transform {
   919‚Üí  will-change: transform;
   920‚Üí}
   921‚Üí
   922‚Üí.will-change-opacity {
   923‚Üí  will-change: opacity;
   924‚Üí}
   925‚Üí
   926‚Üí.gpu-acceleration {
   927‚Üí  transform: translateZ(0);
   928‚Üí  backface-visibility: hidden;
   929‚Üí  perspective: 1000;
   930‚Üí}
   931‚Üí
   932‚Üí/* Battery optimization styles */
   933‚Üí@media (max-width: 768px) {
   934‚Üí  .battery-optimize {
   935‚Üí    animation-duration: 0s !important;
   936‚Üí    transition-duration: 0.1s !important;
   937‚Üí  }
   938‚Üí  
   939‚Üí  .battery-optimize .glass-medium,
   940‚Üí  .battery-optimize .glass-strong {
   941‚Üí    backdrop-filter: none !important;
   942‚Üí    -webkit-backdrop-filter: none !important;
   943‚Üí    background: var(--color-surface-primary) !important;
   944‚Üí  }
   945‚Üí}
   946‚Üí
   947‚Üí/* Print styles */
   948‚Üí@media print {
   949‚Üí  .no-print {
   950‚Üí    display: none !important;
   951‚Üí  }
   952‚Üí  
   953‚Üí  .notifications-container,
   954‚Üí  .chat-widget-container,
   955‚Üí  .connection-indicator {
   956‚Üí    display: none !important;
   957‚Üí  }
   958‚Üí  
   959‚Üí  * {
   960‚Üí    background: transparent !important;
   961‚Üí    color: black !important;
   962‚Üí    box-shadow: none !important;
   963‚Üí    text-shadow: none !important;
   964‚Üí  }
   965‚Üí}
   966‚Üí
   967‚Üí/* ===== MOBILE OPTIMIZATIONS ===== */
   968‚Üí
   969‚Üí/* Touch optimization styles */
   970‚Üí.touch-optimized {
   971‚Üí  -webkit-tap-highlight-color: transparent;
   972‚Üí  -webkit-touch-callout: none;
   973‚Üí  -webkit-user-select: none;
   974‚Üí  user-select: none;
