---
import Navbar from "@vue/global/Navbar.vue";
import SideMenu from "@vue/global/SideMenu.vue";
import Footer from "@vue/global/Footer.vue";
import GlobalActionWidget from "@vue/global/GlobalActionWidget.vue";
import GlobalActionWidgetWithOverrides from "@vue/global/GlobalActionWidgetWithOverrides.vue";
import SmartNotifications from "@vue/global/SmartNotifications.vue";
import DataProvider from "@components/providers/DataProvider.vue";
import CreatePostProvider from "@vue/providers/CreatePostProvider.vue";
import SessionInitializer from "@vue/global/SessionInitializer.vue";
import MetadataTags from "@components/global/MetadataTags.astro";
import ClarityInit from "@components/global/ClarityInit.astro";
import CookieGuard from "@vue/guards/CookieGuard.vue";
import DocsModal from "@vue/modals/DocsModal.vue";
import { SITE_DATA } from "@/constants/siteData";
import { getCollection } from 'astro:content';
import { docCategories } from '@/content/docs/categories';
import type { DocSummary } from '@/types/docs';

const siteData = SITE_DATA;

interface Props {
  title: string;
}

const sessionUser = await Astro.session?.get("authUser");

const { title } = Astro.props;

// Fetch doc summaries for DocsModal without shipping full markdown bodies
const docsCollection = await getCollection('docs');
const docSummaries: DocSummary[] = docsCollection.map((doc) => ({
  slug: doc.slug,
  title: doc.data.title,
  description: doc.data.description,
  category: doc.data.category,
  order: doc.data.order ?? null,
  tags: doc.data.tags,
  relatedSections: doc.data.relatedSections,
  lastUpdated: doc.data.lastUpdated ? doc.data.lastUpdated.toISOString() : null,
  author: doc.data.author ?? null,
}));

// Prevent caching of authenticated pages to avoid logout issues
Astro.response.headers.set('Cache-Control', 'private, no-cache, no-store, must-revalidate');
Astro.response.headers.set('Pragma', 'no-cache');
Astro.response.headers.set('Expires', '0');
---

<!doctype html>
<html lang="en">
  <head>
    <MetadataTags title={title} description={siteData.description} />
    <style is:global>
      html {
        --bg-color-default: #333;
        --text-color-default: white;
        color: white;
        background-color: #333;
        font-family: system-ui, sans-serif;
      }

      html,
      body,
      #body {
        height: 100% !important;
      }

      [data-theme="dark"] {
        --bg-color-default: #333;
        --text-color-default: white;
        color: white;
        background-color: #333;
      }

      [data-theme="light"] {
        --bg-color-default: white;
        --text-color-default: black;
        color: black;
        background-color: white;
      }

      code {
        font-family:
          Menlo,
          Monaco,
          Lucida Console,
          Liberation Mono,
          DejaVu Sans Mono,
          Bitstream Vera Sans Mono,
          Courier New,
          monospace;
      }
    </style>
    <ClarityInit />
  </head>
  <body
    id="body"
    class="relative min-h-screen flex flex-col overflow-hidden touch-optimized mobile-a11y"
  >
    <Navbar client:load transition:persist user={sessionUser} />
    <div class="flex-1 flex overflow-hidden">
      <SideMenu client:load />
      <div
        class="flex-1 flex flex-col overflow-y-auto bg-gray-100 dark:bg-gray-900 transition-colors duration-300"
      >
        <DataProvider client:load>
          <slot />
        </DataProvider>
        <Footer client:load transition:persist />
      </div>
    </div>

    <CookieGuard client:load transition:persist />
    <CreatePostProvider client:load />
    <SessionInitializer client:load />

    <!-- Teleport Layer: keep this as a single top-level target for modals/popovers -->
    <div id="teleport-layer"></div>

    <!-- Global Action Widget -->
    <GlobalActionWidgetWithOverrides
      client:load
      position="bottom-right"
      glass="subtle"
      show-chat-actions={true}
      transition:persist="global-action-widget"
    />

    <!-- Smart Notifications -->
    <SmartNotifications
      client:load
      position="top-right"
      max-visible={5}
      enable-ai-prioritization={true}
      enable-stacking-prevention={true}
      enable-smart-grouping={true}
      transition:persist
    />

    <!-- Documentation Modal -->
    <DocsModal
      client:idle
      docs={docSummaries}
      categories={docCategories}
    />

  </body>
</html>

---
export const prerender = true;
import Layout from "../layouts/Layout.astro";
import LoginView from "@/components/vue/views/auth/LoginView.vue";
---

<Layout title="Home">
  <main>
    <LoginView client:load />
  </main>
</Layout>

---
import { ClientRouter } from "astro:transitions";
import Themes from "astro-themes";
import "@fontsource/noto-color-emoji";
import "@fontsource/inter";
import "../../styles/global.css";
import "sweetalert2/src/sweetalert2.scss";

interface Props {
  title: string;
  description: string;
  image?: string;
  noindex?: boolean;
}

const {
  title,
  description,
  image = "/og-image.jpg",
  noindex = false,
} = Astro.props;

const canonicalURL = new URL(Astro.url.pathname, Astro.site);
const siteName = "Socialaize";
const fullTitle = `${title} - ${siteName}`;
---

<!-- Essential Meta Tags -->
<meta charset="UTF-8" />
<meta name="viewport" content="width=device-width, initial-scale=1.0" />
<meta name="generator" content={Astro.generator} />

<!-- SEO Meta Tags -->
<title>{fullTitle}</title>
<meta name="description" content={description} />
<link rel="canonical" href={canonicalURL} />
{noindex && <meta name="robots" content="noindex" />}

<!-- OpenGraph Meta Tags -->
<meta property="og:title" content={fullTitle} />
<meta property="og:description" content={description} />
<meta property="og:type" content="website" />
<meta property="og:url" content={canonicalURL} />
<meta property="og:image" content={new URL(image, Astro.url)} />
<meta property="og:site_name" content={siteName} />

<!-- Twitter Meta Tags -->
<meta name="twitter:card" content="summary_large_image" />
<meta name="twitter:title" content={fullTitle} />
<meta name="twitter:description" content={description} />
<meta name="twitter:image" content={new URL(image, Astro.url)} />

<!-- Favicon -->
<link rel="icon" type="image/webp" href="/favicon.webp" />

<!-- Theme Color -->
<meta
  name="theme-color"
  content="#ffffff"
  media="(prefers-color-scheme: light)"
/>
<meta
  name="theme-color"
  content="#333333"
  media="(prefers-color-scheme: dark)"
/>

<!-- Client Router for Transitions -->
<ClientRouter />

<!-- Themes Handler (light/dark mode) -->
<Themes defaultTheme="dark" />

