---
import DashLayout from "@layouts/DashLayout.astro";
import BillingView from "@vue/views/billing/BillingView.vue";

const user = await Astro.session?.get("authUser");

// Extract URL parameters for upgrade navigation
interface UpgradeNavigationProps {
  step?: 'choose' | 'confirm' | 'checkout';
  suggested?: string;
  source?: 'accounts' | 'storage' | 'ai-credits' | 'teams' | 'workflows' | 'general';
  urgency?: 'low' | 'medium' | 'high';
}

const upgradeNavigation: UpgradeNavigationProps = {
  step: Astro.url.searchParams.get("step") as any,
  suggested: Astro.url.searchParams.get("suggested") ?? undefined,
  source: Astro.url.searchParams.get("source") as any,
  urgency: Astro.url.searchParams.get("urgency") as any,
};

// Only pass upgrade navigation if at least one parameter is present
const hasUpgradeParams = upgradeNavigation.step || upgradeNavigation.suggested || upgradeNavigation.source || upgradeNavigation.urgency;
---

<DashLayout title="Billing">
  <main>
    <BillingView
      client:load
      user={user}
      upgradeNavigation={hasUpgradeParams ? upgradeNavigation : undefined}
    />
  </main>
</DashLayout>

