---
import DashLayout from "@layouts/DashLayout.astro";
import TeamInviteView from "@vue/views/teams/TeamInviteView.vue";
import TeamView from "@vue/views/teams/TeamView.vue";
interface Props {
  teamId: string;
}

const { teamId } = Astro.props as Props;
if (!teamId) {
  Astro.redirect("/404");
}
const isInvite =
  Astro.url.searchParams.get("secret") !== null &&
  Astro.url.searchParams.get("membershipId") !== null;

let invitedTeamId = null;
let membershipId = null;
let secret = null;
let teamName = null;
let userId = null;


if (isInvite) {
  invitedTeamId = Astro.url.searchParams.get("teamId");
  membershipId = Astro.url.searchParams.get("membershipId");
  secret = Astro.url.searchParams.get("secret");
  teamName = Astro.url.searchParams.get("teamName");
  userId = Astro.url.searchParams.get("userId");
}

---

<DashLayout title="Team Invitation">
  <main>
    {
      isInvite ? (
        <TeamInviteView teamId={invitedTeamId ?? ""} membershipId={membershipId ?? ""} secret={secret ?? ""} teamName={teamName ?? ""} userId={userId ?? ""} client:load />
      ) : (
        <TeamView teamId={teamId} client:load />
      )
    }
  </main>
</DashLayout>

