import { persistentAtom } from "@nanostores/persistent";
import {
  Query,
  type Models,
  ExecutionMethod,
} from "appwrite";
import {
  databaseId,
} from "@/constants/appwrite";
import { z } from "zod";
import type { ULIDFactory } from "ulid-workers";
import { useAppwrite } from "@composables/useAppwriteClient";


export class BaseStore<T extends z.ZodType> {
  protected collectionId: string;
  protected schema: T;
  protected databaseId: string | undefined;
  atom: ReturnType<typeof persistentAtom<z.infer<T> | undefined>>;
  private ulidInstance: ULIDFactory | null = null;

  constructor(
    collectionId: string,
    schema: T,
    atomKey: string,
    databaseId?: string
  ) {
    this.collectionId = collectionId;
    this.schema = schema;
    this.atom = persistentAtom<z.infer<T> | undefined>(atomKey, undefined, {
      encode: JSON.stringify,
      decode: (val) => {
        try {
          const parsed = JSON.parse(val);
          return parsed ? this.schema.parse(parsed) : undefined;
        } catch (e) {
          return undefined;
        }
      },
    });
    this.databaseId = databaseId;
  }

  get client() {
    const { client } = useAppwrite();
    return client;
  }

  get db() {
    const { databases } = useAppwrite();
    return databases;
  }

  get teams() {
    const { teams } = useAppwrite();
    return teams;
  }

  get storage() {
    const { storage } = useAppwrite();
    return storage;
  }

  get functions() {
    const { functions } = useAppwrite();
    return functions;
  }

  getUlidFactory = async (): Promise<ULIDFactory> => {
    if (!this.ulidInstance) {
      const { ulidFactory } = await import("ulid-workers");
      this.ulidInstance = ulidFactory();
    }
    return this.ulidInstance;
  };

  get storedValue(): z.infer<T> | undefined {
    return this.atom.get();
  }

  set storedValue(value: z.infer<T> | undefined) {
    this.atom.set(value);
  }

  updateStoredValue(value: Partial<z.infer<T>>) {
    const current = this.storedValue;
    if (JSON.stringify(current) === JSON.stringify(value)) return;
    this.atom.set({
      ...current,
      ...value,
    });
  }

  clearStoredValue() {
    this.atom.set(undefined);
  }

  async create(
    data: z.infer<T>,
    docId?: string,
    permissions?: string[],
    databaseId?: string
  ): Promise<z.infer<T>> {
    try {
      delete data.$id;
      delete data.$createdAt;
      delete data.$updatedAt;
      delete data.$databaseId;
      delete data.$collectionId;
      delete data.$permissions;
      const ulid = await this.getUlidFactory();
      const doc = await this.db.createDocument(
        databaseId || this.databaseId!,
        this.collectionId,
        docId || ulid(),
        data,
        permissions
      );
      return this.schema.parse(doc);
    } catch (e) {
      console.error(`Failed to create document in ${this.collectionId}:`, e);
      throw e;
    }
  }

  async get(docId: string, databaseId?: string): Promise<z.infer<T> | null> {
    try {
      const doc = await this.db.getDocument(
        databaseId || this.databaseId!,
        this.collectionId,
        docId
      );
      return this.schema.parse(doc);
    } catch (e) {
      console.error(`Failed to get document in ${this.collectionId}:`, e);
      return null;
    }
  }

  async getRaw(docId: string, databaseId?: string): Promise<Models.Document> {
    try {
      const doc = await this.db.getDocument(
        databaseId || this.databaseId!,
        this.collectionId,
        docId
      );
      return doc;
    } catch (e) {
      console.error(`Failed to get document in ${this.collectionId}:`, e);
      throw e;
    }
  }

  async listRaw(
    queries: string[] = [],
    databaseId?: string
  ): Promise<Models.Document[]> {
    const docs = await this.db.listDocuments(
      databaseId || this.databaseId!,
      this.collectionId,
      queries
    );
    return docs.documents;
  }

  async update(
    docId: string,
    data: Partial<z.infer<T>>,
    permissions?: string[],
    databaseId?: string
  ): Promise<z.infer<T>> {
    try {
      let updatedData: any = data;
      delete updatedData.$id;
      delete updatedData.$createdAt;
      delete updatedData.$updatedAt;
      delete updatedData.$databaseId;
      delete updatedData.$collectionId;
      delete updatedData.$permissions;
      const doc = await this.db.updateDocument(
        databaseId || this.databaseId!,
        this.collectionId,
        docId,
        data,
        permissions
      );
      return this.schema.parse(doc);
    } catch (e) {
      console.error(`Failed to update document in ${this.collectionId}:`, e);
      throw e;
    }
  }

  async upsert(
    docId: string,
    data: z.infer<T>,
    permissions?: string[],
    databaseId?: string
  ): Promise<z.infer<T>> {
    try {
      let upsertData: any = { ...data };
      delete upsertData.$id;
      delete upsertData.$createdAt;
      delete upsertData.$updatedAt;
      delete upsertData.$databaseId;
      delete upsertData.$collectionId;
      delete upsertData.$permissions;
      try {
        const doc = await this.db.updateDocument(
          databaseId || this.databaseId!,
          this.collectionId,
          docId,
          upsertData,
        );
        return this.schema.parse(doc);
      } catch (updateError: any) {
        if (updateError.code === 404 || updateError.type === "document_not_found") {
          const doc = await this.db.createDocument(
            databaseId || this.databaseId!,
            this.collectionId,
            docId,
            upsertData,
            permissions
          );
          return this.schema.parse(doc);
        }
        throw updateError;
      }
    } catch (e) {
      console.error(`Failed to upsert document in ${this.collectionId}:`, e);
      throw e;
    }
  }

  async delete(docId: string, databaseId?: string): Promise<boolean> {
    try {
      await this.db.deleteDocument(
        databaseId || this.databaseId!,
        this.collectionId,
        docId
      );
      return true;
    } catch (e) {
      console.error(`Failed to delete document in ${this.collectionId}:`, e);
      throw e;
    }
  }

  async countDocsInCollection(
    queries: string[] = [],
    databaseId?: string
  ): Promise<number> {
    const countExecution = await this.functions.createExecution(
      "counter",
      JSON.stringify({
        databaseId: databaseId || this.databaseId!,
        collectionId: this.collectionId,
        queries: queries,
      }),
      false,
      undefined,
      ExecutionMethod.POST,
      {
        "Content-Type": "application/json",
      }
    );
    const count = JSON.parse(countExecution.responseBody)?.count || 0;
    return count;
  }

  async list(
    queries: string[] = [],
    databaseId?: string
  ): Promise<z.infer<T>[]> {
    try {
      const docs = await this.db.listDocuments(
        databaseId || this.databaseId!,
        this.collectionId,
        [...queries]
      );
      return docs.documents.map((doc) => this.schema.parse(doc));
    } catch (e) {
      console.error(`Failed to list documents in ${this.collectionId}:`, e);
      throw e;
    }
  }

  async listWithTotal(
    queries: string[] = [],
    databaseId?: string
  ): Promise<{ documents: z.infer<T>[]; total: number }> {
    const docs = await this.list(queries, databaseId);
    const total = await this.countDocsInCollection(queries, databaseId);
    return { documents: docs, total: total };
  }

  async getAll(
    queries: string[] = [],
    databaseId?: string
  ): Promise<z.infer<T>[]> {
    let allDocs: z.infer<T>[] = [];
    let docsResponse = await this.list(
      [Query.limit(100), ...queries],
      databaseId
    );
    allDocs.push(...docsResponse.map((doc) => this.schema.parse(doc)));
    let cursor =
      docsResponse.length > 0
        ? docsResponse[docsResponse.length - 1].$id
        : undefined;
    if (docsResponse.length === 100) {
      while (cursor) {
        docsResponse = await this.list(
          [Query.limit(100), Query.cursorAfter(cursor)],
          databaseId
        );
        cursor =
          docsResponse.length > 0
            ? docsResponse[docsResponse.length - 1].$id
            : undefined;
      }
      allDocs.push(...docsResponse.map((doc) => this.schema.parse(doc)));
    }
    return allDocs;
  }

  async getFile(bucketId: string, fileId: string): Promise<Models.File | null> {
    try {
      const file = await this.storage.getFile(bucketId, fileId);
      return file;
    } catch (e) {
      console.error(`Failed to get file in ${bucketId}:`, e);
      return null;
    }
  }

  get current(): z.infer<T> | undefined {
    return this.atom.get();
  }

  set current(value: z.infer<T> | undefined) {
    if (value) {
      this.atom.set(this.schema.parse(value));
    } else {
      this.atom.set(undefined);
    }
  }
}

