import { atom } from "nanostores";

export type NavigationMode = "chat" | "post" | null;

export interface NavigationState {
  lastActiveMode: NavigationMode;
  isChatOpen: boolean;
  isPostModalOpen: boolean;
}

export const navigationState = atom<NavigationState>({
  lastActiveMode: null,
  isChatOpen: false,
  isPostModalOpen: false,
});

// Actions
export const setLastActiveMode = (mode: NavigationMode) => {
  navigationState.set({
    ...navigationState.get(),
    lastActiveMode: mode,
  });
};

export const setChatOpen = (isOpen: boolean) => {
  navigationState.set({
    ...navigationState.get(),
    isChatOpen: isOpen,
    lastActiveMode: isOpen ? "chat" : navigationState.get().lastActiveMode,
  });
};

export const setPostModalOpen = (isOpen: boolean) => {
  navigationState.set({
    ...navigationState.get(),
    isPostModalOpen: isOpen,
    lastActiveMode: isOpen ? "post" : navigationState.get().lastActiveMode,
  });
};

export const closeAllModals = () => {
  navigationState.set({
    ...navigationState.get(),
    isChatOpen: false,
    isPostModalOpen: false,
  });
};

export const toggleLastActiveMode = () => {
  const state = navigationState.get();

  if (state.lastActiveMode === "chat") {
    // If chat was last active, toggle chat
    setChatOpen(!state.isChatOpen);
    if (state.isPostModalOpen) {
      setPostModalOpen(false);
    }
  } else if (state.lastActiveMode === "post") {
    // If post was last active, toggle post modal
    setPostModalOpen(!state.isPostModalOpen);
    if (state.isChatOpen) {
      setChatOpen(false);
    }
  } else {
    // If no previous mode, default to post creation
    setPostModalOpen(true);
    if (state.isChatOpen) {
      setChatOpen(false);
    }
  }
};

