    super(
      APPWRITE_COLL_POST_CONTAINER,
      PostContainerSchema,
      "currentPostContainer",
      "socialaize_data"
    );
  }

  async listContainers(queries: string[] = []) {
    return this.list(queries);
  }

  async listWithCount(queries: string[] = []) {
    return this.listWithTotal(queries);
  }

