    return this.update(id, data);
  }

  async deleteContainer(id: string) {
    return this.delete(id);
  }
}

const createPostContainerStore = () => {
  const store = new PostContainerStore();
  return {
    store,
    currentPostContainer: store.atom,
    listContainers: store.listContainers.bind(store),
    listWithCount: store.listWithCount.bind(store),
    createContainer: store.createContainer.bind(store),
    updateContainer: store.updateContainer.bind(store),
    deleteContainer: store.deleteContainer.bind(store),
  };
};

