import { ref, computed, watch } from "vue";
import { useStore } from "@nanostores/vue";
import {
  getWidgetsForTeam,
  getTemplateWidgets,
  createWidget,
  updateWidget,
  deleteWidget,
  cloneTemplateWidget,
  widgetsAtom,
  templatesAtom as widgetTemplatesAtom,
  updateDashboardWidgetsMapping,
} from "@/stores/analyticWidgetStore";
import {
  getDashboardsForTeam,
  getTemplateDashboards,
  createDashboard,
  updateDashboard,
  deleteDashboard,
  cloneTemplateDashboard,
  setSelectedDashboard,
  dashboardsAtom,
  selectedDashboardAtom,
  dashboardTemplatesAtom,
  clearTemplateCache,
} from "@/stores/analyticDashboardStore";
import { useBilling } from "@composables/useBilling";
import { useUserAdmin, useUserTester } from "@composables/useUserAdmin";
import { useTeams } from "@composables/useTeams";
import type { AnalyticWidget } from "@appwrite/schemas/analyticWidget";
import type { AnalyticDashboard } from "@appwrite/schemas/analyticDashboard";

// Parse JSON safely with error handling
function safeJsonParse<T>(jsonString: string, fallback: T): T {
  try {
    return JSON.parse(jsonString) as T;
  } catch (e) {
    console.error("Failed to parse JSON:", e);
    return fallback;
  }
}

export function useAnalyticsStore() {
  const { activeTeam } = useTeams();
  const { billing, getUserLimits } = useBilling();
  const isUserAdmin = useUserAdmin();

  // Loading and error states
  const isLoadingDashboards = ref(false);
  const isLoadingWidgets = ref(false);
  const isLoadingTemplates = ref(false);
  const error = ref<string | null>(null);

  // Store connections
  const widgets = useStore(widgetsAtom);
  const widgetTemplates = useStore(widgetTemplatesAtom);
  const dashboards = useStore(dashboardsAtom);
  const selectedDashboard = useStore(selectedDashboardAtom);
  const dashboardTemplates = useStore(dashboardTemplatesAtom);

  // Computed states
  const currentTeamId = computed(() => activeTeam.value?.$id);
  const currentUserTier = computed(() =>
    billing.value?.nextBillingDate && billing.value?.subscriptionPlan
      ? billing.value?.subscriptionPlan
      : "free"
  );

  // Check if user can customize dashboards based on tier
  const canCustomize = computed(() => {
    const tier = currentUserTier.value;
    return tier === "advanced" || tier === "enterprise";
  });

  // Parse dashboard layout from JSON string
  const dashboardLayout = computed(() => {
    if (!selectedDashboard.value || !selectedDashboard.value.layout) {
      return { widgets: [] };
    }
    return safeJsonParse(selectedDashboard.value.layout, { widgets: [] });

