import { computed } from 'vue';
import { useStore } from '@nanostores/vue';
import { docsModalState, closeDocsModal } from '@/stores/docsStore';

const modalState = useStore(docsModalState);

const isOpen = computed({
  get: () => modalState.value.isOpen,
  set: (value) => {
    if (!value) closeDocsModal();
