import { Account, Avatars, Client, Databases, Functions, Storage, Teams, type Models } from "appwrite";
import { APPWRITE_ENDPOINT, APPWRITE_PROJECT_ID, SESSION_COOKIE_NAME } from "astro:env/client";
import { ref, computed } from "vue";
import { createGlobalState, useLocalStorage } from "@vueuse/core";
// IMPORTANT: Do not import authStore here to avoid circular deps during hydration.
// We'll lazy-import when we need to update those stores.

// New global-state composable using @vueuse/core
export const useAppwriteClient = createGlobalState(() => {
  // Instances (kept inside the factory rather than module-level constants)
  let client: Client | null = null;
  let account: Account | null = null;
  let databases: Databases | null = null;
  let functions: Functions | null = null;
  let storage: Storage | null = null;
  let teams: Teams | null = null;
  let avatars: Avatars | null = null;

  // Reactive state
  const isInitialized = ref(false);
  const isAuthenticated = ref(false);
  const sessionToken = ref<string | undefined>(undefined);

  // Persisted cookie fallback (used throughout the app)
  const fallbackCookies = useLocalStorage<Record<string, string>>("fallbackCookies", {});

  // Helper function to validate a token
  const isValidToken = (token: string | undefined | null): boolean => {
    return !!token && token !== "undefined" && token !== "null" && token.trim() !== "";
  };

  const initClient = () => {
    if (!client) {
      client = new Client().setEndpoint(APPWRITE_ENDPOINT).setProject(APPWRITE_PROJECT_ID);
      account = new Account(client);
      databases = new Databases(client);
      functions = new Functions(client);
      storage = new Storage(client);
      teams = new Teams(client);
      avatars = new Avatars(client);

      // Initialize session from persisted cookies if available
      let token = fallbackCookies.value?.[SESSION_COOKIE_NAME];

      // Also check Appwrite SDK's cookieFallback as fallback
      if (!token && typeof localStorage !== 'undefined') {
        try {
          const legacyFallback = localStorage.getItem("cookieFallback");
          if (legacyFallback) {
            const parsed = JSON.parse(legacyFallback);
            token = parsed[`a_session_${APPWRITE_PROJECT_ID}`];
          }
        } catch (e) {
          // Ignore parse errors
        }
      }

      if (isValidToken(token)) {
        try {
          client.setSession(token!);
          isAuthenticated.value = true;
          sessionToken.value = token!;
        } catch {
          // ignore
        }
      }
      isInitialized.value = true;
    }
    return client;
  };

  const refreshClientSession = () => {
    if (!client) return;
    const token = fallbackCookies.value?.[SESSION_COOKIE_NAME];
    if (isValidToken(token)) {
      try {
        client.setSession(token!);
        isAuthenticated.value = true;
        sessionToken.value = token!;
      } catch {
        // ignore
      }
    }
  };

  const setFallbackCookies = (cookies: Record<string, string>) => {
    fallbackCookies.value = cookies;
    refreshClientSession();
  };

  const clearFallbackCookies = () => {
    fallbackCookies.value = {};
    isAuthenticated.value = false;
    if (client) {
      try {
        client.setSession("");
      } catch {
        // ignore
      }
    }

    // Also clear Appwrite SDK's cookieFallback
    if (typeof localStorage !== 'undefined') {
      try {
        localStorage.removeItem("cookieFallback");
      } catch (e) {
        console.warn('Failed to clear cookieFallback:', e);
      }
    }
  };

  const setSession = (token: string | undefined) => {
    initClient();
    try {
      if (isValidToken(token)) {
        client!.setSession(token!);
        isAuthenticated.value = true;
        sessionToken.value = token!;
        // Persist into our app's stores/cookies without creating import cycles
        try {
          // Update authStore lazily if available
          import("@/stores/authStore").then((mod) => {
            if (mod?.setCurUserSession) mod.setCurUserSession(token!);
          }).catch(() => {});
        } catch {}
        setFallbackCookies({ [SESSION_COOKIE_NAME]: token! });

        // Also set Appwrite SDK's expected cookieFallback format
        if (typeof localStorage !== 'undefined') {
          try {
            localStorage.setItem("cookieFallback", JSON.stringify({
              [`a_session_${APPWRITE_PROJECT_ID}`]: token!
            }));
          } catch (e) {
            console.warn('Failed to set cookieFallback:', e);
          }
        }
      } else {
        sessionToken.value = undefined;
        isAuthenticated.value = false;
      }
    } catch {
      // ignore
    }
  };

  const clearSession = () => {
    isAuthenticated.value = false;
    clearFallbackCookies();
  };

  const checkAuthStatus = async (): Promise<Models.User<Models.Preferences> | null> => {
    try {
      initClient();
      refreshClientSession();
      const me = await account!.get();
      isAuthenticated.value = true;
      return me;
    } catch (error) {
      isAuthenticated.value = false;

      // Try to initialize from server if in browser
      if (typeof window !== "undefined") {
        try {
          const response = await fetch(`${import.meta.env.SITE}/api/auth/verify.json`, {
            method: "GET",
            credentials: "include",
            headers: { "Content-Type": "application/json" },
          });
          if (response.ok) {
            const data = await response.json();
            if (data.sessionToken && data.user) {
              setSession(data.sessionToken);
              isAuthenticated.value = true;
              try {
                import("@/stores/authStore").then((mod) => {
                  if (mod?.setCurrentUser) mod.setCurrentUser(data.user);
                }).catch(() => {});
              } catch {}
              return data.user;
            }
          }
        } catch (initError) {
          console.warn("Failed to initialize session from server:", initError);
        }
      }
      return null;
    }
  };

  // Initialize session from cookies/localStorage or provided data (ported from requested file)
  const initializeSessionFromCookies = (
    session?: string,
    user?: Models.User<Models.Preferences>
  ) => {
    // Try localStorage first for hard-refresh recovery
    if (!session && typeof localStorage !== "undefined") {
      try {
        const persisted = localStorage.getItem("curUserSession");
        if (persisted && isValidToken(persisted)) {
          session = persisted;
        }
      } catch {}
    }

    // Fall back to cookie if no session yet
    if (!session && typeof document !== "undefined" && !sessionToken.value) {
      const cookies = document.cookie.split(";");
      const sessionCookie = cookies.find((c) => c.trim().startsWith(`${SESSION_COOKIE_NAME}=`));
      if (sessionCookie) {
        const token = sessionCookie.split("=")[1];
        if (isValidToken(token)) {
          session = token;
        }
      }
    }

    // Apply if we found one
    if (isValidToken(session)) {
      setSession(session);
      if (user) {
        try {
          import("@/stores/authStore").then((mod) => {
            if (mod?.setCurrentUser) mod.setCurrentUser(user);
          }).catch(() => {});
        } catch {}
      }
    } else if (session) {
      // invalid
      setSession(undefined);
    }
  };

  // Additional helpers matching requested source
  const currentSession = computed(() => client!.config.session || sessionToken.value);
  const isReady = computed(() => client !== null && client!.config.session !== undefined);
  const waitForReady = (timeoutMs: number = 2000): Promise<void> => {
    return new Promise((resolve, reject) => {
      if (isReady.value) return resolve();
      const start = Date.now();
      const check = () => {
        if (isReady.value || isValidToken(client!.config.session)) return resolve();
        if (Date.now() - start > timeoutMs) return reject(new Error("Timeout waiting for Appwrite client"));
        setTimeout(check, 50);
      };
      check();
    });
  };

  const getSession = () => client!.config.session || sessionToken.value;

  const deleteSession = async () => {
    try {
      if (!account) return;
      const cur = client!.config.session || sessionToken.value;
      if (cur) await account.deleteSession("current");
    } catch (error) {
      console.error("Failed to delete session:", error);
    } finally {
      clearSession();
    }
  };

  const sendPasswordRecovery = async (email: string, redirectUrl: string) => {
    try {
      if (!account) throw new Error("Account service not initialized");
      return await account.createRecovery(email, redirectUrl);
    } catch (error) {
      console.error("Failed to send password recovery:", error);
      throw error;
    }
  };

  const reinitClient = () => {
    // Try restoring from localStorage first
    if (typeof localStorage !== "undefined") {
      const persisted = localStorage.getItem("curUserSession");
      if (persisted && isValidToken(persisted)) {
        setSession(persisted);
        return;
      }
    }
    // Fallback to cookie lookup
    initializeSessionFromCookies();
  };

  // Ensure client is initialized immediately for consumers
  initClient();

  return {
    // Instances (non-null after init)
    client: client!,
    account: account!,
    databases: databases!,
    functions: functions!,
    storage: storage!,
    teams: teams!,
    avatars: avatars!,

    // State
    isInitialized,
    isAuthenticated,
    currentSession,
    isReady,

    // Session helpers
    checkAuthStatus,
    setSession,
    getSession,
    clearSession,
    deleteSession,
    refreshClientSession,
    setFallbackCookies,
    clearFallbackCookies,
    waitForReady,
    sendPasswordRecovery,
    initializeSessionFromCookies,
    reinitClient,
  };
});

// Backwards-compatible named helpers that many files import
export const getAppwriteClientSingleton = (): Client => useAppwriteClient().client;
export const getDatabasesSingleton = (): Databases => useAppwriteClient().databases;
export const getFunctionsSingleton = (): Functions => useAppwriteClient().functions;
export const getStorageSingleton = (): Storage => useAppwriteClient().storage;
export const getTeamsSingleton = (): Teams => useAppwriteClient().teams;
export const getAccountSingleton = (): Account => useAppwriteClient().account;

// Also re-export session helpers for existing imports
export const setSession = (sessionToken: string) => useAppwriteClient().setSession(sessionToken);
export const clearSession = () => useAppwriteClient().clearSession();
export const setFallbackCookies = (cookies: Record<string, string>) => useAppwriteClient().setFallbackCookies(cookies);
export const clearFallbackCookies = () => useAppwriteClient().clearFallbackCookies();
export const refreshClientSession = () => useAppwriteClient().refreshClientSession();
export const checkAuthStatus = () => useAppwriteClient().checkAuthStatus();

// Export the initialization function for standalone use (compat with requested file)
export const initializeSessionFromCookies = (
  session?: string,
  user?: Models.User<Models.Preferences>
) => useAppwriteClient().initializeSessionFromCookies(session, user);

// Friendly alias matching the original filename from the other repo
export const useAppwrite = useAppwriteClient;

