   339‚Üí
   340‚Üí      // Add attachment to ticket
   341‚Üí      const updatedTicket = await addTicketAttachment(ticketId, fileUpload.$id);
   342‚Üí
   343‚Üí      // Update local state
   344‚Üí      const ticketIndex = tickets.value.findIndex(t => t.$id === ticketId);
   345‚Üí      if (ticketIndex !== -1) {
   346‚Üí        tickets.value[ticketIndex] = updatedTicket;
   347‚Üí      }
   348‚Üí
   349‚Üí      if (selectedTicket.value?.$id === ticketId) {
   350‚Üí        selectedTicket.value = updatedTicket;
   351‚Üí        currentSupportTicket.set(updatedTicket);
   352‚Üí      }
   353‚Üí
   354‚Üí      return { ticket: updatedTicket, file: fileUpload };
   355‚Üí    } catch (err) {
   356‚Üí      error.value = err instanceof Error ? err.message : "Failed to upload attachment";
   357‚Üí      throw err;
   358‚Üí    } finally {
   359‚Üí      isLoading.value = false;
   360‚Üí    }
   361‚Üí  };
   362‚Üí
   363‚Üí  const removeAttachmentFromTicket = async (ticketId: string, attachmentId: string) => {
   364‚Üí    isLoading.value = true;
   365‚Üí    error.value = null;
   366‚Üí    try {
   367‚Üí      const updatedTicket = await removeTicketAttachment(ticketId, attachmentId);
   368‚Üí
   369‚Üí      // Update local state
   370‚Üí      const ticketIndex = tickets.value.findIndex(t => t.$id === ticketId);
   371‚Üí      if (ticketIndex !== -1) {
   372‚Üí        tickets.value[ticketIndex] = updatedTicket;
   373‚Üí      }
   374‚Üí
   375‚Üí      if (selectedTicket.value?.$id === ticketId) {
   376‚Üí        selectedTicket.value = updatedTicket;
   377‚Üí        currentSupportTicket.set(updatedTicket);
