import { ref, computed, provide } from "vue";
import { useStore } from "@nanostores/vue";
import { type SupportTicket } from "@appwrite/schemas/supportTicket";
import {
  supportTicketStore,
  currentSupportTicket,
  createSupportTicket,
  updateTicketStatus,
  assignTicket,
  addTicketMessage,
  updateTicketPriority,
  addTicketAttachment,
  removeTicketAttachment,
  getTicketsByStatus,
  getTicketsByUser,
  getTicketsByTeam,
  getTicketsByAssignee,
  getTicketsByPriority,
  getTicketsByCategory,
  getOpenTickets,
  getUrgentTickets,
  getRecentTickets,
  searchTickets,
} from "@/stores/supportTicketStore";
import { useAuth } from "./useAuth";
import { useTeams } from "./useTeams";
import { useAppwrite } from "./useAppwriteClient";

export type SupportView = 'center' | 'create' | 'list' | 'detail';
export type SupportTicketStatus = 'open' | 'in_progress' | 'waiting' | 'resolved' | 'closed';
export type SupportTicketPriority = 'low' | 'medium' | 'high' | 'urgent';
export type SupportTicketCategory = 'general' | 'technical' | 'billing' | 'account' | 'api' | 'bug_report' | 'feature_request';

const SUPPORT_TICKET_LOADING_KEY = Symbol("supportTicketLoading");
const CURRENT_SUPPORT_TICKET_KEY = Symbol("currentSupportTicket");
const SUPPORT_VIEW_KEY = Symbol("supportView");

export function useSupportTickets(shouldProvide: boolean = false) {
  const currentTicket = useStore(currentSupportTicket);
  const { user } = useAuth();
  const { activeTeam } = useTeams();
  const { storage } = useAppwrite();

  const isLoading = ref(false);
  const error = ref<string | null>(null);
  const supportView = ref<SupportView>('center');
  const tickets = ref<SupportTicket[]>([]);
  const selectedTicket = ref<SupportTicket | null>(null);

  // Form state for ticket creation
  const ticketForm = ref<Partial<SupportTicket & {
    attachments: Array<{ name: string; url: string; type: string; id: string }>;
  }>>({
    title: '',
    description: '',
    category: 'general',
    priority: 'medium',
    attachments: [],
  });

  // Computed properties
  const visibleTickets = computed(() => {
    return tickets.value.slice(0, 20); // Lazy loading like in GlobalActionWidget
  });

  const openTicketsCount = computed(() => {
    return tickets.value.filter(t => !['resolved', 'closed'].includes(t.status || '')).length;
  });

  const urgentTicketsCount = computed(() => {
    return tickets.value.filter(t => t.priority === 'urgent' && !['resolved', 'closed'].includes(t.status || '')).length;
  });

  const canCreateTicket = computed(() => {
    return !!user.value && !!activeTeam.value;
  });

  // Navigation methods
  const setSupportView = (view: SupportView) => {
    supportView.value = view;
    error.value = null;
  };

  const selectTicket = (ticket: SupportTicket) => {
    selectedTicket.value = ticket;
    currentSupportTicket.set(ticket);
    supportView.value = 'detail';
  };

  const clearSelectedTicket = () => {
    selectedTicket.value = null;
    currentSupportTicket.set(undefined);
  };

  // Data fetching methods
  const fetchUserTickets = async () => {
    if (!user.value) return;

    isLoading.value = true;
    error.value = null;
    try {
      const userTickets = await getTicketsByUser(user.value.$id);
      tickets.value = userTickets;
      return userTickets;
    } catch (err) {
      error.value = err instanceof Error ? err.message : "Failed to fetch tickets";
      throw err;
    } finally {
      isLoading.value = false;
    }
  };

  const fetchTeamTickets = async (teamId?: string) => {
    const targetTeamId = teamId || activeTeam.value?.$id;
    if (!targetTeamId) return;

    isLoading.value = true;
    error.value = null;
    try {
      const teamTickets = await getTicketsByTeam(targetTeamId);
      tickets.value = teamTickets;
      return teamTickets;
    } catch (err) {
      error.value = err instanceof Error ? err.message : "Failed to fetch team tickets";
      throw err;
    } finally {
      isLoading.value = false;
    }
  };

  const fetchTicketsByStatus = async (status: SupportTicketStatus) => {
    isLoading.value = true;
    error.value = null;
    try {
      const statusTickets = await getTicketsByStatus(status);
      tickets.value = statusTickets;
      return statusTickets;
    } catch (err) {
      error.value = err instanceof Error ? err.message : `Failed to fetch ${status} tickets`;
      throw err;
    } finally {
      isLoading.value = false;
    }
  };

  const fetchRecentTickets = async (limit = 10) => {
    isLoading.value = true;
    error.value = null;
    try {
      const recentTickets = await getRecentTickets(limit);
      tickets.value = recentTickets;
      return recentTickets;
    } catch (err) {
      error.value = err instanceof Error ? err.message : "Failed to fetch recent tickets";
      throw err;
    } finally {
      isLoading.value = false;
    }
  };

  const searchSupportTickets = async (searchTerm: string) => {
    if (!searchTerm.trim()) {
      await fetchUserTickets();
      return;
    }

    isLoading.value = true;
    error.value = null;
    try {
      const searchResults = await searchTickets(searchTerm);
      tickets.value = searchResults;
      return searchResults;
    } catch (err) {
      error.value = err instanceof Error ? err.message : "Failed to search tickets";
      throw err;
    } finally {
      isLoading.value = false;
    }
  };

  // Ticket management methods
  const createNewTicket = async (ticketData: {
    title: string;
    description: string;
    category?: SupportTicketCategory;
    priority?: SupportTicketPriority;
    attachments?: string[];
    deviceInfo?: string;
    currentUrl?: string;
    metadata?: Record<string, any>;
  }) => {
    if (!user.value) throw new Error("User not authenticated");

    isLoading.value = true;
    error.value = null;
    try {
      const ticket = await createSupportTicket({
        ...ticketData,
        userId: user.value.$id,
        teamId: activeTeam.value?.$id,
        deviceInfo: ticketData.deviceInfo || getUserDeviceInfo(),
        currentUrl: ticketData.currentUrl || window.location.href,
      });

      // Refresh tickets list
      await fetchUserTickets();

      // Navigate to the new ticket
      selectTicket(ticket);

      return ticket;
    } catch (err) {
      error.value = err instanceof Error ? err.message : "Failed to create ticket";
      throw err;
    } finally {
      isLoading.value = false;
    }
  };

  const createAnonymousTicket = async (ticketData: {
    title: string;
    description: string;
    contactEmail: string;
    contactName: string;
    category?: SupportTicketCategory;
    priority?: SupportTicketPriority;
  }) => {
    // Validate email format
    if (!ticketData.contactEmail || !ticketData.contactEmail.includes('@')) {
      throw new Error("Valid email address is required");
    }

    // Validate name
    if (!ticketData.contactName || ticketData.contactName.trim().length < 2) {
      throw new Error("Name is required and must be at least 2 characters");
    }

    isLoading.value = true;
    error.value = null;
    try {
      const ticket = await createSupportTicket({
        title: ticketData.title,
        description: ticketData.description,
        contactEmail: ticketData.contactEmail,
        contactName: ticketData.contactName,
        category: ticketData.category || 'general',
        priority: ticketData.priority || 'medium',
        userId: undefined,
        teamId: undefined,
        deviceInfo: getUserDeviceInfo(),
        currentUrl: window.location.href,
      });

      // Don't fetch user tickets or navigate for anonymous
      // Just return the ticket
      return ticket;
    } catch (err) {
      error.value = err instanceof Error ? err.message : "Failed to create ticket";
      throw err;
    } finally {
      isLoading.value = false;
    }
  };

  const updateTicketStatusAndRefresh = async (ticketId: string, status: SupportTicketStatus) => {
    isLoading.value = true;
    error.value = null;
    try {
      const updatedTicket = await updateTicketStatus(ticketId, status);

      // Update local ticket list
      const ticketIndex = tickets.value.findIndex(t => t.$id === ticketId);
      if (ticketIndex !== -1) {
        tickets.value[ticketIndex] = updatedTicket;
      }

      // Update selected ticket if it's the same one
      if (selectedTicket.value?.$id === ticketId) {
        selectedTicket.value = updatedTicket;
        currentSupportTicket.set(updatedTicket);
      }

      return updatedTicket;
    } catch (err) {
      error.value = err instanceof Error ? err.message : "Failed to update ticket status";
      throw err;
    } finally {
      isLoading.value = false;
    }
  };

  const addCommentToTicket = async (ticketId: string, content: string, role: 'user' | 'agent' | 'support' = 'user') => {
    if (!user.value) throw new Error("User not authenticated");

    isLoading.value = true;
    error.value = null;
    try {
      const message = {
        id: crypto.randomUUID(),
        content,
        role,
        timestamp: new Date().toISOString(),
      };

      const updatedTicket = await addTicketMessage(ticketId, message);

      // Update local state
      const ticketIndex = tickets.value.findIndex(t => t.$id === ticketId);
      if (ticketIndex !== -1) {
        tickets.value[ticketIndex] = updatedTicket;
      }

      if (selectedTicket.value?.$id === ticketId) {
        selectedTicket.value = updatedTicket;
        currentSupportTicket.set(updatedTicket);
      }

      return updatedTicket;
    } catch (err) {
      error.value = err instanceof Error ? err.message : "Failed to add comment";
      throw err;
    } finally {
      isLoading.value = false;
    }
  };

  const uploadTicketAttachment = async (ticketId: string, file: File) => {
    if (!storage) throw new Error("Storage not available");

    isLoading.value = true;
    error.value = null;
    try {
      // Upload file to Appwrite storage
      const fileUpload = await storage.createFile(
        "global_bucket", // Using the global bucket
        crypto.randomUUID(),
        file
      );

      // Add attachment to ticket
      const updatedTicket = await addTicketAttachment(ticketId, fileUpload.$id);

      // Update local state
      const ticketIndex = tickets.value.findIndex(t => t.$id === ticketId);
      if (ticketIndex !== -1) {
        tickets.value[ticketIndex] = updatedTicket;
      }

      if (selectedTicket.value?.$id === ticketId) {
        selectedTicket.value = updatedTicket;
        currentSupportTicket.set(updatedTicket);
      }

      return { ticket: updatedTicket, file: fileUpload };
    } catch (err) {
      error.value = err instanceof Error ? err.message : "Failed to upload attachment";
      throw err;
    } finally {
      isLoading.value = false;
    }
  };

  const removeAttachmentFromTicket = async (ticketId: string, attachmentId: string) => {
    isLoading.value = true;
    error.value = null;
    try {
      const updatedTicket = await removeTicketAttachment(ticketId, attachmentId);

      // Update local state
      const ticketIndex = tickets.value.findIndex(t => t.$id === ticketId);
      if (ticketIndex !== -1) {
        tickets.value[ticketIndex] = updatedTicket;
      }

      if (selectedTicket.value?.$id === ticketId) {
        selectedTicket.value = updatedTicket;
        currentSupportTicket.set(updatedTicket);
      }

      return updatedTicket;
    } catch (err) {
      error.value = err instanceof Error ? err.message : "Failed to remove attachment";
      throw err;
    } finally {
      isLoading.value = false;
    }
  };

  // Utility methods
  const resetTicketForm = () => {
    ticketForm.value = {
      title: '',
      description: '',
      category: 'general',
      priority: 'medium',
      attachments: [],
    };
  };

  const getUserDeviceInfo = (): string => {
    try {
      return JSON.stringify({
        userAgent: navigator.userAgent,
        platform: navigator.platform,
        language: navigator.language,
        screen: `${screen.width}x${screen.height}`,
        viewport: `${window.innerWidth}x${window.innerHeight}`,
        timezone: Intl.DateTimeFormat().resolvedOptions().timeZone,
      });
    } catch (e) {
      return 'Unable to collect device info';
    }
  };

  // Provide data if requested
  if (shouldProvide) {
    provide(SUPPORT_TICKET_LOADING_KEY, isLoading);
    provide(CURRENT_SUPPORT_TICKET_KEY, currentTicket);
    provide(SUPPORT_VIEW_KEY, supportView);
  }

  return {
    // State
    currentTicket,
    isLoading,
    error,
    supportView,
    tickets,
    selectedTicket,
    ticketForm,
    visibleTickets,
    openTicketsCount,
    urgentTicketsCount,
    canCreateTicket,

    // Navigation
    setSupportView,
    selectTicket,
    clearSelectedTicket,

    // Data fetching
    fetchUserTickets,
    fetchTeamTickets,
    fetchTicketsByStatus,
    fetchRecentTickets,
    searchSupportTickets,

    // Ticket management
    createNewTicket,
    createAnonymousTicket,
    updateTicketStatusAndRefresh,
    addCommentToTicket,
    uploadTicketAttachment,
    removeAttachmentFromTicket,

    // Utility
    resetTicketForm,
    getUserDeviceInfo,
  };
}

