import { Query, type Models } from "appwrite";
import { persistentAtom } from "@nanostores/persistent";
import { 
  getAccountSingleton,
  getTeamsSingleton,
  getStorageSingleton
} from "@composables/useAppwriteClient";
import { DateTime } from "luxon";
import { databaseId } from "@/constants/appwrite";
import { APPWRITE_COLL_MEMBERS } from "astro:env/client";
import { TEAM_ROLES } from "@/constants/teamRoles";
import { getUserJwt } from "./authStore";
import { z } from "zod";
import { BaseStore } from "./baseStore";

export const TeamPrefsSchema = z.object({
  teamIds: z
    .union([
      z.array(z.string()),
      z.string().transform((val) => {
        try {
          return JSON.parse(val);
        } catch {
          return [];
        }
      }),
    ])
    .transform((val) => (Array.isArray(val) ? val : []))
    .pipe(z.array(z.string())),
});

export type TeamPrefs = z.infer<typeof TeamPrefsSchema>;

export const selectedUserTeam =
  persistentAtom<Models.Team<Models.Preferences> | null>(
    "selectedUserTeam",
    null,
    {
      encode: JSON.stringify,
      decode: JSON.parse,
    }
  );

export const currentUserMemberships = persistentAtom<Models.Membership[]>(
  "currentUserMemberships",
  [],
  {
    encode: JSON.stringify,
    decode: JSON.parse,
  }
);

export const setSelectedUserTeam = (team: Models.Team<Models.Preferences>) => {
  selectedUserTeam.set(team);
};

export const clearSelectedUserTeam = () => {
  selectedUserTeam.set(null);
};

export const setCurrentUserMemberships = (memberships: Models.Membership[]) => {
  currentUserMemberships.set(memberships);
};

export const clearCurrentUserMemberships = () => {
  currentUserMemberships.set([]);
};

class TeamStore extends BaseStore<typeof TeamPrefsSchema> {
  constructor() {
    super(APPWRITE_COLL_MEMBERS, TeamPrefsSchema, "currentTeam", databaseId);
  }

  async getOrCreateCurrentUserPrimaryTeam(
    user: Models.User<Models.Preferences>
  ) {
    const userTeams = await this.teams.list([Query.equal("$id", user.$id)]);
    if (userTeams.teams.length === 0) {
      const newTeam = await this.teams.create(user.$id, `${user.name}'s Team`, [
        ...TEAM_ROLES,
      ]);

      if (selectedUserTeam.get() === null) {
        setSelectedUserTeam(newTeam);
      }
      return newTeam;
    }
    if (selectedUserTeam.get() === null) {
      setSelectedUserTeam(userTeams.teams[0]);
    }
    return userTeams.teams[0];
  }

  async ensureTeamStorage(teamId: string, userId?: string) {
    const response = await fetch("/api/teams/ensureTeamStorage.json", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      credentials: "include",

