import { persistentAtom } from "@nanostores/persistent";
import {
  Query,
  type Models,
  ExecutionMethod,
} from "appwrite";
import {
  databaseId,
} from "@/constants/appwrite";
import { z } from "zod";
import type { ULIDFactory } from "ulid-workers";
import { useAppwrite } from "@composables/useAppwriteClient";


export class BaseStore<T extends z.ZodType> {
  protected collectionId: string;
  protected schema: T;
  protected databaseId: string | undefined;
  atom: ReturnType<typeof persistentAtom<z.infer<T> | undefined>>;
  private ulidInstance: ULIDFactory | null = null;

  constructor(
    collectionId: string,
    schema: T,
    atomKey: string,
    databaseId?: string
  ) {
    this.collectionId = collectionId;
    this.schema = schema;
    this.atom = persistentAtom<z.infer<T> | undefined>(atomKey, undefined, {
      encode: JSON.stringify,
      decode: (val) => {
        try {
          const parsed = JSON.parse(val);
          return parsed ? this.schema.parse(parsed) : undefined;
        } catch (e) {
          return undefined;
        }
      },
    });
    this.databaseId = databaseId;
  }

  get client() {
    const { client } = useAppwrite();
    return client;
  }

  get db() {
    const { databases } = useAppwrite();
    return databases;
  }

  get teams() {
    const { teams } = useAppwrite();
    return teams;
  }

  get storage() {
    const { storage } = useAppwrite();
    return storage;
  }

  get functions() {
    const { functions } = useAppwrite();
    return functions;
  }

  getUlidFactory = async (): Promise<ULIDFactory> => {
    if (!this.ulidInstance) {
      const { ulidFactory } = await import("ulid-workers");
      this.ulidInstance = ulidFactory();
    }
    return this.ulidInstance;
  };

  get storedValue(): z.infer<T> | undefined {
    return this.atom.get();
  }

  set storedValue(value: z.infer<T> | undefined) {
    this.atom.set(value);
  }

  updateStoredValue(value: Partial<z.infer<T>>) {
    const current = this.storedValue;
    if (JSON.stringify(current) === JSON.stringify(value)) return;
    this.atom.set({
      ...current,
      ...value,
    });
  }

  clearStoredValue() {
    this.atom.set(undefined);
  }

  async create(
    data: z.infer<T>,
    docId?: string,
    permissions?: string[],
    databaseId?: string
  ): Promise<z.infer<T>> {
    try {
      delete data.$id;
      delete data.$createdAt;
      delete data.$updatedAt;
      delete data.$databaseId;
      delete data.$collectionId;
      delete data.$permissions;
      const ulid = await this.getUlidFactory();
      const doc = await this.db.createDocument(
        databaseId || this.databaseId!,
        this.collectionId,
        docId || ulid(),
        data,
        permissions
      );
      return this.schema.parse(doc);
    } catch (e) {
      console.error(`Failed to create document in ${this.collectionId}:`, e);
      throw e;
    }
  }

  async get(docId: string, databaseId?: string): Promise<z.infer<T> | null> {
    try {
      const doc = await this.db.getDocument(
        databaseId || this.databaseId!,
        this.collectionId,
        docId
      );
      return this.schema.parse(doc);
    } catch (e) {
      console.error(`Failed to get document in ${this.collectionId}:`, e);
      return null;
    }
  }

  async getRaw(docId: string, databaseId?: string): Promise<Models.Document> {
    try {
      const doc = await this.db.getDocument(
        databaseId || this.databaseId!,
        this.collectionId,
        docId
      );
      return doc;
    } catch (e) {
      console.error(`Failed to get document in ${this.collectionId}:`, e);
      throw e;

