import { BaseStore } from "./baseStore";
import {
  SupportTicketSchema,
  type SupportTicket,
} from "@appwrite/schemas/supportTicket";
import { APPWRITE_COLL_SUPPORT_TICKETS } from "astro:env/client";
import { Query, Permission, Role } from "appwrite";
import { DateTime } from "luxon";

class SupportTicketStore extends BaseStore<typeof SupportTicketSchema> {
  constructor() {
    super(
      APPWRITE_COLL_SUPPORT_TICKETS,
      SupportTicketSchema,
      "supportTicket",
      "system_content"
    );
  }

  async createSupportTicket({
    title,
    description,
    userId,
    teamId,
    contactEmail,
    contactName,
    category = "general",
    priority = "medium",
    deviceInfo,
    currentUrl,
    attachments,
    metadata,
  }: {
    title: string;
    description: string;
    userId?: string;
    teamId?: string;
    contactEmail?: string;
    contactName?: string;
    category?: SupportTicket["category"];
    priority?: SupportTicket["priority"];
    deviceInfo?: string;
    currentUrl?: string;
    attachments?: string[];
    metadata?: Record<string, any>;
  }) {
    const now = DateTime.now().toISO();

    // Build permissions array
    const permissions: string[] = [];

    // Authenticated user permissions
    if (userId) {
      permissions.push(
        Permission.read(Role.user(userId)),
        Permission.write(Role.user(userId))
      );

      // Add team permissions if teamId exists
      if (teamId) {
        permissions.push(
          Permission.read(Role.team(teamId)),
          Permission.write(Role.team(teamId, "owner"))
        );
      }
    } else {
      // Anonymous ticket - only admins/support can access
      // Using Role.any() for read to allow support team access
      permissions.push(
        Permission.read(Role.any())
      );
    }

    return this.create(
      {
        title,
        description,
        userId: userId || null,
        teamId: teamId || null,
        contactEmail: contactEmail || null,
        contactName: contactName || null,
        category,
        priority,
        status: "open" as const,
        messages: [],
        deviceInfo: deviceInfo || null,
        currentUrl: currentUrl || null,
        assignee: null,
        lastActivity: now,
        attachments: attachments || [],
        metadata: metadata ? JSON.stringify(metadata) : null,
      } as any,
      undefined,
      permissions
    );
  }

