import { ref, computed, provide } from "vue";
import { useStore } from "@nanostores/vue";
import { type SupportTicket } from "@appwrite/schemas/supportTicket";
import {
  supportTicketStore,
  currentSupportTicket,
  createSupportTicket,
  updateTicketStatus,
  assignTicket,
  addTicketMessage,
  updateTicketPriority,
  addTicketAttachment,
  removeTicketAttachment,
  getTicketsByStatus,
  getTicketsByUser,
  getTicketsByTeam,
  getTicketsByAssignee,
  getTicketsByPriority,
  getTicketsByCategory,
  getOpenTickets,
  getUrgentTickets,
  getRecentTickets,
  searchTickets,
} from "@/stores/supportTicketStore";
import { useAuth } from "./useAuth";
import { useTeams } from "./useTeams";
import { useAppwrite } from "./useAppwriteClient";

export type SupportView = 'center' | 'create' | 'list' | 'detail';
export type SupportTicketStatus = 'open' | 'in_progress' | 'waiting' | 'resolved' | 'closed';
export type SupportTicketPriority = 'low' | 'medium' | 'high' | 'urgent';
export type SupportTicketCategory = 'general' | 'technical' | 'billing' | 'account' | 'api' | 'bug_report' | 'feature_request';

const SUPPORT_TICKET_LOADING_KEY = Symbol("supportTicketLoading");
const CURRENT_SUPPORT_TICKET_KEY = Symbol("currentSupportTicket");
const SUPPORT_VIEW_KEY = Symbol("supportView");

export function useSupportTickets(shouldProvide: boolean = false) {
  const currentTicket = useStore(currentSupportTicket);
  const { user } = useAuth();
  const { activeTeam } = useTeams();
  const { storage } = useAppwrite();

  const isLoading = ref(false);
  const error = ref<string | null>(null);
  const supportView = ref<SupportView>('center');
  const tickets = ref<SupportTicket[]>([]);
  const selectedTicket = ref<SupportTicket | null>(null);

  // Form state for ticket creation
  const ticketForm = ref<Partial<SupportTicket & {
    attachments: Array<{ name: string; url: string; type: string; id: string }>;
  }>>({
    title: '',
    description: '',
    category: 'general',
    priority: 'medium',
    attachments: [],
  });

  // Computed properties
  const visibleTickets = computed(() => {
    return tickets.value.slice(0, 20); // Lazy loading like in GlobalActionWidget
  });

  const openTicketsCount = computed(() => {
    return tickets.value.filter(t => !['resolved', 'closed'].includes(t.status || '')).length;
  });

  const urgentTicketsCount = computed(() => {
    return tickets.value.filter(t => t.priority === 'urgent' && !['resolved', 'closed'].includes(t.status || '')).length;
  });

  const canCreateTicket = computed(() => {
    return !!user.value && !!activeTeam.value;
  });

  // Navigation methods
  const setSupportView = (view: SupportView) => {
    supportView.value = view;

