import { ref, computed, onMounted } from 'vue';
import { Icon } from '@iconify/vue';
import type { OAuthAccount } from '@appwrite/schemas/oAuthAccount';
import { useVirtualScrolling, createVirtualScrollContainer, createVirtualScrollSpacer } from '@/components/composables/useVirtualScrolling';
import type { VirtualScrollItem } from '@/components/composables/useVirtualScrolling';

interface Props {
  accounts: OAuthAccount[];
  selectedAccountId?: string;
  allowedPlatforms?: string[];
  enableFooter?: boolean;
  enableClear?: boolean;
  enableConfirm?: boolean;
  requiresAccount?: boolean;
}

interface Emits {
  (e: 'update:selectedAccountId', accountId: string | undefined): void;
  (e: 'select', account: OAuthAccount): void;
  (e: 'confirm', account: OAuthAccount | undefined): void;
  (e: 'clear'): void;
}

const props = withDefaults(defineProps<Props>(), {
  enableFooter: false,
  enableClear: true,
  enableConfirm: false,
  requiresAccount: false
});

