  const groups = new Map<string, OAuthAccount[]>();

  props.accounts.forEach(account => {
    if (props.allowedPlatforms && !props.allowedPlatforms.includes(account.platform)) {
      return;
    }
    if (!groups.has(account.platform)) {
      groups.set(account.platform, []);
    }
    groups.get(account.platform)!.push(account);
  });

  return Array.from(groups.entries())
    .map(([platform, accounts]) => ({
      platform,
      accounts: accounts.sort((a, b) => a.name.localeCompare(b.name))
    }))
    .sort((a, b) => getPlatformName(a.platform).localeCompare(getPlatformName(b.platform)));
});

const filteredGroups = computed(() => {
  if (!searchQuery.value) return groupedAccounts.value;

  const query = searchQuery.value.toLowerCase();
  return groupedAccounts.value.map(group => ({
    ...group,
    accounts: group.accounts.filter(account =>
      account.name.toLowerCase().includes(query) ||
      account.username?.toLowerCase().includes(query)
    )
  })).filter(group => group.accounts.length > 0);
});

// Performance optimization
