<script setup lang="ts">
import { ref, computed } from "vue";
import { useMounted } from "@vueuse/core";
import BaseModal from "@vue/modals/BaseModal.vue";
import TextInput from "@vue/inputs/TextInput.vue";
import { Icon } from "@iconify/vue";
import { useSupportTickets, type SupportTicketCategory, type SupportTicketPriority } from "@/components/composables/useSupportTickets";
import { useNotification } from "@/utils/useNotification";
import { useAuth } from "@/components/composables/useAuth";

// v-model for modal open state
const isOpen = defineModel<boolean>();

const { success, error: showError } = useNotification();
const mounted = useMounted();
const { createNewTicket, createAnonymousTicket, isLoading } = useSupportTickets();
const { user } = useAuth();

const isAuthenticated = computed(() => !!user.value);

// Form state with types
const formData = ref<{
  contactName: string;
  contactEmail: string;
  title: string;
  description: string;
  category: SupportTicketCategory;
  priority: SupportTicketPriority;
}>({
  contactName: "",
  contactEmail: "",
  title: "",
  description: "",
  category: "general",
  priority: "medium",
});

const handleSubmit = async () => {
  try {
    if (isAuthenticated.value) {
      await createNewTicket({
        title: formData.value.title,
        description: formData.value.description,
        category: formData.value.category,
        priority: formData.value.priority,
      });
      success("Support ticket created successfully!");
    } else {
      await createAnonymousTicket({...formData.value});
      success(`We'll contact you at ${formData.value.contactEmail}`);
    }
    handleClose();
  } catch (err) {
    console.error("Error creating support ticket:", err);
    showError(err instanceof Error ? err.message : "Failed to create support ticket");
  }
};
</script>
