  1001‚Üí
  1002‚Üí.large-touch-targets .btn-lg {
  1003‚Üí  min-height: 48px;
  1004‚Üí  min-width: 48px;
  1005‚Üí}
  1006‚Üí
  1007‚Üí/* Performance mode optimizations */
  1008‚Üí.performance-mode * {
  1009‚Üí  will-change: auto !important;
  1010‚Üí  animation-duration: 0.1s !important;
  1011‚Üí  transition-duration: 0.1s !important;
  1012‚Üí}
  1013‚Üí
  1014‚Üí.performance-mode .glass-medium,
  1015‚Üí.performance-mode .glass-strong {
  1016‚Üí  backdrop-filter: none !important;
  1017‚Üí  -webkit-backdrop-filter: none !important;
  1018‚Üí  background: var(--color-surface-primary) !important;
  1019‚Üí}
  1020‚Üí
  1021‚Üí/* Reduced animations for accessibility and battery */
  1022‚Üí.reduce-animations * {
  1023‚Üí  animation-duration: 0.01ms !important;
  1024‚Üí  animation-iteration-count: 1 !important;
  1025‚Üí  transition-duration: 0.01ms !important;
  1026‚Üí}
  1027‚Üí
  1028‚Üí.reduce-animations .animate-pulse,
  1029‚Üí.reduce-animations .animate-bounce,
  1030‚Üí.reduce-animations .animate-spin {
  1031‚Üí  animation: none !important;
  1032‚Üí}
  1033‚Üí
  1034‚Üí/* Mobile-specific breakpoints */
  1035‚Üí@media (max-width: 480px) {
  1036‚Üí  /* Extra small mobile phones */
  1037‚Üí  .responsive-mobile-xs {
  1038‚Üí    font-size: 14px;
  1039‚Üí    padding: var(--spacing-2);
  1040‚Üí  }
  1041‚Üí  
  1042‚Üí  .btn-base {
  1043‚Üí    font-size: var(--font-size-sm);
  1044‚Üí    padding: var(--spacing-2) var(--spacing-3);
  1045‚Üí  }
  1046‚Üí  
  1047‚Üí  .modal-backdrop {
  1048‚Üí    backdrop-filter: none;
  1049‚Üí    -webkit-backdrop-filter: none;
  1050‚Üí  }
  1051‚Üí}
  1052‚Üí
  1053‚Üí@media (max-width: 640px) {
  1054‚Üí  /* Small mobile devices */
  1055‚Üí  .responsive-mobile-sm {
  1056‚Üí    padding: var(--spacing-3);
  1057‚Üí  }
  1058‚Üí  
  1059‚Üí  .card-base {
  1060‚Üí    border-radius: var(--radius-lg);
  1061‚Üí    margin: var(--spacing-2);
  1062‚Üí  }
  1063‚Üí  
  1064‚Üí  .glass-medium,
  1065‚Üí  .glass-strong {
  1066‚Üí    backdrop-filter: blur(8px) !important;
  1067‚Üí    -webkit-backdrop-filter: blur(8px) !important;
  1068‚Üí  }
  1069‚Üí}
  1070‚Üí
  1071‚Üí/* Landscape mobile orientation */
  1072‚Üí@media (max-height: 500px) and (orientation: landscape) {
  1073‚Üí  .landscape-optimize {
  1074‚Üí    height: 100vh;
  1075‚Üí    overflow-y: auto;
  1076‚Üí  }
  1077‚Üí  
  1078‚Üí  .notifications-container {
  1079‚Üí    top: var(--spacing-2);
  1080‚Üí    right: var(--spacing-2);
  1081‚Üí  }
  1082‚Üí  
  1083‚Üí  .chat-widget-container {
  1084‚Üí    bottom: var(--spacing-2);
  1085‚Üí    right: var(--spacing-2);
  1086‚Üí  }
  1087‚Üí}
  1088‚Üí
  1089‚Üí/* High DPI displays (retina) */
  1090‚Üí@media (-webkit-min-device-pixel-ratio: 2), (min-resolution: 2dppx) {
  1091‚Üí  .retina-optimize {
  1092‚Üí    image-rendering: -webkit-optimize-contrast;
  1093‚Üí    image-rendering: crisp-edges;
  1094‚Üí  }
  1095‚Üí  
  1096‚Üí  .retina-optimize img {
  1097‚Üí    image-rendering: -webkit-optimize-contrast;
  1098‚Üí    image-rendering: crisp-edges;
  1099‚Üí  }
  1100‚Üí}
  1101‚Üí
  1102‚Üí/* Hover detection for touch devices */
  1103‚Üí@media (hover: none) {
  1104‚Üí  .hover-only {
  1105‚Üí    display: none !important;
  1106‚Üí  }
  1107‚Üí  
  1108‚Üí  .btn-base:hover {
  1109‚Üí    transform: none !important;
  1110‚Üí  }
  1111‚Üí  
  1112‚Üí  .card-base:hover {
  1113‚Üí    transform: none !important;
  1114‚Üí    box-shadow: var(--shadow-sm) !important;
  1115‚Üí  }
  1116‚Üí}
  1117‚Üí
  1118‚Üí/* Pointer device optimization */
  1119‚Üí@media (pointer: coarse) {
  1120‚Üí  /* Touch devices */
  1121‚Üí  .form-control-base {
  1122‚Üí    font-size: 16px; /* Prevents zoom on iOS */
  1123‚Üí    padding: var(--spacing-3);
  1124‚Üí  }
  1125‚Üí  
  1126‚Üí  .btn-base {
  1127‚Üí    padding: var(--spacing-3) var(--spacing-4);
  1128‚Üí  }
  1129‚Üí  
  1130‚Üí  select,
  1131‚Üí  input,
  1132‚Üí  textarea {
  1133‚Üí    font-size: 16px; /* Prevents zoom on iOS */
  1134‚Üí  }
  1135‚Üí}
  1136‚Üí
  1137‚Üí/* Safe area insets for devices with notches */
  1138‚Üí@supports (padding: max(0px)) {
  1139‚Üí  .safe-area-inset-top {
  1140‚Üí    padding-top: max(var(--spacing-4), env(safe-area-inset-top));
  1141‚Üí  }
  1142‚Üí  
  1143‚Üí  .safe-area-inset-bottom {
  1144‚Üí    padding-bottom: max(var(--spacing-4), env(safe-area-inset-bottom));
  1145‚Üí  }
  1146‚Üí  
  1147‚Üí  .safe-area-inset-left {
  1148‚Üí    padding-left: max(var(--spacing-4), env(safe-area-inset-left));
  1149‚Üí  }
  1150‚Üí  
  1151‚Üí  .safe-area-inset-right {
  1152‚Üí    padding-right: max(var(--spacing-4), env(safe-area-inset-right));
  1153‚Üí  }
  1154‚Üí}
  1155‚Üí
  1156‚Üí/* PWA display modes */
  1157‚Üí@media (display-mode: standalone) {
  1158‚Üí  .pwa-standalone {
  1159‚Üí    padding-top: var(--spacing-4);
  1160‚Üí  }
  1161‚Üí  
  1162‚Üí  .pwa-standalone .navbar {
  1163‚Üí    padding-top: env(safe-area-inset-top);
  1164‚Üí  }
  1165‚Üí}
  1166‚Üí
  1167‚Üí/* Connection-aware styles */
  1168‚Üí.slow-connection .glass-medium,
  1169‚Üí.slow-connection .glass-strong {
  1170‚Üí  backdrop-filter: none !important;
  1171‚Üí  -webkit-backdrop-filter: none !important;
  1172‚Üí  background: var(--color-surface-primary) !important;
  1173‚Üí}
  1174‚Üí
  1175‚Üí.slow-connection img {
  1176‚Üí  filter: blur(2px);
  1177‚Üí  transition: filter var(--transition-base);
  1178‚Üí}
  1179‚Üí
  1180‚Üí.slow-connection img.loaded {
  1181‚Üí  filter: none;
  1182‚Üí}
  1183‚Üí
  1184‚Üí/* Gesture feedback */
  1185‚Üí.gesture-feedback {
  1186‚Üí  position: relative;
  1187‚Üí  overflow: hidden;
  1188‚Üí}
  1189‚Üí
  1190‚Üí.gesture-feedback::after {
  1191‚Üí  content: '';
  1192‚Üí  position: absolute;
  1193‚Üí  top: 50%;
  1194‚Üí  left: 50%;
  1195‚Üí  width: 0;
  1196‚Üí  height: 0;
  1197‚Üí  border-radius: 50%;
  1198‚Üí  background: rgba(0, 0, 0, 0.1);
  1199‚Üí  transform: translate(-50%, -50%);
  1200‚Üí  transition: width 0.3s ease, height 0.3s ease;
  1201‚Üí  pointer-events: none;
  1202‚Üí}
  1203‚Üí
  1204‚Üí.gesture-feedback.active::after {
  1205‚Üí  width: 100px;
  1206‚Üí  height: 100px;
  1207‚Üí}
  1208‚Üí
  1209‚Üí/* Dark mode gesture feedback */
  1210‚Üí[data-theme="dark"] .gesture-feedback::after {
  1211‚Üí  background: rgba(255, 255, 255, 0.1);
  1212‚Üí}
  1213‚Üí
  1214‚Üí/* Mobile navigation improvements - removed unused mobile-nav-optimize styles that caused gray bar at bottom */
  1215‚Üí
  1216‚Üí/* Swipe gesture indicators */
  1217‚Üí.swipe-indicator {
  1218‚Üí  position: relative;
  1219‚Üí}
  1220‚Üí
  1221‚Üí.swipe-indicator::before {
  1222‚Üí  content: '';
  1223‚Üí  position: absolute;
  1224‚Üí  top: 50%;
  1225‚Üí  right: var(--spacing-4);
  1226‚Üí  width: 20px;
  1227‚Üí  height: 2px;
  1228‚Üí  background: var(--color-gray-400);
  1229‚Üí  transform: translateY(-50%);
  1230‚Üí  opacity: 0.5;
  1231‚Üí}
  1232‚Üí
  1233‚Üí.swipe-indicator::after {
  1234‚Üí  content: '';
  1235‚Üí  position: absolute;
  1236‚Üí  top: 50%;
  1237‚Üí  right: var(--spacing-3);
  1238‚Üí  width: 0;
  1239‚Üí  height: 0;
  1240‚Üí  border-left: 6px solid var(--color-gray-400);
  1241‚Üí  border-top: 4px solid transparent;
  1242‚Üí  border-bottom: 4px solid transparent;
  1243‚Üí  transform: translateY(-50%);
  1244‚Üí  opacity: 0.5;
  1245‚Üí}
  1246‚Üí
  1247‚Üí/* Loading states optimized for mobile */
  1248‚Üí.mobile-loading {
  1249‚Üí  display: flex;
  1250‚Üí  flex-direction: column;
  1251‚Üí  align-items: center;
  1252‚Üí  justify-content: center;
  1253‚Üí  min-height: 200px;
  1254‚Üí  padding: var(--spacing-8);
  1255‚Üí}
  1256‚Üí
  1257‚Üí.mobile-loading .loading-spinner {
  1258‚Üí  width: 40px;
  1259‚Üí  height: 40px;
  1260‚Üí  border: 3px solid var(--color-gray-200);
  1261‚Üí  border-top: 3px solid var(--color-primary-500);
  1262‚Üí  border-radius: 50%;
  1263‚Üí  animation: spin 1s linear infinite;
  1264‚Üí}
  1265‚Üí
  1266‚Üí.mobile-loading .loading-text {
  1267‚Üí  margin-top: var(--spacing-4);
  1268‚Üí  font-size: var(--font-size-sm);
  1269‚Üí  color: var(--color-text-secondary);
  1270‚Üí  text-align: center;
  1271‚Üí}
  1272‚Üí
  1273‚Üí/* Battery optimization styles */
  1274‚Üí.battery-optimize {
  1275‚Üí  animation-play-state: paused !important;
  1276‚Üí}
  1277‚Üí
  1278‚Üí.battery-optimize * {
  1279‚Üí  animation-play-state: paused !important;
  1280‚Üí  transition-duration: 0.1s !important;
  1281‚Üí}
  1282‚Üí
  1283‚Üí.battery-optimize .glass-medium,
  1284‚Üí.battery-optimize .glass-strong {
  1285‚Üí  backdrop-filter: none !important;
  1286‚Üí  -webkit-backdrop-filter: none !important;
  1287‚Üí  background: var(--color-surface-primary) !important;
  1288‚Üí}
  1289‚Üí
  1290‚Üí.battery-optimize .animate-pulse,
  1291‚Üí.battery-optimize .animate-bounce,
  1292‚Üí.battery-optimize .animate-spin {
  1293‚Üí  animation: none !important;
  1294‚Üí}
  1295‚Üí
  1296‚Üí/* Accessibility enhancements for mobile */
  1297‚Üí@media (max-width: 768px) {
  1298‚Üí  .mobile-a11y button,
  1299‚Üí  .mobile-a11y [role="button"],
  1300‚Üí  .mobile-a11y input,
  1301‚Üí  .mobile-a11y select {
  1302‚Üí    min-height: 44px;
  1303‚Üí    font-size: 16px;
  1304‚Üí  }
  1305‚Üí  
  1306‚Üí  .mobile-a11y .focus-ring:focus-visible {
  1307‚Üí    outline: 3px solid var(--color-primary-500);
  1308‚Üí    outline-offset: 2px;
  1309‚Üí  }
  1310‚Üí}

