   642‚Üí    autoHideTimers.clear();
   643‚Üí    pausedTimers.clear();
   644‚Üí  });
   645‚Üí
   646‚Üí  return {
   647‚Üí    // State
   648‚Üí    notifications: readonly(notifications),
   649‚Üí    groups: readonly(groups),
   650‚Üí    queuedNotifications: readonly(queuedNotifications),
   651‚Üí    settings: readonly(settings),
   652‚Üí
   653‚Üí    // Computed
   654‚Üí    visibleNotifications,
   655‚Üí    hasQueuedNotifications,
