import { ref, reactive, computed, watch, onMounted, onUnmounted, readonly, type Ref } from 'vue';
import { useAppwriteClient } from './useAppwriteClient';
import { useAuth } from './useAuth';
import { useRealtime, type RealtimeEvent, type RealtimeEventType } from './useRealtime';
import { ExecutionMethod } from 'appwrite';

// TypeScript interfaces
export type NotificationType = 'success' | 'error' | 'warning' | 'info' | 'default';
export type NotificationPriority = 'low' | 'medium' | 'high' | 'urgent';

export interface NotificationAction {
  id: string;
  label: string;
  icon?: string;
  variant?: 'primary' | 'secondary' | 'danger' | 'ghost';
  loading?: boolean;
  handler: (notificationId: string) => Promise<void> | void;
}

export interface NotificationMessage {
  text?: string;
  html?: string;
}

export interface NotificationDetails {
  source?: string;
  metadata?: Record<string, any>;
  expandable?: boolean;
}

export interface SmartNotification {
  id: string;
  type: NotificationType;
  priority: NotificationPriority;
  title: string;
  message: string | NotificationMessage;
  icon: string;
  timestamp: Date;
  autoHide?: boolean;
  persistent?: boolean;
  progress?: number;
  actions?: NotificationAction[];
  details?: NotificationDetails;
  expanded?: boolean;
  isPaused?: boolean;
  aiPriority?: boolean;
  aiScore?: number;
  showAiProcessing?: boolean;
  groupId?: string;
  metadata?: Record<string, any>;
}

export interface NotificationGroup {
  id: string;
  title: string;
  count: number;
  priority: NotificationPriority;
  lastNotification: SmartNotification;
  collapsed?: boolean;
}

// AI prioritization system
interface AiPrioritizationConfig {
  enabled: boolean;
  scoreThreshold: number;
  priorityBoosts: Record<string, number>;
  contextWeights: Record<string, number>;
}

// Stack prevention system  
interface StackPreventionConfig {
  enabled: boolean;
  similarityThreshold: number;
  maxSimilarNotifications: number;
  groupingTimeWindow: number; // milliseconds
}

export interface NotificationConfig {
  maxVisible?: number;
  defaultAutoHideDelay?: number;
  enableAiPrioritization?: boolean;
  enableStackingPrevention?: boolean;
  enableSmartGrouping?: boolean;
  enableRealTimeSync?: boolean;
}

export const useNotifications = (config: NotificationConfig = {}) => {
  // Configuration with defaults
  const settings = reactive({
    maxVisible: config.maxVisible || 5,
    defaultAutoHideDelay: config.defaultAutoHideDelay || 5000,
    enableAiPrioritization: config.enableAiPrioritization ?? true,
    enableStackingPrevention: config.enableStackingPrevention ?? true,
    enableSmartGrouping: config.enableSmartGrouping ?? true,
    enableRealTimeSync: config.enableRealTimeSync ?? true
  });

  // State
  const notifications: Ref<SmartNotification[]> = ref([]);
  const groups: Ref<NotificationGroup[]> = ref([]);
  const queuedNotifications: Ref<SmartNotification[]> = ref([]);
  const dismissedIds = new Set<string>();
  const autoHideTimers = new Map<string, NodeJS.Timeout>();
  const pausedTimers = new Map<string, Date>();

  // Composables
  const { functions } = useAppwriteClient();
  const { user } = useAuth();
  const { subscribe: subscribeRealtime, isConnected } = useRealtime();

  // AI Prioritization System
  const aiConfig = reactive<AiPrioritizationConfig>({
    enabled: settings.enableAiPrioritization,
    scoreThreshold: 0.7,
    priorityBoosts: {
      'billing.credit.exhausted': 1.0,
      'platform.error': 0.8,
      'post.failed': 0.7,
      'oauth.expired': 0.6,
      'workflow.failed': 0.5
    },
    contextWeights: {
      time_sensitivity: 0.3,
      user_importance: 0.25,
      business_impact: 0.25,
      frequency: 0.2
    }
  });

  // Stack Prevention System
  const stackConfig = reactive<StackPreventionConfig>({
    enabled: settings.enableStackingPrevention,
    similarityThreshold: 0.8,
    maxSimilarNotifications: 2,
    groupingTimeWindow: 60000 // 1 minute
  });

  // AI-powered priority scoring
  const calculateAiPriority = async (notification: Partial<SmartNotification>): Promise<number> => {
    if (!aiConfig.enabled) return 0.5;

    try {
      const context = {
        type: notification.type,
        title: notification.title,
        message: typeof notification.message === 'string' ? notification.message : notification.message?.text,
        timestamp: notification.timestamp?.toISOString(),
        metadata: notification.metadata,
        user_context: {
          id: user.value?.$id,
          team_id: user.value?.prefs?.selectedTeamId,
          timezone: Intl.DateTimeFormat().resolvedOptions().timeZone,
          current_time: new Date().toISOString()
        }
      };

      const response = await functions.createExecution(
        'AI_API',
        JSON.stringify({
          action: 'prioritize_notification',
          context,
          config: aiConfig
        }),
        false,
        '/action',
        ExecutionMethod.POST,
      );

      const result = JSON.parse(response.responseBody);

      if (result.error) {
        console.warn('AI prioritization failed:', result.message);
        return 0.5;
      }

      return Math.max(0, Math.min(1, result.priority_score || 0.5));
    } catch (error) {
      console.warn('AI prioritization error:', error);
      return 0.5;
    }
  };

  // Smart grouping system
  const calculateSimilarity = (notif1: SmartNotification, notif2: SmartNotification): number => {
    let similarity = 0;
    let factors = 0;

    // Type similarity
    if (notif1.type === notif2.type) {
      similarity += 0.3;
    }
    factors += 0.3;

    // Title similarity (simple word matching)
    const words1 = notif1.title.toLowerCase().split(' ');
    const words2 = notif2.title.toLowerCase().split(' ');
    const commonWords = words1.filter(word => words2.includes(word));
    const titleSimilarity = commonWords.length / Math.max(words1.length, words2.length);
    similarity += titleSimilarity * 0.4;
    factors += 0.4;

    // Time proximity
    const timeDiff = Math.abs(notif1.timestamp.getTime() - notif2.timestamp.getTime());
    const timeProximity = Math.max(0, 1 - timeDiff / stackConfig.groupingTimeWindow);
    similarity += timeProximity * 0.3;
    factors += 0.3;

    return similarity / factors;
  };

  const shouldGroupNotification = (newNotification: SmartNotification): string | null => {
    if (!stackConfig.enabled) return null;

    const recentNotifications = notifications.value.filter(n => {
      const timeDiff = Date.now() - n.timestamp.getTime();
      return timeDiff < stackConfig.groupingTimeWindow;
    });

    for (const existing of recentNotifications) {
      const similarity = calculateSimilarity(newNotification, existing);
      if (similarity > stackConfig.similarityThreshold) {
        // Check if we already have too many similar notifications
        const similarCount = notifications.value.filter(n => 
          calculateSimilarity(n, existing) > stackConfig.similarityThreshold
        ).length;

        if (similarCount >= stackConfig.maxSimilarNotifications) {
          return existing.groupId || existing.id;
        }
      }
    }

    return null;
  };

  // Core notification methods
  const addNotification = async (
    notificationData: Omit<SmartNotification, 'id' | 'timestamp' | 'aiScore' | 'aiPriority'>
  ): Promise<string> => {
    const id = crypto.randomUUID();

    const notification: SmartNotification = {
      id,
      timestamp: new Date(),
      autoHide: notificationData.autoHide ?? true,
      persistent: notificationData.persistent ?? false,
      aiScore: 0.5,
      aiPriority: false,
      ...notificationData
    };

    // AI prioritization
    if (aiConfig.enabled) {
      notification.showAiProcessing = true;
      const aiScore = await calculateAiPriority(notification);
      notification.aiScore = aiScore;
      notification.aiPriority = aiScore > aiConfig.scoreThreshold;
      notification.showAiProcessing = false;

      // Adjust priority based on AI score
      if (aiScore > 0.9) notification.priority = 'urgent';
      else if (aiScore > 0.7) notification.priority = 'high';
      else if (aiScore > 0.5) notification.priority = 'medium';
      else notification.priority = 'low';
    }

    // Smart grouping
    const groupId = shouldGroupNotification(notification);
    if (groupId) {
      notification.groupId = groupId;
      updateNotificationGroup(groupId, notification);
    }

    // Add to appropriate queue
    if (notifications.value.length >= settings.maxVisible) {
      queuedNotifications.value.push(notification);
    } else {
      notifications.value.push(notification);

      // Sort by priority and AI score
      sortNotifications();

      // Set up auto-hide if enabled
      if (notification.autoHide && !notification.persistent) {
        scheduleAutoHide(notification);
      }
    }

    console.log(`üì¢ Added notification: ${notification.title} (AI Score: ${notification.aiScore})`);

    return id;
  };

  const dismissNotification = (id: string): void => {
    const index = notifications.value.findIndex(n => n.id === id);
    if (index !== -1) {
      const notification = notifications.value[index];
      notifications.value.splice(index, 1);

      // Cancel auto-hide timer
      if (autoHideTimers.has(id)) {
        clearTimeout(autoHideTimers.get(id)!);
        autoHideTimers.delete(id);
      }

      // Remove from paused timers
      pausedTimers.delete(id);

      // Remember dismissal
      dismissedIds.add(id);

      // Process queue
      processQueue();

      console.log(`üóëÔ∏è Dismissed notification: ${notification.title}`);
    }
  };

  const updateNotification = (id: string, updates: Partial<SmartNotification>): void => {
    const notification = notifications.value.find(n => n.id === id);
    if (notification) {
      Object.assign(notification, updates);

      // Re-sort if priority changed
      if ('priority' in updates || 'aiScore' in updates) {
        sortNotifications();
      }
    }
  };

  const clearAllNotifications = (): void => {
    // Clear all timers
    autoHideTimers.forEach(timer => clearTimeout(timer));
    autoHideTimers.clear();
    pausedTimers.clear();

    // Clear arrays
    notifications.value = [];
    queuedNotifications.value = [];
    groups.value = [];

    console.log('üßπ Cleared all notifications');
  };

  // Notification grouping
  const updateNotificationGroup = (groupId: string, notification: SmartNotification): void => {
    let group = groups.value.find(g => g.id === groupId);

    if (!group) {
      group = {
        id: groupId,
        title: `Similar to "${notification.title}"`,
        count: 1,
        priority: notification.priority,
        lastNotification: notification,
        collapsed: true
      };
      groups.value.push(group);
    } else {
      group.count++;
      group.lastNotification = notification;

      // Update priority to highest in group
      if (notification.priority === 'urgent' || 
          (notification.priority === 'high' && group.priority !== 'urgent') ||
          (notification.priority === 'medium' && !['urgent', 'high'].includes(group.priority))) {
        group.priority = notification.priority;
      }
    }
  };

  // Timer management
  const scheduleAutoHide = (notification: SmartNotification): void => {
    if (autoHideTimers.has(notification.id)) {
      clearTimeout(autoHideTimers.get(notification.id)!);
    }

    const delay = settings.defaultAutoHideDelay * (notification.priority === 'urgent' ? 2 : 1);

    const timer = setTimeout(() => {
      dismissNotification(notification.id);
    }, delay);

    autoHideTimers.set(notification.id, timer);
  };

  const pauseAutoHide = (id: string): void => {
    const notification = notifications.value.find(n => n.id === id);
    if (notification && autoHideTimers.has(id)) {
      clearTimeout(autoHideTimers.get(id)!);
      autoHideTimers.delete(id);
      pausedTimers.set(id, new Date());
      notification.isPaused = true;
    }
  };

  const resumeAutoHide = (id: string): void => {
    const notification = notifications.value.find(n => n.id === id);
    if (notification && pausedTimers.has(id) && notification.autoHide && !notification.persistent) {
      const pausedAt = pausedTimers.get(id)!;
      const pauseDuration = Date.now() - pausedAt.getTime();
      const originalDelay = settings.defaultAutoHideDelay * (notification.priority === 'urgent' ? 2 : 1);
      const remainingDelay = Math.max(1000, originalDelay - pauseDuration);

      const timer = setTimeout(() => {
        dismissNotification(id);
      }, remainingDelay);

      autoHideTimers.set(id, timer);
      pausedTimers.delete(id);
      notification.isPaused = false;
    }
  };

  const getTimerProgress = (id: string): number => {
    const notification = notifications.value.find(n => n.id === id);
    if (!notification || !notification.autoHide || notification.persistent || notification.isPaused) {
      return 0;
    }

    const elapsed = Date.now() - notification.timestamp.getTime();
    const total = settings.defaultAutoHideDelay * (notification.priority === 'urgent' ? 2 : 1);

    return Math.min(100, (elapsed / total) * 100);
  };

  // Queue management
  const processQueue = (): void => {
    while (notifications.value.length < settings.maxVisible && queuedNotifications.value.length > 0) {
      const next = queuedNotifications.value.shift();
      if (next && !dismissedIds.has(next.id)) {
        notifications.value.push(next);

        if (next.autoHide && !next.persistent) {
          scheduleAutoHide(next);
        }
      }
    }

    sortNotifications();
  };

  const sortNotifications = (): void => {
    notifications.value.sort((a, b) => {
      // Priority order: urgent > high > medium > low
      const priorityOrder = { urgent: 4, high: 3, medium: 2, low: 1 };
      const priorityDiff = priorityOrder[b.priority] - priorityOrder[a.priority];

      if (priorityDiff !== 0) return priorityDiff;

      // Then by AI score
      const aiScoreDiff = (b.aiScore || 0) - (a.aiScore || 0);
      if (aiScoreDiff !== 0) return aiScoreDiff;

      // Then by timestamp (newer first)
      return b.timestamp.getTime() - a.timestamp.getTime();
    });
  };

  // Action execution
  const executeNotificationAction = async (notificationId: string, action: NotificationAction): Promise<void> => {
    const notification = notifications.value.find(n => n.id === notificationId);
    if (!notification) return;

    // Set loading state
    action.loading = true;

    try {
      await action.handler(notificationId);

      // Auto-dismiss after successful action unless persistent
      if (!notification.persistent) {
        setTimeout(() => dismissNotification(notificationId), 1000);
      }
    } catch (error) {
      console.error('Notification action failed:', error);

      // Show error notification
      addNotification({
        type: 'error',
        priority: 'high',
        title: 'Action Failed',
        message: `Failed to execute "${action.label}": ${error instanceof Error ? error.message : 'Unknown error'}`,
        icon: 'fluent:error-circle-24-filled',
        autoHide: true
      });
    } finally {
      action.loading = false;
    }
  };

  // Realtime integration
  const handleRealtimeEvent = (event: RealtimeEvent): void => {
    if (!settings.enableRealTimeSync) return;

    // Map realtime events to notifications
    const notificationMapping: Record<string, Partial<SmartNotification>> = {
      'post.published': {
        type: 'success',
        title: 'Post Published',
        message: 'Your post has been successfully published',
        icon: 'fluent:checkmark-circle-24-filled',
        priority: 'medium'
      },
      'post.failed': {
        type: 'error',
        title: 'Post Failed',
        message: 'Failed to publish your post',
        icon: 'fluent:error-circle-24-filled',
        priority: 'high',
        actions: [{
          id: 'retry',
          label: 'Retry',
          icon: 'fluent:arrow-clockwise-24-regular',
          variant: 'primary',
          handler: async () => {
            // Retry logic would go here
            console.log('Retrying post...');
          }
        }]
      },
      'billing.credit.low': {
        type: 'warning',
        title: 'Credits Running Low',
        message: 'You have less than 100 AI credits remaining',
        icon: 'fluent:warning-24-filled',
        priority: 'high',
        persistent: true,
        actions: [{
          id: 'upgrade',
          label: 'Upgrade Plan',
          icon: 'fluent:arrow-up-24-regular',
          variant: 'primary',
          handler: async () => {
            window.location.href = '/dashboard/billing';
          }
        }]
      },
      'oauth.expired': {
        type: 'error',
        title: 'Account Disconnected',
        message: 'A social media account has been disconnected',
        icon: 'fluent:plug-disconnected-24-filled',
        priority: 'high',
        actions: [{
          id: 'reconnect',
          label: 'Reconnect',
          icon: 'fluent:link-24-regular',
          variant: 'primary',
          handler: async () => {
            window.location.href = '/dashboard/accounts';
          }
        }]
      }
    };

    const template = notificationMapping[event.type];
    if (template) {
      addNotification({
        type: template.type || 'info',
        priority: template.priority || 'medium',
        title: template.title || 'Notification',
        message: template.message || 'You have a new notification',
        icon: template.icon || 'fluent:info-24-filled',
        actions: template.actions || [],
        metadata: {
          realtimeEvent: event,
          source: 'realtime'
        }
      });
    }
  };

  // Computed properties
  const visibleNotifications = computed(() => notifications.value.slice(0, settings.maxVisible));
  const hasQueuedNotifications = computed(() => queuedNotifications.value.length > 0);
  const totalNotifications = computed(() => notifications.value.length + queuedNotifications.value.length);

  // Predefined notification helpers
  const showSuccess = (title: string, message: string, options?: Partial<SmartNotification>) => {
    return addNotification({
      type: 'success',
      title,
      message,
      icon: 'fluent:checkmark-circle-24-filled',
      priority: 'medium',
      ...options
    });
  };

  const showError = (title: string, message: string, options?: Partial<SmartNotification>) => {
    return addNotification({
      type: 'error',
      title,
      message,
      icon: 'fluent:error-circle-24-filled',
      priority: 'high',
      ...options
    });
  };

  const showWarning = (title: string, message: string, options?: Partial<SmartNotification>) => {
    return addNotification({
      type: 'warning',
      title,
      message,
      icon: 'fluent:warning-24-filled',
      priority: 'medium',
      ...options
    });
  };

  const showInfo = (title: string, message: string, options?: Partial<SmartNotification>) => {
    return addNotification({
      type: 'info',
      title,
      message,
      icon: 'fluent:info-24-filled',
      priority: 'low',
      ...options
    });
  };

  // Lifecycle
  onMounted(() => {
    // Subscribe to realtime events if enabled
    if (settings.enableRealTimeSync && isConnected.value) {
      const eventTypes: RealtimeEventType[] = [
        'post.published', 'post.failed', 'billing.credit.low', 
        'oauth.expired', 'platform.error', 'workflow.completed'
      ];

      eventTypes.forEach(type => {
        subscribeRealtime(type as any, handleRealtimeEvent);
      });
    }
  });

  onUnmounted(() => {
    // Clear all timers
    autoHideTimers.forEach(timer => clearTimeout(timer));
    autoHideTimers.clear();
    pausedTimers.clear();
  });

  return {
    // State
    notifications: readonly(notifications),
    groups: readonly(groups),
    queuedNotifications: readonly(queuedNotifications),
    settings: readonly(settings),

    // Computed
    visibleNotifications,
    hasQueuedNotifications,
    totalNotifications,

    // Core methods
    addNotification,
    dismissNotification,
    updateNotification,
    clearAllNotifications,

    // Timer management
    pauseAutoHide,
    resumeAutoHide,
    getTimerProgress,

    // Action execution
    executeNotificationAction,

    // Convenience methods
    showSuccess,
    showError,
    showWarning,
    showInfo,

    // Configuration
    aiConfig: readonly(aiConfig),
    stackConfig: readonly(stackConfig)
  };
};

export default useNotifications;

