   138‚Üí  return props.isValid 
   139‚Üí    ? "success !border-success-500 focus:!shadow-focus-success" 
   140‚Üí    : "error !border-error-500 focus:!shadow-focus-error";
   141‚Üí});
   142‚Üí
   143‚Üíconst toggleDropdown = () => {
   144‚Üí  if (props.disabled) return;
   145‚Üí  isOpen.value = !isOpen.value;
   146‚Üí  if (isOpen.value) {
   147‚Üí    nextTick(update);
   148‚Üí  }
   149‚Üí};
   150‚Üí
   151‚Üíconst selectOption = (option: Option) => {
   152‚Üí  if (props.multiple && Array.isArray(modelValue.value)) {
   153‚Üí    // Handle special case for exclusive options like 'all'
   154‚Üí    if (props.exclusiveOption) {
   155‚Üí      if (option.value === props.exclusiveOption) {
   156‚Üí        // When 'all' is selected, deselect all other options
   157‚Üí        modelValue.value = [props.exclusiveOption];
   158‚Üí      } else {
   159‚Üí        // When a specific option is selected:
   160‚Üí        // 1. Remove the 'all' option if it exists
   161‚Üí        // 2. Toggle the selected option
   162‚Üí        const newValue = modelValue.value.filter(v => v !== props.exclusiveOption);
   163‚Üí        const index = newValue.indexOf(option.value);
   164‚Üí
   165‚Üí        if (index === -1) {
   166‚Üí          // Add the option if not already selected
   167‚Üí          newValue.push(option.value);
   168‚Üí          modelValue.value = newValue;
   169‚Üí        } else {
   170‚Üí          // Remove the option if already selected
   171‚Üí          newValue.splice(index, 1);
   172‚Üí          // If nothing left, default to 'all'
   173‚Üí          modelValue.value = newValue.length === 0 ? [props.exclusiveOption] : newValue;
   174‚Üí        }
   175‚Üí      }
   176‚Üí    } else {
   177‚Üí      // Standard multiple selection behavior without exclusivity
   178‚Üí      const index = modelValue.value.indexOf(option.value);
   179‚Üí      if (index === -1) {
   180‚Üí        modelValue.value = [...(modelValue.value || []), option.value];
   181‚Üí      } else {
   182‚Üí        modelValue.value = modelValue.value.filter((v) => v !== option.value);
   183‚Üí      }
   184‚Üí    }
   185‚Üí  } else {
   186‚Üí    modelValue.value = option.value;
   187‚Üí    isOpen.value = false;
   188‚Üí  }
   189‚Üí
   190‚Üí  hasBlurredInput.value = true;
   191‚Üí  nextTick(() => {
   192‚Üí    if (!props.multiple) {
   193‚Üí      isOpen.value = false;
   194‚Üí    }
   195‚Üí    validateInput();
   196‚Üí  });
   197‚Üí};
   198‚Üí
   199‚Üíconst isSelected = (value: string) => {
   200‚Üí  return Array.isArray(modelValue.value)
   201‚Üí    ? modelValue.value.includes(value)
   202‚Üí    : modelValue.value === value;
   203‚Üí};
   204‚Üí
   205‚Üíconst getOptionLabel = (value: string) => {
   206‚Üí  const option = props.options.find((opt) => opt.value === value);
   207‚Üí  return option ? option.label : value;
   208‚Üí};
   209‚Üí
   210‚Üíconst handleBlur = () => {
   211‚Üí  hasBlurredInput.value = true;
   212‚Üí  if (!isOpen.value) {
   213‚Üí    nextTick(validateInput);
   214‚Üí  }
