   216‚Üí
   217‚Üíconst validateInput = () => {
   218‚Üí  if (props.isValidTest === undefined) return;
   219‚Üí  errorMessage.value = "";
   220‚Üí
   221‚Üí  if (props.required &&
   222‚Üí    (!modelValue.value || (Array.isArray(modelValue.value) && modelValue.value.length === 0))) {
   223‚Üí    errorMessage.value = "This field is required";
   224‚Üí  } else if (props.isValidTest && !props.isValidTest(modelValue.value)) {
   225‚Üí    errorMessage.value = props.invalidMessage || "Invalid input";
   226‚Üí  }
   227‚Üí};
   228‚Üí
   229‚ÜíonClickOutside(referenceEl, () => {
   230‚Üí  isOpen.value = false;
   231‚Üí});
   232‚Üí
   233‚Üíconst { width, height } = useWindowSize();
   234‚Üíconst { x: scrollX, y: scrollY } = useWindowScroll();
   235‚Üí
   236‚Üíwatch([width, height, scrollX, scrollY], () => {
   237‚Üí  if (isOpen.value) {
   238‚Üí    update();
