   106→import { ref, computed, watch } from 'vue'
   107→import { Icon } from '@iconify/vue'
   108→import ShootingPosts from '@/components/vue/conductor/ShootingPosts.vue'
   109→import FourCards from '@/components/vue/conductor/FourCards.vue'
   110→import { useAnimation } from '@/components/composables/useAnimation'
   111→import { ANIMATION_TOKENS } from '@/components/composables/animationTokens'
   112→import AnimatedFloatingElement from '@/components/vue/ui/AnimatedFloatingElement.vue'
   113→
   114→interface TargetPlatform {
   115→  name: string
   116→  icon: string
   117→  color: string
   118→}
   119→
   120→interface Props {
   121→  platforms?: TargetPlatform[]
   122→  teleportTarget?: string
   123→  showBackground?: boolean
   124→  showText?: boolean
   125→  active?: boolean
   126→}
   127→
   128→const props = withDefaults(defineProps<Props>(), {
   129→  platforms: () => [
   130→    { name: 'Instagram', icon: 'mdi:instagram', color: 'text-pink-600 dark:text-pink-400' },
   131→    { name: 'Facebook', icon: 'mdi:facebook', color: 'text-blue-600 dark:text-blue-400' },
   132→    { name: 'LinkedIn', icon: 'mdi:linkedin', color: 'text-blue-700 dark:text-blue-500' },
   133→    { name: 'TikTok', icon: 'simple-icons:tiktok', color: 'text-gray-900 dark:text-white' },
   134→  ],
   135→  teleportTarget: undefined,
   136→  showBackground: true,
   137→  showText: true,
   138→  active: true
   139→})
   140→
   141→const targetPlatforms = props.platforms
   142→
   143→// Setup responsive animation system
   144→const containerRef = ref<HTMLElement>()
   145→const isFloatingVisible = ref(props.active)
   146→const floatingPositioning = Object.freeze({
   147→  position: 'absolute',
   148→  inset: 0,
   149→  display: 'flex',
   150→  justifyContent: 'center',
   151→  alignItems: 'center',
   152→  width: '100%',
   153→  height: '100%'
   154→})
   155→
   156→// Sync props.active to isFloatingVisible
   157→watch(() => props.active, (newValue) => {
   158→  isFloatingVisible.value = newValue
   159→})
   160→
   161→const animation = useAnimation(containerRef, {
   162→  responsive: {
   163→    enabled: true,
   164→    baselineWidth: 1120,
   165→    minScale: 0.25,
   166→    maxScale: 1.45
   167→  },
   168→  autoplay: {
   169→    threshold: 0.25,
   170→    enabled: computed(() => props.active)
   171→  },
   172→  respectEnvironment: true
   173→})
   174→
   175→// Access responsive helpers (only for SVG viewBox and layout)
   176→const fallbackDimensions = computed(() => ({
   177→  width: ANIMATION_TOKENS.baseline.viewport,
   178→  height: ANIMATION_TOKENS.baseline.containerHeight,
   179→  scale: 1,
   180→  aspectRatio: ANIMATION_TOKENS.baseline.viewport / ANIMATION_TOKENS.baseline.containerHeight
   181→}))
   182→const dimensions = animation.responsive?.dimensions ?? fallbackDimensions
   183→
   184→const clamp = (value: number, min: number, max: number) => Math.min(Math.max(value, min), max)
   185→
   186→const MAX_HERO_CONTENT_WIDTH = 1120
   187→const DUPLICATE_CARD_BASE = 100 // Base size for flying duplicate cards
   188→const HERO_BASE_SOURCE_WIDTH = 320
   189→const HERO_BASE_ARROW_WIDTH = ANIMATION_TOKENS.theaters.workflow.arrowWidth
   190→const HERO_BASE_DEST_WIDTH = ANIMATION_TOKENS.theaters.workflow.cardWidth * 2 + 80
   191→const HERO_BASE_GAP = 72
   192→const HERO_MIN_SCALE = 0.35
   193→const HERO_MAX_SCALE = 1.1
   194→const HERO_MIN_GAP = 24
   195→const HERO_MAX_GAP = 96
   196→
   197→const heroLayout = computed(() => {
   198→  const rawWidth = dimensions.value.width || MAX_HERO_CONTENT_WIDTH
   199→  const contentWidth = Math.max(360, Math.min(rawWidth, MAX_HERO_CONTENT_WIDTH))
   200→  const baseTotalNoGap = HERO_BASE_SOURCE_WIDTH + HERO_BASE_ARROW_WIDTH + HERO_BASE_DEST_WIDTH
   201→  const baseTotalWithGap = baseTotalNoGap + HERO_BASE_GAP * 2
   202→  const normalizedScale = contentWidth / MAX_HERO_CONTENT_WIDTH
   203→  const widthScale = contentWidth / baseTotalWithGap
   204→  let scale = clamp(Math.min(normalizedScale, widthScale), HERO_MIN_SCALE, HERO_MAX_SCALE)
   205→  let totalAtScale = baseTotalNoGap * scale + HERO_BASE_GAP * scale * 2
   206→  if (totalAtScale > contentWidth) {
   207→    scale *= contentWidth / totalAtScale
   208→    totalAtScale = baseTotalNoGap * scale + HERO_BASE_GAP * scale * 2
   209→  }
   210→  const gap = clamp(HERO_BASE_GAP * scale, HERO_MIN_GAP, HERO_MAX_GAP)
   211→  const columns = {
   212→    left: HERO_BASE_SOURCE_WIDTH * scale,
   213→    arrow: HERO_BASE_ARROW_WIDTH * scale,
   214→    right: HERO_BASE_DEST_WIDTH * scale
   215→  }
   216→  const duplicateWidth = Math.max(DUPLICATE_CARD_BASE * scale, 40)
   217→  return {
   218→    scale,
   219→    gap,
   220→    columns,
   221→    duplicateWidth,
   222→    contentWidth,
   223→    totalWidth: totalAtScale
   224→  }
   225→})
   226→
   227→const heroScale = computed(() => heroLayout.value.scale)
   228→
   229→const heroLayoutStyle = computed(() => ({
   230→  gridTemplateColumns: `${Math.round(heroLayout.value.columns.left)}px ${Math.round(heroLayout.value.columns.arrow)}px ${Math.round(heroLayout.value.columns.right)}px`,
   231→  gap: `${Math.round(heroLayout.value.gap)}px`,
   232→  justifyContent: 'center'
   233→}))
   234→
   235→// Base values from design tokens - children will handle responsive scaling
   236→const baseCardWidth = ANIMATION_TOKENS.theaters.workflow.cardWidth
   237→
   238→// Calculate target positions - pass base values, children scale based on their containers
   239→const getTargetX = (index: number) => {
   240→  const layout = heroLayout.value
   241→  const duplicateWidth = layout.duplicateWidth
   242→  const maxInside = Math.max(layout.contentWidth - duplicateWidth, layout.columns.left + layout.columns.arrow)
   243→  const flight = layout.columns.left + layout.gap + layout.columns.arrow + layout.gap + Math.max(layout.columns.right - duplicateWidth, layout.columns.right * 0.5)
   244→  const safeDistance = Math.min(flight, maxInside)
   245→  return `${Math.round(safeDistance)}px`
   246→}
   247→
   248→const getTargetY = (index: number) => {
   249→  const row = Math.floor(index / 2)
   250→  const baseY = (row * 70) - 35 // Center around middle
   251→  return `${Math.round(baseY * heroScale.value)}px`
   252→}
   253→
   254→const arrowDimensions = computed(() => {
   255→  const width = clamp(heroLayout.value.columns.arrow, 140, 420)
   256→  const height = clamp(60 * heroScale.value, 44, 72)
   257→  return {
   258→    width: `${width}px`,
   259→    height: `${height}px`
   260→  }
   261→})
   262→
   263→// Source card responsive sizing (matching AIEnhancementTheater)
   264→const sourceCardStyle = computed(() => {
   265→  const layout = heroLayout.value
   266→  const baseWidth = HERO_BASE_SOURCE_WIDTH * layout.scale
   267→  const width = clamp(baseWidth, 150, Math.max(layout.columns.left * 0.92, 140))
   268→  const padding = clamp(16 * layout.scale, 12, 24)
   269→  return {
   270→    width: `${Math.round(width)}px`,
   271→    maxWidth: '95%',
   272→    padding: `${Math.round(padding)}px`
   273→  }
   274→})
   275→</script>
