import { ref, computed, shallowRef, markRaw, nextTick, readonly } from 'vue'
import type { Component, AsyncComponentLoader, DefineComponent } from 'vue'
import { getGlobalMemoryManager } from './useMemoryManagement'
import { getGlobalAnimationPerformance } from './animationPerformanceUtils'

export interface LazyComponentConfig {
  loadingComponent?: Component
  errorComponent?: Component
  delay?: number
  timeout?: number
  suspensible?: boolean
  retryAttempts?: number
  preloadDistance?: number
  enableCaching?: boolean
  cacheDuration?: number
  enablePrefetch?: boolean
  enablePreload?: boolean
  priority?: 'low' | 'normal' | 'high'
}

export interface ComponentChunk {
  id: string
  name: string
  size?: number
  loadTime?: number
  isLoaded: boolean
  isLoading: boolean
  hasError: boolean
  component?: Component
  error?: Error
  lastAccessed: number
  accessCount: number
  priority: 'low' | 'normal' | 'high'
}

export interface BundleStats {
  totalChunks: number
  loadedChunks: number
  failedChunks: number
  totalSize: number
  loadedSize: number
  averageLoadTime: number
  cacheHitRate: number
  memoryUsage: number
}

export function useCodeSplitting() {
  const memoryManager = getGlobalMemoryManager()
  const performance = getGlobalAnimationPerformance()

  // Component cache with LRU eviction
  const componentCache = ref(new Map<string, ComponentChunk>())
  const loadingPromises = ref(new Map<string, Promise<Component>>())
  const preloadQueue = ref<Array<{ id: string; priority: 'low' | 'normal' | 'high' }>>([])
  const bundleStats = ref<BundleStats>({
    totalChunks: 0,
    loadedChunks: 0,
    failedChunks: 0,
    totalSize: 0,
    loadedSize: 0,
    averageLoadTime: 0,
    cacheHitRate: 0,
    memoryUsage: 0
  })

  // Configuration
  const maxCacheSize = ref(50) // Maximum number of cached components
  const cacheDuration = ref(10 * 60 * 1000) // 10 minutes
  const enableIntelligentPrefetch = ref(true)
  const enableBandwidthOptimization = ref(true)

  // Network information
  const networkInfo = ref({
    effectiveType: 'unknown',
    saveData: false,
    downlink: 10
  })

  // Initialize network monitoring
  const initializeNetworkMonitoring = () => {
    if ('connection' in navigator) {
      const conn = (navigator as any).connection
      networkInfo.value = {
        effectiveType: conn.effectiveType || 'unknown',
        saveData: conn.saveData || false,
        downlink: conn.downlink || 10
      }

      conn.addEventListener('change', () => {
        networkInfo.value = {
          effectiveType: conn.effectiveType || 'unknown',
          saveData: conn.saveData || false,
          downlink: conn.downlink || 10
        }
      })
    }
  }

  // Create lazy component with advanced optimizations
  const createLazyComponent = (
    loader: AsyncComponentLoader,
    config: LazyComponentConfig = {}
  ): DefineComponent => {
    const {
      loadingComponent,
      errorComponent,
      delay = 200,
      timeout = 30000,
      suspensible = false,
      retryAttempts = 3,
      enableCaching = true,
      cacheDuration: componentCacheDuration = cacheDuration.value,
      enablePrefetch = false,
      enablePreload = false,
      priority = 'normal'
    } = config

    const componentId = generateComponentId(loader)

    // Register component in cache
    if (!componentCache.value.has(componentId)) {
      componentCache.value.set(componentId, {
        id: componentId,
        name: loader.name || 'UnknownComponent',
        isLoaded: false,
        isLoading: false,
        hasError: false,
        lastAccessed: Date.now(),
        accessCount: 0,
        priority
      })
      bundleStats.value.totalChunks++
    }

    // Enhanced loader with performance optimizations
    const enhancedLoader = async (): Promise<Component> => {
      const startTime = globalThis.performance.now()
      const chunk = componentCache.value.get(componentId)!

      // Check cache first
      if (enableCaching && chunk.isLoaded && chunk.component) {
        const age = Date.now() - chunk.lastAccessed
        if (age < componentCacheDuration) {
          chunk.lastAccessed = Date.now()
          chunk.accessCount++
          bundleStats.value.cacheHitRate = calculateCacheHitRate()
          return chunk.component
        }
      }

      // Check if already loading
      if (loadingPromises.value.has(componentId)) {
        return loadingPromises.value.get(componentId)!
      }

      chunk.isLoading = true
      chunk.hasError = false

      const loadingPromise = loadComponentWithRetry(loader, retryAttempts)
        .then((component: Component) => {
          const loadTime = performance.now() - startTime

          // Update chunk info
          chunk.isLoaded = true
          chunk.isLoading = false
          chunk.component = markRaw(component)
          chunk.loadTime = loadTime
          chunk.lastAccessed = Date.now()
          chunk.accessCount++

          // Update bundle stats
          bundleStats.value.loadedChunks++
          bundleStats.value.averageLoadTime = updateAverageLoadTime(loadTime)

          // Clean up loading promise
          loadingPromises.value.delete(componentId)

          // Trigger cache cleanup if needed
          if (componentCache.value.size > maxCacheSize.value) {
            cleanupCache()
          }

          return component
        })
        .catch((error: Error) => {
          chunk.isLoading = false
          chunk.hasError = true
          chunk.error = error

          bundleStats.value.failedChunks++
          loadingPromises.value.delete(componentId)

          throw error
        })

      loadingPromises.value.set(componentId, loadingPromise)
      return loadingPromise
    }

    // Return Vue async component
    return {
      loader: enhancedLoader,
      loadingComponent,
      errorComponent,
      delay,
      timeout,
      suspensible,
      name: `LazyComponent_${componentId}`
    } as unknown as DefineComponent
  }

  // Load component with retry logic and intelligent backoff
  const loadComponentWithRetry = async (
    loader: AsyncComponentLoader,
    maxAttempts: number
  ): Promise<Component> => {
    let lastError: Error

    for (let attempt = 1; attempt <= maxAttempts; attempt++) {
      try {
        // Apply network-based delays
        if (attempt > 1) {
          const delay = calculateRetryDelay(attempt)
          await new Promise(resolve => setTimeout(resolve, delay))
        }

        // Check if we should defer loading based on performance
        if (shouldDeferLoading()) {
          await waitForBetterConditions()
        }

        const component = await loader()

        // Ensure component is properly wrapped
        if (typeof component === 'object' && 'default' in component) {
          return (component as any).default
        }

        return component as Component
      } catch (error) {
        lastError = error as Error
        console.warn(`Component load attempt ${attempt}/${maxAttempts} failed:`, error)

        // If this was the last attempt, throw the error
        if (attempt === maxAttempts) {
          throw lastError
        }
      }
    }

    throw lastError!
  }

  // Calculate retry delay with exponential backoff
  const calculateRetryDelay = (attempt: number): number => {
    const baseDelay = 1000
    const exponentialDelay = baseDelay * Math.pow(2, attempt - 1)
    const jitter = Math.random() * 1000

    // Network-based adjustments
    if (networkInfo.value.effectiveType === '2g' || networkInfo.value.saveData) {
      return exponentialDelay * 2 + jitter
    }
    if (networkInfo.value.effectiveType === '3g') {
      return exponentialDelay * 1.5 + jitter
    }

    return exponentialDelay + jitter
  }

  // Check if we should defer loading based on current conditions
  const shouldDeferLoading = (): boolean => {
    const performanceScore = performance.metrics.value.performanceScore
    const memoryUsage = memoryManager.memoryUsage.value.percentage

    // Defer if performance is poor
    if (performanceScore < 30) return true

    // Defer if memory is high
    if (memoryUsage > 85) return true

    // Defer if on slow network and save-data is enabled
    if (networkInfo.value.saveData && ['2g', 'slow-2g'].includes(networkInfo.value.effectiveType)) {
      return true
    }

    // Defer if too many components are loading
    if (loadingPromises.value.size > 3) return true

    return false
  }

  // Wait for better loading conditions
  const waitForBetterConditions = async (): Promise<void> => {
    const maxWait = 5000 // Maximum 5 seconds
    const checkInterval = 500 // Check every 500ms
    let waited = 0

    while (waited < maxWait && shouldDeferLoading()) {
      await new Promise(resolve => setTimeout(resolve, checkInterval))
      waited += checkInterval
    }
  }

  // Preload component
  const preloadComponent = async (
    loader: AsyncComponentLoader,
    priority: 'low' | 'normal' | 'high' = 'normal'
  ): Promise<void> => {
    const componentId = generateComponentId(loader)
    const chunk = componentCache.value.get(componentId)

    // Skip if already loaded or loading
    if (chunk?.isLoaded || chunk?.isLoading) return

    // Add to preload queue
    if (!preloadQueue.value.some(item => item.id === componentId)) {
      preloadQueue.value.push({ id: componentId, priority })

      // Sort queue by priority
      preloadQueue.value.sort((a, b) => {
        const priorityOrder = { high: 3, normal: 2, low: 1 }
        return priorityOrder[b.priority] - priorityOrder[a.priority]
      })

      processPreloadQueue()
    }
  }

  // Process preload queue
  const processPreloadQueue = async (): Promise<void> => {
    // Don't preload if conditions are poor
    if (shouldDeferLoading()) return

    // Process high priority items immediately
    const highPriorityItems = preloadQueue.value.filter(item => item.priority === 'high')

    for (const item of highPriorityItems) {
      const index = preloadQueue.value.findIndex(queueItem => queueItem.id === item.id)
      if (index !== -1) {
        preloadQueue.value.splice(index, 1)

        try {
          // This would need the actual loader, which we'd need to store
          // For now, we'll just mark the intent to preload
          console.log(`Preloading high priority component: ${item.id}`)
        } catch (error) {
          console.warn(`Preload failed for ${item.id}:`, error)
        }
      }
    }

    // Process normal/low priority items with throttling
    if (preloadQueue.value.length > 0) {
      setTimeout(() => {
        if (preloadQueue.value.length > 0 && !shouldDeferLoading()) {
          const item = preloadQueue.value.shift()!
          console.log(`Preloading component: ${item.id} (${item.priority} priority)`)
          // Process the item...
        }
      }, 1000)
    }
  }

  // Prefetch related components based on user behavior
  const prefetchRelatedComponents = (
    currentComponentId: string,
    relatedComponentLoaders: AsyncComponentLoader[]
  ): void => {
    if (!enableIntelligentPrefetch.value) return

    // Use requestIdleCallback for prefetching
    const prefetch = () => {
      relatedComponentLoaders.forEach(loader => {
        preloadComponent(loader, 'low')
      })
    }

    if ('requestIdleCallback' in window) {
      requestIdleCallback(prefetch, { timeout: 10000 })
    } else {
      setTimeout(prefetch, 0)
    }
  }

  // Generate unique component ID
  const generateComponentId = (loader: AsyncComponentLoader): string => {
    // Use function toString to create a hash-like ID
    const loaderStr = loader.toString()
    let hash = 0
    for (let i = 0; i < loaderStr.length; i++) {
      const char = loaderStr.charCodeAt(i)
      hash = ((hash << 5) - hash) + char
      hash = hash & hash // Convert to 32-bit integer
    }
    return `component_${Math.abs(hash).toString(36)}`
  }

  // Cache management
  const cleanupCache = (): void => {
    if (componentCache.value.size <= maxCacheSize.value) return

    // Get cache entries sorted by usage (LRU eviction)
    const entries = Array.from(componentCache.value.entries())
      .sort(([, a], [, b]) => {
        // Sort by last accessed time and access count
        const aScore = a.lastAccessed + (a.accessCount * 60000) // Bonus for frequent access
        const bScore = b.lastAccessed + (b.accessCount * 60000)
        return aScore - bScore
      })

    // Remove least recently used entries
    const toRemove = entries.slice(0, entries.length - maxCacheSize.value)
    toRemove.forEach(([id, chunk]) => {
      componentCache.value.delete(id)

      // Update stats
      if (chunk.isLoaded) {
        bundleStats.value.loadedChunks--
      }
      bundleStats.value.totalChunks--
    })

    console.log(`Cleaned up ${toRemove.length} cached components`)
  }

  // Bundle analysis
  const analyzeBundleUsage = () => {
    const chunks = Array.from(componentCache.value.values())

    return {
      summary: bundleStats.value,
      mostUsed: chunks
        .filter(chunk => chunk.isLoaded)
        .sort((a, b) => b.accessCount - a.accessCount)
        .slice(0, 10),
      slowestLoading: chunks
        .filter(chunk => chunk.loadTime)
        .sort((a, b) => (b.loadTime || 0) - (a.loadTime || 0))
        .slice(0, 10),
      leastUsed: chunks
        .filter(chunk => chunk.isLoaded && chunk.accessCount > 0)
        .sort((a, b) => a.accessCount - b.accessCount)
        .slice(0, 10)
    }
  }

  // Update average load time
  const updateAverageLoadTime = (newLoadTime: number): number => {
    const currentAvg = bundleStats.value.averageLoadTime
    const loadedCount = bundleStats.value.loadedChunks

    if (loadedCount === 0) return newLoadTime

    return ((currentAvg * (loadedCount - 1)) + newLoadTime) / loadedCount
  }

  // Calculate cache hit rate
  const calculateCacheHitRate = (): number => {
    const totalRequests = Array.from(componentCache.value.values())
      .reduce((sum, chunk) => sum + chunk.accessCount, 0)

    const cacheHits = Array.from(componentCache.value.values())
      .filter(chunk => chunk.isLoaded)
      .reduce((sum, chunk) => sum + Math.max(0, chunk.accessCount - 1), 0)

    return totalRequests > 0 ? (cacheHits / totalRequests) * 100 : 0
  }

  // Clear cache
  const clearCache = (): void => {
    componentCache.value.clear()
    loadingPromises.value.clear()
    preloadQueue.value = []

    bundleStats.value = {
      totalChunks: 0,
      loadedChunks: 0,
      failedChunks: 0,
      totalSize: 0,
      loadedSize: 0,
      averageLoadTime: 0,
      cacheHitRate: 0,
      memoryUsage: 0
    }
  }

  // Route-based code splitting helpers
  const createLazyRoute = (
    path: string,
    componentLoader: AsyncComponentLoader,
    config: LazyComponentConfig = {}
  ) => {
    return {
      path,
      component: createLazyComponent(componentLoader, {
        ...config,
        enablePrefetch: true,
        priority: 'high'
      }),
      meta: {
        lazy: true,
        preload: config.enablePreload
      }
    }
  }

  // Feature-based code splitting
  const createFeatureModule = (
    moduleLoader: () => Promise<any>,
    config: LazyComponentConfig = {}
  ) => {
    const moduleId = generateComponentId(moduleLoader)

    return {
      id: moduleId,
      load: async () => {
        const module = await moduleLoader()

        // Register all components in the module
        if (module.default && typeof module.default === 'object') {
          Object.keys(module.default).forEach(key => {
            const component = module.default[key]
            if (component && typeof component === 'object' && component.render) {
              // This is a Vue component
              preloadComponent(() => Promise.resolve(component), 'normal')
            }
          })
        }

        return module
      },
      config
    }
  }

  // Initialize
  initializeNetworkMonitoring()

  // Periodic cache cleanup
  const cleanupInterval = setInterval(() => {
    // Clean up expired cache entries
    const now = Date.now()
    const expired = Array.from(componentCache.value.entries())
      .filter(([, chunk]) => now - chunk.lastAccessed > cacheDuration.value)

    expired.forEach(([id]) => {
      componentCache.value.delete(id)
    })

    if (expired.length > 0) {
      console.log(`Cleaned up ${expired.length} expired components from cache`)
    }
  }, 5 * 60 * 1000) // Every 5 minutes

  // Cleanup on page unload
  const cleanup = () => {
    clearInterval(cleanupInterval)
    clearCache()
  }

  window.addEventListener('beforeunload', cleanup)

  return {
    // Core functionality
    createLazyComponent,
    preloadComponent,
    prefetchRelatedComponents,

    // Route helpers
    createLazyRoute,
    createFeatureModule,

    // Cache management
    clearCache,
    cleanupCache,

    // Analysis
    analyzeBundleUsage,
    bundleStats: readonly(bundleStats),
    componentCache: readonly(componentCache),

    // Configuration
    maxCacheSize,
    cacheDuration,
    enableIntelligentPrefetch,
    enableBandwidthOptimization,

    // Network info
    networkInfo: readonly(networkInfo),

    // Statistics
    totalCachedComponents: computed(() => componentCache.value.size),
    loadingComponentsCount: computed(() => loadingPromises.value.size),
    preloadQueueLength: computed(() => preloadQueue.value.length),
    cacheHitRate: computed(() => bundleStats.value.cacheHitRate)
  }
}

// Global code splitting manager
let globalCodeSplittingManager: ReturnType<typeof useCodeSplitting> | null = null

export function getGlobalCodeSplittingManager() {
  if (!globalCodeSplittingManager) {
    globalCodeSplittingManager = useCodeSplitting()
  }
  return globalCodeSplittingManager
}

// Utility function for creating optimized lazy components
export function defineLazyComponent(
  loader: AsyncComponentLoader,
  config?: LazyComponentConfig
): DefineComponent {
  const manager = getGlobalCodeSplittingManager()
  return manager.createLazyComponent(loader, config)
}

import { ref, computed, onMounted, onUnmounted, nextTick, readonly } from 'vue'
import { useIntersectionObserver, useElementSize } from '@vueuse/core'
import { getGlobalAnimationPerformance } from './animationPerformanceUtils'

export interface LazyLoadConfig {
  threshold: number
  rootMargin: string
  enablePreload: boolean
  preloadDistance: number
  enableSkeleton: boolean
  skeletonType: 'shimmer' | 'pulse'
  retryAttempts: number
  retryDelay: number
  enableViewportOptimization: boolean
  enableBandwidthOptimization: boolean
  enableBatteryOptimization: boolean
}

export interface LazyLoadItem {
  id: string | number
  isVisible: boolean
  isLoading: boolean
  isLoaded: boolean
  hasError: boolean
  retryCount: number
  priority: 'low' | 'medium' | 'high'
  type: 'image' | 'component' | 'data' | 'media'
}

export interface LazyLoadMetrics {
  totalItems: number
  loadedItems: number
  failedItems: number
  averageLoadTime: number
  bandwidthUsed: number
  memoryUsed: number
  performanceScore: number
}

export function useLazyLoading(config: Partial<LazyLoadConfig> = {}) {
  const {
    threshold = 0.1,
    rootMargin = '100px',
    enablePreload = true,
    preloadDistance = 200,
    enableSkeleton = true,
    skeletonType = 'shimmer',
    retryAttempts = 3,
    retryDelay = 1000,
    enableViewportOptimization = true,
    enableBandwidthOptimization = true,
    enableBatteryOptimization = true
  } = config

  const performance = getGlobalAnimationPerformance()

  // State
  const lazyItems = ref(new Map<string | number, LazyLoadItem>())
  const observedElements = ref(new Map<string | number, HTMLElement>())
  const loadingQueue = ref<Array<{ id: string | number; priority: 'low' | 'medium' | 'high' }>>([])
  const isProcessingQueue = ref(false)

  // Performance metrics
  const metrics = ref<LazyLoadMetrics>({
    totalItems: 0,
    loadedItems: 0,
    failedItems: 0,
    averageLoadTime: 0,
    bandwidthUsed: 0,
    memoryUsed: 0,
    performanceScore: 100
  })

  // Network and device capabilities
  const networkInfo = ref<{
    effectiveType: string
    downlink: number
    rtt: number
    saveData: boolean
  }>({
    effectiveType: 'unknown',
    downlink: 10,
    rtt: 100,
    saveData: false
  })

  const deviceMemory = ref(4) // GB

  // Initialize network monitoring
  const initializeNetworkMonitoring = () => {
    const nav = navigator as any
    if ('connection' in nav) {
      const conn = nav.connection
      networkInfo.value = {
        effectiveType: conn.effectiveType || 'unknown',
        downlink: conn.downlink || 10,
        rtt: conn.rtt || 100,
        saveData: conn.saveData || false
      }

      conn.addEventListener('change', () => {
        networkInfo.value = {
          effectiveType: conn.effectiveType || 'unknown',
          downlink: conn.downlink || 10,
          rtt: conn.rtt || 100,
          saveData: conn.saveData || false
        }
      })
    }

    if ('deviceMemory' in nav) {
      deviceMemory.value = nav.deviceMemory || 4
    }
  }

  // Adaptive thresholds based on performance
  const adaptiveThreshold = computed(() => {
    const performanceScore = performance.metrics.value.performanceScore
    const baseThreshold = threshold

    if (performanceScore < 30) return baseThreshold * 0.5 // More aggressive for low performance
    if (performanceScore < 60) return baseThreshold * 0.8
    if (networkInfo.value.saveData) return baseThreshold * 0.3

    return baseThreshold
  })

  const adaptiveRootMargin = computed(() => {
    const performanceScore = performance.metrics.value.performanceScore
    const baseMargin = parseInt(rootMargin.replace('px', ''))

    if (performanceScore < 30 || networkInfo.value.saveData) {
      return `${Math.max(20, baseMargin * 0.3)}px`
    }
    if (networkInfo.value.effectiveType === 'slow-2g' || networkInfo.value.effectiveType === '2g') {
      return `${Math.max(50, baseMargin * 0.5)}px`
    }

    return rootMargin
  })

  // Create intersection observer
  let intersectionObserver: IntersectionObserver | null = null
  const createObserver = () => {
    if (intersectionObserver) {
      intersectionObserver.disconnect()
    }

    intersectionObserver = new IntersectionObserver(
      (entries) => {
        entries.forEach((entry) => {
          const id = entry.target.getAttribute('data-lazy-id')
          if (!id) return

          const item = lazyItems.value.get(id)
          if (!item) return

          const isIntersecting = entry.isIntersecting
          const intersectionRatio = entry.intersectionRatio

          // Update item visibility
          item.isVisible = isIntersecting

          if (isIntersecting && !item.isLoaded && !item.isLoading) {
            // Add to loading queue with priority
            const priority = determinePriority(entry, item)
            queueItemForLoading(id, priority)
          }

          // Preload nearby items if enabled
          if (enablePreload && isIntersecting && intersectionRatio > 0.5) {
            preloadNearbyItems(id)
          }
        })
      },
      {
        threshold: adaptiveThreshold.value,
        rootMargin: adaptiveRootMargin.value
      }
    )

    return intersectionObserver
  }

  // Determine loading priority
  const determinePriority = (entry: IntersectionObserverEntry, item: LazyLoadItem): 'low' | 'medium' | 'high' => {
    const rect = entry.boundingClientRect
    const viewportHeight = window.innerHeight

    // High priority if near top of viewport
    if (rect.top < viewportHeight * 0.3) return 'high'
    // Medium priority if in middle third
    if (rect.top < viewportHeight * 0.7) return 'medium'
    // Consider item type
    if (item.type === 'image' || item.type === 'media') return 'medium'

    return 'low'
  }

  // Queue management with priority
  const queueItemForLoading = (id: string | number, priority: 'low' | 'medium' | 'high') => {
    // Check if already queued
    if (loadingQueue.value.some(item => item.id === id)) return

    loadingQueue.value.push({ id, priority })

    // Sort by priority: high -> medium -> low
    loadingQueue.value.sort((a, b) => {
      const priorityOrder = { high: 3, medium: 2, low: 1 }
      return priorityOrder[b.priority] - priorityOrder[a.priority]
    })

    processLoadingQueue()
  }

  // Process loading queue with throttling
  const processLoadingQueue = async () => {
    if (isProcessingQueue.value || loadingQueue.value.length === 0) return

    isProcessingQueue.value = true

    // Determine concurrent loading limit based on performance
    const performanceScore = performance.metrics.value.performanceScore
    const concurrentLimit = performanceScore > 70 ? 3 : performanceScore > 40 ? 2 : 1

    // Process items in batches
    while (loadingQueue.value.length > 0) {
      const batch = loadingQueue.value.splice(0, concurrentLimit)
      const loadPromises = batch.map(({ id }) => loadItem(id))

      try {
        await Promise.allSettled(loadPromises)
      } catch (error) {
        console.warn('Batch loading error:', error)
      }

      // Respect performance constraints
      if (performance.shouldThrottleAnimations.value) {
        await new Promise(resolve => setTimeout(resolve, 50))
      }
    }

    isProcessingQueue.value = false
  }

  // Load individual item
  const loadItem = async (id: string | number): Promise<void> => {
    const item = lazyItems.value.get(id)
    if (!item || item.isLoading || item.isLoaded) return

    item.isLoading = true
    const startTime = globalThis.performance.now()

    try {
      await loadItemContent(id, item)

      item.isLoaded = true
      item.hasError = false
      item.retryCount = 0

      // Update metrics
      const loadTime = globalThis.performance.now() - startTime
      updateMetrics('success', loadTime)

    } catch (error) {
      console.warn(`Failed to load item ${id}:`, error)
      item.hasError = true
      item.retryCount++

      // Retry if within limits
      if (item.retryCount < retryAttempts) {
        setTimeout(() => {
          if (item.isVisible) {
            queueItemForLoading(id, 'high')
          }
        }, retryDelay * Math.pow(2, item.retryCount)) // Exponential backoff
      }

      updateMetrics('error')
    } finally {
      item.isLoading = false
    }
  }

  // Load item content based on type
  const loadItemContent = async (id: string | number, item: LazyLoadItem): Promise<void> => {
    switch (item.type) {
      case 'image':
        return loadImage(id)
      case 'component':
        return loadComponent(id)
      case 'data':
        return loadData(id)
      case 'media':
        return loadMedia(id)
      default:
        throw new Error(`Unknown item type: ${item.type}`)
    }
  }

  // Image loading with optimization
  const loadImage = async (id: string | number): Promise<void> => {
    const element = observedElements.value.get(id) as HTMLImageElement
    if (!element) throw new Error('Element not found')

    return new Promise((resolve, reject) => {
      const img = new Image()

      img.onload = () => {
        element.src = img.src
        element.classList.remove('lazy-loading')
        element.classList.add('lazy-loaded')
        resolve()
      }

      img.onerror = () => {
        reject(new Error('Image failed to load'))
      }

      // Get optimized image source based on device and network
      const src = getOptimizedImageSrc(element.dataset.src || '', element)
      img.src = src
    })
  }

  // Component lazy loading
  const loadComponent = async (id: string | number): Promise<void> => {
    const element = observedElements.value.get(id)
    if (!element) throw new Error('Element not found')

    const componentName = element.dataset.component
    if (!componentName) throw new Error('Component name not specified')

    // Dynamic import with retry logic
    try {
      const module = await import(`@/components/${componentName}.vue`)

      // Emit event for parent to handle component loading
      element.dispatchEvent(new CustomEvent('lazy-component-loaded', {
        detail: { id, component: module.default }
      }))
    } catch (error) {
      throw new Error(`Failed to load component: ${componentName}`)
    }
  }

  // Data loading
  const loadData = async (id: string | number): Promise<void> => {
    const element = observedElements.value.get(id)
    if (!element) throw new Error('Element not found')

    const dataUrl = element.dataset.src
    if (!dataUrl) throw new Error('Data URL not specified')

    const response = await fetch(dataUrl, {
      signal: createTimeoutSignal(10000) // 10s timeout
    })

    if (!response.ok) {
      throw new Error(`HTTP ${response.status}: ${response.statusText}`)
    }

    const data = await response.json()

    // Emit event with loaded data
    element.dispatchEvent(new CustomEvent('lazy-data-loaded', {
      detail: { id, data }
    }))
  }

  // Media loading (videos, audio)
  const loadMedia = async (id: string | number): Promise<void> => {
    const element = observedElements.value.get(id) as HTMLVideoElement | HTMLAudioElement
    if (!element) throw new Error('Element not found')

    return new Promise((resolve, reject) => {
      const handleLoad = () => {
        element.classList.remove('lazy-loading')
        element.classList.add('lazy-loaded')
        resolve()
      }

      const handleError = () => {
        reject(new Error('Media failed to load'))
      }

      element.addEventListener('loadeddata', handleLoad, { once: true })
      element.addEventListener('error', handleError, { once: true })

      // Set source and start loading
      const src = element.dataset.src
      if (src) {
        element.src = src
        element.load()
      }
    })
  }

  // Get optimized image source
  const getOptimizedImageSrc = (src: string, element: HTMLImageElement): string => {
    if (!enableBandwidthOptimization) return src

    const width = element.getAttribute('width') || element.clientWidth
    const height = element.getAttribute('height') || element.clientHeight
    const devicePixelRatio = window.devicePixelRatio || 1

    // Adjust quality based on network conditions
    let quality = 80
    if (networkInfo.value.effectiveType === '2g' || networkInfo.value.saveData) {
      quality = 60
    } else if (networkInfo.value.effectiveType === '3g') {
      quality = 70
    }

    // Calculate optimal dimensions
    const optimalWidth = Math.round(Number(width) * devicePixelRatio)
    const optimalHeight = Math.round(Number(height) * devicePixelRatio)

    // If URL supports query parameters for optimization
    if (src.includes('?') || src.includes('&')) {
      return `${src}&w=${optimalWidth}&h=${optimalHeight}&q=${quality}`
    }

    return src
  }

  // Preload nearby items
  const preloadNearbyItems = (currentId: string | number) => {
    if (!enablePreload) return

    const currentElement = observedElements.value.get(currentId)
    if (!currentElement) return

    const rect = currentElement.getBoundingClientRect()

    // Find nearby elements within preload distance
    observedElements.value.forEach((element, id) => {
      if (id === currentId) return

      const item = lazyItems.value.get(id)
      if (!item || item.isLoaded || item.isLoading) return

      const elementRect = element.getBoundingClientRect()
      const distance = Math.abs(elementRect.top - rect.top)

      if (distance < preloadDistance) {
        queueItemForLoading(id, 'low')
      }
    })
  }

  // Register element for lazy loading
  const registerElement = (
    element: HTMLElement,
    id: string | number,
    type: LazyLoadItem['type'] = 'image',
    priority: LazyLoadItem['priority'] = 'medium'
  ) => {
    if (lazyItems.value.has(id)) return

    // Set data attribute for observer
    element.setAttribute('data-lazy-id', String(id))

    // Create lazy item
    const item: LazyLoadItem = {
      id,
      isVisible: false,
      isLoading: false,
      isLoaded: false,
      hasError: false,
      retryCount: 0,
      priority,
      type
    }

    lazyItems.value.set(id, item)
    observedElements.value.set(id, element)

    // Add skeleton/loading state
    if (enableSkeleton && type === 'image') {
      element.classList.add('lazy-loading', `lazy-skeleton-${skeletonType}`)
    }

    // Start observing
    if (intersectionObserver) {
      intersectionObserver.observe(element)
    }

    metrics.value.totalItems++
  }

  // Unregister element
  const unregisterElement = (id: string | number) => {
    const element = observedElements.value.get(id)
    if (element && intersectionObserver) {
      intersectionObserver.unobserve(element)
    }

    lazyItems.value.delete(id)
    observedElements.value.delete(id)

    // Remove from queue if pending
    const queueIndex = loadingQueue.value.findIndex(item => item.id === id)
    if (queueIndex !== -1) {
      loadingQueue.value.splice(queueIndex, 1)
    }

    metrics.value.totalItems = Math.max(0, metrics.value.totalItems - 1)
  }

  // Update performance metrics
  const updateMetrics = (type: 'success' | 'error', loadTime?: number) => {
    if (type === 'success') {
      metrics.value.loadedItems++
      if (loadTime) {
        const currentAvg = metrics.value.averageLoadTime
        const totalLoaded = metrics.value.loadedItems
        metrics.value.averageLoadTime = ((currentAvg * (totalLoaded - 1)) + loadTime) / totalLoaded
      }
    } else {
      metrics.value.failedItems++
    }

    // Calculate performance score
    const totalAttempts = metrics.value.loadedItems + metrics.value.failedItems
    if (totalAttempts > 0) {
      const successRate = metrics.value.loadedItems / totalAttempts
      const avgLoadScore = Math.max(0, 100 - (metrics.value.averageLoadTime / 10))
      metrics.value.performanceScore = (successRate * 70) + (avgLoadScore * 0.3)
    }
  }

  // Utility functions
  const createTimeoutSignal = (timeout: number): AbortSignal => {
    const controller = new AbortController()
    setTimeout(() => controller.abort(), timeout)
    return controller.signal
  }

  // Force load an item
  const forceLoadItem = async (id: string | number) => {
    const item = lazyItems.value.get(id)
    if (!item) return

    await loadItem(id)
  }

  // Clear all items
  const clearAllItems = () => {
    observedElements.value.forEach((_, id) => {
      unregisterElement(id)
    })
    loadingQueue.value = []
    isProcessingQueue.value = false
  }

  // Get loading statistics
  const getStatistics = () => {
    const total = metrics.value.totalItems
    const loaded = metrics.value.loadedItems
    const failed = metrics.value.failedItems
    const pending = total - loaded - failed

    return {
      total,
      loaded,
      failed,
      pending,
      loadRate: total > 0 ? (loaded / total) * 100 : 0,
      errorRate: total > 0 ? (failed / total) * 100 : 0,
      averageLoadTime: metrics.value.averageLoadTime,
      performanceScore: metrics.value.performanceScore
    }
  }

  // Lifecycle
  onMounted(() => {
    initializeNetworkMonitoring()
    createObserver()
  })

  onUnmounted(() => {
    if (intersectionObserver) {
      intersectionObserver.disconnect()
    }
    clearAllItems()
  })

  return {
    // Registration
    registerElement,
    unregisterElement,

    // Control
    forceLoadItem,
    clearAllItems,

    // State
    lazyItems: readonly(lazyItems),
    metrics: readonly(metrics),
    networkInfo: readonly(networkInfo),

    // Statistics
    getStatistics,

    // Configuration
    adaptiveThreshold,
    adaptiveRootMargin,

    // Queue info
    queueLength: computed(() => loadingQueue.value.length),
    isProcessing: readonly(isProcessingQueue)
  }
}

// Utility composable for easy image lazy loading
export function useLazyImage() {
  const lazyLoading = useLazyLoading({
    enableSkeleton: true,
    skeletonType: 'shimmer',
    enableBandwidthOptimization: true
  })

  const registerImage = (
    img: HTMLImageElement,
    id: string | number,
    priority: LazyLoadItem['priority'] = 'medium'
  ) => {
    lazyLoading.registerElement(img, id, 'image', priority)
  }

  return {
    ...lazyLoading,
    registerImage
  }
}

// Utility composable for component lazy loading
export function useLazyComponent() {
  const lazyLoading = useLazyLoading({
    enableSkeleton: false,
    enablePreload: false
  })

  const registerComponent = (
    element: HTMLElement,
    id: string | number,
    componentName: string,
    priority: LazyLoadItem['priority'] = 'high'
  ) => {
    element.dataset.component = componentName
    lazyLoading.registerElement(element, id, 'component', priority)
  }

  return {
    ...lazyLoading,
    registerComponent
  }
}

import { ref, computed, watch, nextTick, onMounted, onUnmounted, readonly, type Ref } from 'vue'
import { useElementSize, useScroll, useRafFn, useResizeObserver } from '@vueuse/core'
import { getGlobalAnimationPerformance } from './animationPerformanceUtils'

export interface VirtualScrollItem {
  id: string | number
  data: any
  height?: number
  isVisible?: boolean
}

export interface VirtualScrollConfig {
  itemHeight: number
  bufferSize: number
  overscan: number
  enableDynamicHeight: boolean
  enableScrollToIndex: boolean
  enableSmoothScrolling: boolean
  enableKeyboardNavigation: boolean
  estimatedItemHeight?: number
  minItemHeight?: number
  maxItemHeight?: number
}

export interface VirtualScrollState {
  scrollTop: number
  containerHeight: number
  totalHeight: number
  visibleStartIndex: number
  visibleEndIndex: number
  renderedStartIndex: number
  renderedEndIndex: number
  isScrolling: boolean
  scrollDirection: 'up' | 'down'
}

export function useVirtualScrolling<T = any>(
  items: Ref<VirtualScrollItem[]>,
  containerRef: Ref<HTMLElement | null>,
  config: Partial<VirtualScrollConfig> = {}
) {
  const {
    itemHeight = 50,
    bufferSize = 5,
    overscan = 3,
    enableDynamicHeight = false,
    enableScrollToIndex = true,
    enableSmoothScrolling = true,
    enableKeyboardNavigation = true,
    estimatedItemHeight = itemHeight,
    minItemHeight = 20,
    maxItemHeight = 200
  } = config

  const performance = getGlobalAnimationPerformance()

  // Refs
  const scrollElementRef = ref<HTMLElement | null>(null)
  const spacerTopRef = ref<HTMLElement | null>(null)
  const spacerBottomRef = ref<HTMLElement | null>(null)

  // State
  const state = ref<VirtualScrollState>({
    scrollTop: 0,
    containerHeight: 0,
    totalHeight: 0,
    visibleStartIndex: 0,
    visibleEndIndex: 0,
    renderedStartIndex: 0,
    renderedEndIndex: 0,
    isScrolling: false,
    scrollDirection: 'down'
  })

  // Cache for measured heights (when dynamic height is enabled)
  const heightCache = ref(new Map<string | number, number>())
  const heightMeasurementCache = ref(new Map<string | number, HTMLElement>())

  // Performance tracking
  const scrollThrottle = ref(false)
  const lastScrollTime = ref(0)
  const scrollVelocity = ref(0)
  const measurementQueue = ref<Array<{ id: string | number; element: HTMLElement }>>([])

  // Container size tracking
  const { height: containerHeight } = useElementSize(containerRef)
  const { y: scrollTop, isScrolling } = useScroll(scrollElementRef, {
    throttle: performance.shouldThrottleAnimations.value ? 16 : 8
  })

  // Calculate item positions and heights
  const itemPositions = computed(() => {
    const positions = new Map<string | number, { top: number; height: number }>()
    let currentTop = 0

    items.value.forEach((item: VirtualScrollItem) => {
      const id = item.id
      let height = itemHeight

      if (enableDynamicHeight) {
        height = heightCache.value.get(id) || estimatedItemHeight
        height = Math.max(minItemHeight!, Math.min(maxItemHeight!, height))
      } else if (item.height) {
        height = item.height
      }

      positions.set(id, { top: currentTop, height })
      currentTop += height
    })

    return positions
  })

  // Calculate total height
  const totalHeight = computed(() => {
    if (items.value.length === 0) return 0

    if (enableDynamicHeight) {
      let total = 0
      items.value.forEach((item: VirtualScrollItem) => {
        const cached = heightCache.value.get(item.id)
        total += cached || estimatedItemHeight
      })
      return total
    }

    return items.value.length * itemHeight
  })

  // Calculate visible range
  const visibleRange = computed(() => {
    const scrollTopValue = scrollTop.value
    const containerHeightValue = containerHeight.value || state.value.containerHeight

    if (containerHeightValue === 0 || items.value.length === 0) {
      return { startIndex: 0, endIndex: 0 }
    }

    let startIndex = 0
    let endIndex = 0

    if (enableDynamicHeight) {
      // Binary search for start index
      let left = 0
      let right = items.value.length - 1

      while (left <= right) {
        const mid = Math.floor((left + right) / 2)
        const item = items.value[mid]
        const position = itemPositions.value.get(item.id)

        if (!position) {
          left = mid + 1
          continue
        }

        if (position.top <= scrollTopValue) {
          startIndex = mid
          left = mid + 1
        } else {
          right = mid - 1
        }
      }

      // Find end index
      let currentTop = itemPositions.value.get(items.value[startIndex]?.id)?.top || 0
      endIndex = startIndex

      while (endIndex < items.value.length && currentTop < scrollTopValue + containerHeightValue) {
        const position = itemPositions.value.get(items.value[endIndex]?.id)
        if (position) {
          currentTop = position.top + position.height
        }
        endIndex++
      }
    } else {
      // Fixed height calculation (faster)
      startIndex = Math.floor(scrollTopValue / itemHeight)
      endIndex = Math.min(
        items.value.length - 1,
        Math.ceil((scrollTopValue + containerHeightValue) / itemHeight)
      )
    }

    return {
      startIndex: Math.max(0, startIndex),
      endIndex: Math.min(items.value.length - 1, endIndex)
    }
  })

  // Calculate rendered range with buffer
  const renderedRange = computed(() => {
    const { startIndex, endIndex } = visibleRange.value
    const bufferedStart = Math.max(0, startIndex - bufferSize - overscan)
    const bufferedEnd = Math.min(items.value.length - 1, endIndex + bufferSize + overscan)

    return {
      startIndex: bufferedStart,
      endIndex: bufferedEnd
    }
  })

  // Get visible items
  const visibleItems = computed(() => {
    const { startIndex, endIndex } = renderedRange.value
    return items.value.slice(startIndex, endIndex + 1).map((item: VirtualScrollItem, index: number) => ({
      ...item,
      index: startIndex + index,
      style: getItemStyle(startIndex + index)
    }))
  })

  // Get spacer heights
  const spacerTopHeight = computed(() => {
    const { startIndex } = renderedRange.value
    if (startIndex === 0) return 0

    if (enableDynamicHeight) {
      let height = 0
      for (let i = 0; i < startIndex; i++) {
        const position = itemPositions.value.get(items.value[i]?.id)
        if (position) {
          height += position.height
        }
      }
      return height
    }

    return startIndex * itemHeight
  })

  const spacerBottomHeight = computed(() => {
    const { endIndex } = renderedRange.value
    const remaining = items.value.length - endIndex - 1

    if (remaining <= 0) return 0

    if (enableDynamicHeight) {
      let height = 0
      for (let i = endIndex + 1; i < items.value.length; i++) {
        const position = itemPositions.value.get(items.value[i]?.id)
        if (position) {
          height += position.height
        }
      }
      return height
    }

    return remaining * itemHeight
  })

  // Get item style
  const getItemStyle = (index: number) => {
    if (!enableDynamicHeight) {
      return {
        height: `${itemHeight}px`,
        minHeight: `${itemHeight}px`,
        position: 'relative' as const
      }
    }

    const item = items.value[index]
    if (!item) return {}

    const position = itemPositions.value.get(item.id)
    const height = position?.height || estimatedItemHeight

    return {
      height: `${height}px`,
      minHeight: `${minItemHeight}px`,
      maxHeight: `${maxItemHeight}px`,
      position: 'relative' as const
    }
  }

  // Measure item height (for dynamic height)
  const measureItemHeight = (id: string | number, element: HTMLElement) => {
    if (!enableDynamicHeight) return

    // Throttle measurements for performance
    if (performance.shouldThrottleAnimations.value) {
      measurementQueue.value.push({ id, element })
      processMeasurementQueue()
      return
    }

    const rect = element.getBoundingClientRect()
    const height = rect.height

    if (height > 0 && height !== heightCache.value.get(id)) {
      heightCache.value.set(id, height)
      heightMeasurementCache.value.set(id, element)
    }
  }

  // Process measurement queue with RAF
  const { pause: pauseMeasurements, resume: resumeMeasurements } = useRafFn(() => {
    if (measurementQueue.value.length === 0) {
      pauseMeasurements()
      return
    }

    const batch = measurementQueue.value.splice(0, 3) // Process 3 items per frame
    batch.forEach(({ id, element }) => {
      const rect = element.getBoundingClientRect()
      if (rect.height > 0) {
        heightCache.value.set(id, rect.height)
        heightMeasurementCache.value.set(id, element)
      }
    })
  }, { immediate: false })

  const processMeasurementQueue = () => {
    if (measurementQueue.value.length > 0) {
      resumeMeasurements()
    }
  }

  // Scroll to index
  const scrollToIndex = (index: number, alignment: 'start' | 'center' | 'end' | 'auto' = 'auto') => {
    if (!enableScrollToIndex || !scrollElementRef.value) return

    const item = items.value[index]
    if (!item) return

    const containerHeightValue = containerHeight.value || state.value.containerHeight
    const position = itemPositions.value.get(item.id)

    if (!position) return

    let targetScrollTop = position.top

    switch (alignment) {
      case 'center':
        targetScrollTop = position.top - (containerHeightValue - position.height) / 2
        break
      case 'end':
        targetScrollTop = position.top - containerHeightValue + position.height
        break
      case 'auto':
        const currentScroll = scrollTop.value
        const itemBottom = position.top + position.height

        if (position.top < currentScroll) {
          // Item is above viewport
          targetScrollTop = position.top
        } else if (itemBottom > currentScroll + containerHeightValue) {
          // Item is below viewport
          targetScrollTop = itemBottom - containerHeightValue
        } else {
          // Item is already visible
          return
        }
        break
    }

    targetScrollTop = Math.max(0, Math.min(targetScrollTop, totalHeight.value - containerHeightValue))

    if (enableSmoothScrolling && !performance.shouldThrottleAnimations.value) {
      scrollElementRef.value.scrollTo({
        top: targetScrollTop,
        behavior: 'smooth'
      })
    } else {
      scrollElementRef.value.scrollTop = targetScrollTop
    }
  }

  // Scroll to item by id
  const scrollToItem = (id: string | number, alignment?: 'start' | 'center' | 'end' | 'auto') => {
    const index = items.value.findIndex((item: VirtualScrollItem) => item.id === id)
    if (index !== -1) {
      scrollToIndex(index, alignment)
    }
  }

  // Get item at position
  const getItemAtPosition = (y: number) => {
    if (enableDynamicHeight) {
      for (const [id, position] of itemPositions.value) {
        if (y >= position.top && y < position.top + position.height) {
          const index = items.value.findIndex((item: VirtualScrollItem) => item.id === id)
          return index !== -1 ? { item: items.value[index], index } : null
        }
      }
    } else {
      const index = Math.floor(y / itemHeight)
      if (index >= 0 && index < items.value.length) {
        return { item: items.value[index], index }
      }
    }
    return null
  }

  // Keyboard navigation
  const currentFocusIndex = ref(-1)

  const navigateUp = () => {
    if (!enableKeyboardNavigation) return
    const newIndex = Math.max(0, currentFocusIndex.value - 1)
    currentFocusIndex.value = newIndex
    scrollToIndex(newIndex, 'auto')
  }

  const navigateDown = () => {
    if (!enableKeyboardNavigation) return
    const newIndex = Math.min(items.value.length - 1, currentFocusIndex.value + 1)
    currentFocusIndex.value = newIndex
    scrollToIndex(newIndex, 'auto')
  }

  const navigatePageUp = () => {
    if (!enableKeyboardNavigation) return
    const pageSize = Math.floor((containerHeight.value || 300) / itemHeight)
    const newIndex = Math.max(0, currentFocusIndex.value - pageSize)
    currentFocusIndex.value = newIndex
    scrollToIndex(newIndex, 'start')
  }

  const navigatePageDown = () => {
    if (!enableKeyboardNavigation) return
    const pageSize = Math.floor((containerHeight.value || 300) / itemHeight)
    const newIndex = Math.min(items.value.length - 1, currentFocusIndex.value + pageSize)
    currentFocusIndex.value = newIndex
    scrollToIndex(newIndex, 'start')
  }

  // Update state
  const updateState = () => {
    const { startIndex, endIndex } = visibleRange.value
    const { startIndex: renderedStart, endIndex: renderedEnd } = renderedRange.value

    const prevScrollTop = state.value.scrollTop
    const currentScrollTop = scrollTop.value

    state.value = {
      scrollTop: currentScrollTop,
      containerHeight: containerHeight.value || state.value.containerHeight,
      totalHeight: totalHeight.value,
      visibleStartIndex: startIndex,
      visibleEndIndex: endIndex,
      renderedStartIndex: renderedStart,
      renderedEndIndex: renderedEnd,
      isScrolling: isScrolling.value,
      scrollDirection: currentScrollTop > prevScrollTop ? 'down' : 'up'
    }

    // Calculate scroll velocity
    const now = globalThis.performance.now()
    const deltaTime = now - lastScrollTime.value
    const deltaScroll = Math.abs(currentScrollTop - prevScrollTop)

    if (deltaTime > 0) {
      scrollVelocity.value = deltaScroll / deltaTime
    }

    lastScrollTime.value = now
  }

  // Setup resize observer for dynamic height recalculation
  if (enableDynamicHeight) {
    useResizeObserver(containerRef, () => {
      // Invalidate height cache on container resize
      if (containerHeight.value !== state.value.containerHeight) {
        nextTick(() => {
          heightCache.value.clear()
          heightMeasurementCache.value.clear()
        })
      }
    })
  }

  // Watch for changes
  watch([scrollTop, containerHeight, items], updateState, { immediate: true })
  watch(isScrolling, (scrolling) => {
    if (!scrolling) {
      // Scroll ended, process any queued measurements
      processMeasurementQueue()
    }
  })

  // Initialize
  onMounted(() => {
    if (containerRef.value) {
      scrollElementRef.value = containerRef.value.querySelector('.virtual-scroll-container') || containerRef.value
    }
    nextTick(updateState)
  })

  // Cleanup
  onUnmounted(() => {
    pauseMeasurements()
    heightCache.value.clear()
    heightMeasurementCache.value.clear()
    measurementQueue.value = []
  })

  // Performance monitoring integration
  const getPerformanceMetrics = () => {
    return {
      totalItems: items.value.length,
      visibleItems: visibleItems.value.length,
      renderedItems: renderedRange.value.endIndex - renderedRange.value.startIndex + 1,
      heightCacheSize: heightCache.value.size,
      measurementQueueSize: measurementQueue.value.length,
      scrollVelocity: scrollVelocity.value,
      averageItemHeight: enableDynamicHeight
        ? Array.from(heightCache.value.values()).reduce((sum, h) => sum + h, 0) / heightCache.value.size
        : itemHeight
    }
  }

  return {
    // State
    state: readonly(state),
    visibleItems,
    currentFocusIndex,

    // Refs for template
    scrollElementRef,
    spacerTopRef,
    spacerBottomRef,

    // Computed properties
    totalHeight,
    spacerTopHeight,
    spacerBottomHeight,

    // Methods
    scrollToIndex,
    scrollToItem,
    getItemAtPosition,
    measureItemHeight,
    getItemStyle,

    // Navigation
    navigateUp,
    navigateDown,
    navigatePageUp,
    navigatePageDown,

    // Performance
    getPerformanceMetrics,

    // Utilities
    updateState
  }
}

// Helper function for creating virtual scroll container
export function createVirtualScrollContainer() {
  return {
    class: 'virtual-scroll-container',
    style: {
      height: '100%',
      overflow: 'auto',
      position: 'relative'
    }
  }
}

// Helper function for creating virtual scroll spacers
export function createVirtualScrollSpacer(height: number, position: 'top' | 'bottom') {
  return {
    key: `spacer-${position}`,
    style: {
      height: `${height}px`,
      flexShrink: 0,
      pointerEvents: 'none' as const
    }
  }
}

