import { computed, reactive, ref, watch, unref, onUnmounted, nextTick, getCurrentInstance, isRef, type Ref, type ComputedRef, type CSSProperties } from 'vue'
import {
  useElementVisibility,
  useDocumentVisibility,
  usePreferredReducedMotion,
  useRafFn,
  useEventBus,
  useMounted,
  useBattery,
  useFps,
  useIdle,
  usePageLeave,
  useAnimate,
  useCycleList,
  useScroll,
  toValue,
  type EventBusKey,
  type MaybeRefOrGetter,
  type UseAnimateKeyframes,
  type UseAnimateOptions,
} from '@vueuse/core'

// Import existing presets and constants
import { ANIMATION_DURATION, EASING, FILL_MODE } from '@/constants/animations'

// Export preset system for convenient animation configuration
export { preset, presets } from './useAnimationPresets'

export interface AnimationPreset {
  keyframes: UseAnimateKeyframes
  options: number | UseAnimateOptions
}

export const animationPresets = {
  slideUp: {
    keyframes: [
      { opacity: 0, transform: 'translateY(20px)' },
      { opacity: 1, transform: 'translateY(0px)' }
    ],
    options: {
      duration: ANIMATION_DURATION.NORMAL,
      easing: EASING.BOUNCE,
      fill: FILL_MODE.FORWARDS
    }
  } as AnimationPreset,

  slideDown: {
    keyframes: [
      { opacity: 0, transform: 'translateY(-20px)' },
      { opacity: 1, transform: 'translateY(0px)' }
    ],
    options: {
      duration: ANIMATION_DURATION.NORMAL,
      easing: EASING.BOUNCE,
      fill: FILL_MODE.FORWARDS
    }
  } as AnimationPreset,

  fadeIn: {
    keyframes: [
      { opacity: 0 },
      { opacity: 1 }
    ],
    options: {
      duration: ANIMATION_DURATION.FAST,
      easing: EASING.SMOOTH,
      fill: FILL_MODE.FORWARDS
    }
  } as AnimationPreset,

  fadeOut: {
    keyframes: [
      { opacity: 1 },
      { opacity: 0 }
    ],
    options: {
      duration: ANIMATION_DURATION.FAST,
      easing: EASING.SMOOTH,
      fill: FILL_MODE.FORWARDS
    }
  } as AnimationPreset,

  scaleUp: {
    keyframes: [
      { opacity: 0, transform: 'scale(0.8)' },
      { opacity: 1, transform: 'scale(1.05)' },
      { opacity: 1, transform: 'scale(1)' }
    ],
    options: {
      duration: ANIMATION_DURATION.NORMAL,
      easing: EASING.BOUNCE,
      fill: FILL_MODE.FORWARDS
    }
  } as AnimationPreset,

  scaleDown: {
