/* Landing page typography utilities */

:root {
  --font-size-display-2xl: clamp(3rem, 5vw, 4.5rem);  /* 48-72px hero */
  --font-size-display-xl: clamp(2.5rem, 4vw, 3.5rem); /* Section headings */
  --font-size-heading-card: clamp(1.875rem, 3vw, 2.25rem);
}

.heading-hero {
  font-size: var(--font-size-display-2xl);
  font-weight: 800;
  line-height: 1.1;
  letter-spacing: -0.025em;
  color: rgb(17 24 39); /* gray-900 */
}

html[data-theme="dark"] .heading-hero {
  color: rgb(255 255 255); /* white */
}

.heading-section {
  font-size: var(--font-size-display-xl);
  font-weight: 700;
  line-height: 1.1;
  letter-spacing: -0.025em;
  color: rgb(17 24 39); /* gray-900 */
}

html[data-theme="dark"] .heading-section {
  color: rgb(255 255 255); /* white */
}

.heading-card {
  font-size: var(--font-size-heading-card);
  font-weight: 600;
  line-height: 1.25;
  color: rgb(17 24 39); /* gray-900 */
}

html[data-theme="dark"] .heading-card {
  color: rgb(255 255 255); /* white */
}

.body-hero {
  font-size: 1.25rem;
  line-height: 1.75;
  color: rgb(75 85 99); /* gray-600 */
}

@media (min-width: 1024px) {
  .body-hero {
    font-size: 1.5rem;
  }
}

html[data-theme="dark"] .body-hero {
  color: rgb(209 213 219); /* gray-300 */
}

.body-large {
  font-size: 1.125rem;
  line-height: 1.75;
  color: rgb(75 85 99); /* gray-600 */
}

html[data-theme="dark"] .body-large {
  color: rgb(209 213 219); /* gray-300 */
}

.gradient-text-primary {
  background: linear-gradient(135deg, #007c89 0%, #7c3aed 100%);
  -webkit-background-clip: text;
  -webkit-text-fill-color: transparent;
  background-clip: text;
}

.gradient-text-light {
  background: linear-gradient(135deg, rgb(17 24 39) 0%, rgb(75 85 99) 100%); /* gray-900 to gray-600 for light mode */
  -webkit-background-clip: text;
  -webkit-text-fill-color: transparent;
  background-clip: text;
}

html[data-theme="dark"] .gradient-text-light {
  background: linear-gradient(135deg, white 0%, #d1d5db 100%);
  -webkit-background-clip: text;
  -webkit-text-fill-color: transparent;
  background-clip: text;
}

