    84â†’  --color-gray-700: #374151;
    85â†’  --color-gray-800: #1f2937;
    86â†’  --color-gray-900: #111827;
    87â†’  --color-gray-950: #030712;
    88â†’
    89â†’  /* Surface Colors */
    90â†’  --color-surface-primary: #ffffff;
    91â†’  --color-surface-secondary: var(--color-gray-50);
    92â†’  --color-surface-tertiary: var(--color-gray-100);
    93â†’  --color-surface-inverse: var(--color-gray-900);
    94â†’
    95â†’  /* Border Colors */
    96â†’  --color-border-primary: var(--color-gray-200);
    97â†’  --color-border-secondary: var(--color-gray-300);
    98â†’  --color-border-focus: var(--color-primary-500);
    99â†’  --color-border-error: var(--color-error-500);
   100â†’  --color-border-success: var(--color-success-500);
   101â†’
   102â†’  /* Text Colors */
   103â†’  --color-text-primary: var(--color-gray-900);
   104â†’  --color-text-secondary: var(--color-gray-600);
   105â†’  --color-text-tertiary: var(--color-gray-500);
   106â†’  --color-text-inverse: #ffffff;
   107â†’  --color-text-placeholder: var(--color-gray-400);
   108â†’
   109â†’  /* ===== DARK MODE COLORS ===== */
   110â†’  --color-dark-surface-primary: var(--color-gray-800);
   111â†’  --color-dark-surface-secondary: var(--color-gray-900);
   112â†’  --color-dark-surface-tertiary: var(--color-gray-950);
   113â†’  --color-dark-surface-inverse: #ffffff;
   114â†’
   115â†’  --color-dark-border-primary: var(--color-gray-700);
   116â†’  --color-dark-border-secondary: var(--color-gray-600);
   117â†’  --color-dark-border-focus: var(--color-primary-400);
   118â†’
   119â†’  --color-dark-text-primary: var(--color-gray-100);
   120â†’  --color-dark-text-secondary: var(--color-gray-300);
   121â†’  --color-dark-text-tertiary: var(--color-gray-400);
   122â†’  --color-dark-text-placeholder: var(--color-gray-500);
   123â†’
   124â†’  /* ===== SPACING SYSTEM ===== */
   125â†’  --spacing-0: 0;
   126â†’  --spacing-px: 1px;
   127â†’  --spacing-0_5: 0.125rem;  /* 2px */
   128â†’  --spacing-1: 0.25rem;     /* 4px */
   129â†’  --spacing-1_5: 0.375rem;  /* 6px */
   130â†’  --spacing-2: 0.5rem;      /* 8px */
   131â†’  --spacing-2_5: 0.625rem;  /* 10px */
   132â†’  --spacing-3: 0.75rem;     /* 12px */
   133â†’  --spacing-3_5: 0.875rem;  /* 14px */
   134â†’  --spacing-4: 1rem;        /* 16px */
   135â†’  --spacing-5: 1.25rem;     /* 20px */
   136â†’  --spacing-6: 1.5rem;      /* 24px */
   137â†’  --spacing-7: 1.75rem;     /* 28px */
   138â†’  --spacing-8: 2rem;        /* 32px */
   139â†’  --spacing-9: 2.25rem;     /* 36px */
   140â†’  --spacing-10: 2.5rem;     /* 40px */
   141â†’  --spacing-11: 2.75rem;    /* 44px */
   142â†’  --spacing-12: 3rem;       /* 48px */
   143â†’  --spacing-14: 3.5rem;     /* 56px */
   144â†’  --spacing-16: 4rem;       /* 64px */
   145â†’  --spacing-20: 5rem;       /* 80px */
   146â†’  --spacing-24: 6rem;       /* 96px */
   147â†’  --spacing-28: 7rem;       /* 112px */
   148â†’  --spacing-32: 8rem;       /* 128px */
   149â†’
   150â†’  /* ===== BORDER RADIUS ===== */
   151â†’  --radius-none: 0;
   152â†’  --radius-sm: 0.125rem;    /* 2px */
   153â†’  --radius-base: 0.25rem;   /* 4px */
   154â†’  --radius-md: 0.375rem;    /* 6px */
   155â†’  --radius-lg: 0.5rem;      /* 8px */
   156â†’  --radius-xl: 0.75rem;     /* 12px */
   157â†’  --radius-2xl: 1rem;       /* 16px */
   158â†’  --radius-3xl: 1.5rem;     /* 24px */
   159â†’  --radius-full: 9999px;
   160â†’
   161â†’  /* ===== SHADOWS ===== */
   162â†’  --shadow-sm: 0 1px 2px 0 rgb(0 0 0 / 0.05);
   163â†’  --shadow-base: 0 1px 3px 0 rgb(0 0 0 / 0.1), 0 1px 2px -1px rgb(0 0 0 / 0.1);
   164â†’  --shadow-md: 0 4px 6px -1px rgb(0 0 0 / 0.1), 0 2px 4px -2px rgb(0 0 0 / 0.1);
   165â†’  --shadow-lg: 0 10px 15px -3px rgb(0 0 0 / 0.1), 0 4px 6px -4px rgb(0 0 0 / 0.1);
   166â†’  --shadow-xl: 0 20px 25px -5px rgb(0 0 0 / 0.1), 0 8px 10px -6px rgb(0 0 0 / 0.1);
   167â†’  --shadow-2xl: 0 25px 50px -12px rgb(0 0 0 / 0.25);
   168â†’  --shadow-inner: inset 0 2px 4px 0 rgb(0 0 0 / 0.05);
   169â†’
   170â†’  /* Focus Shadows */
   171â†’  --shadow-focus-primary: 0 0 0 3px rgb(0 124 137 / 0.1);
   172â†’  --shadow-focus-error: 0 0 0 3px rgb(239 68 68 / 0.1);
   173â†’  --shadow-focus-success: 0 0 0 3px rgb(16 185 129 / 0.1);
   174â†’
   175â†’  /* ===== TRANSITIONS ===== */
   176â†’  --transition-fast: 150ms ease;
   177â†’  --transition-base: 200ms ease;
   178â†’  --transition-medium: 300ms ease;
   179â†’  --transition-slow: 500ms ease;
   180â†’
   181â†’  /* Easing Functions */
   182â†’  --ease-linear: linear;
   183â†’  --ease-in: cubic-bezier(0.4, 0, 1, 1);
   184â†’  --ease-out: cubic-bezier(0, 0, 0.2, 1);
   185â†’  --ease-in-out: cubic-bezier(0.4, 0, 0.2, 1);
