  1017â†’  -webkit-backdrop-filter: none !important;
  1018â†’  background: var(--color-surface-primary) !important;
  1019â†’}
  1020â†’
  1021â†’/* Reduced animations for accessibility and battery */
  1022â†’.reduce-animations * {
  1023â†’  animation-duration: 0.01ms !important;
  1024â†’  animation-iteration-count: 1 !important;
  1025â†’  transition-duration: 0.01ms !important;
  1026â†’}
  1027â†’
  1028â†’.reduce-animations .animate-pulse,
  1029â†’.reduce-animations .animate-bounce,
  1030â†’.reduce-animations .animate-spin {
  1031â†’  animation: none !important;
  1032â†’}
  1033â†’
  1034â†’/* Mobile-specific breakpoints */
  1035â†’@media (max-width: 480px) {
  1036â†’  /* Extra small mobile phones */
  1037â†’  .responsive-mobile-xs {
  1038â†’    font-size: 14px;
  1039â†’    padding: var(--spacing-2);
  1040â†’  }
  1041â†’  
  1042â†’  .btn-base {
  1043â†’    font-size: var(--font-size-sm);
  1044â†’    padding: var(--spacing-2) var(--spacing-3);
  1045â†’  }
  1046â†’  
  1047â†’  .modal-backdrop {
  1048â†’    backdrop-filter: none;
  1049â†’    -webkit-backdrop-filter: none;
  1050â†’  }
  1051â†’}
  1052â†’
  1053â†’@media (max-width: 640px) {
  1054â†’  /* Small mobile devices */
  1055â†’  .responsive-mobile-sm {
  1056â†’    padding: var(--spacing-3);
  1057â†’  }
  1058â†’  
  1059â†’  .card-base {
  1060â†’    border-radius: var(--radius-lg);
  1061â†’    margin: var(--spacing-2);
  1062â†’  }
  1063â†’  
  1064â†’  .glass-medium,
  1065â†’  .glass-strong {
  1066â†’    backdrop-filter: blur(8px) !important;
  1067â†’    -webkit-backdrop-filter: blur(8px) !important;
  1068â†’  }
  1069â†’}
  1070â†’
  1071â†’/* Landscape mobile orientation */
  1072â†’@media (max-height: 500px) and (orientation: landscape) {
  1073â†’  .landscape-optimize {
  1074â†’    height: 100vh;
  1075â†’    overflow-y: auto;
  1076â†’  }
  1077â†’  
  1078â†’  .notifications-container {
  1079â†’    top: var(--spacing-2);
  1080â†’    right: var(--spacing-2);
  1081â†’  }
  1082â†’  
  1083â†’  .chat-widget-container {
  1084â†’    bottom: var(--spacing-2);
  1085â†’    right: var(--spacing-2);
  1086â†’  }
  1087â†’}
  1088â†’
  1089â†’/* High DPI displays (retina) */
  1090â†’@media (-webkit-min-device-pixel-ratio: 2), (min-resolution: 2dppx) {
  1091â†’  .retina-optimize {
  1092â†’    image-rendering: -webkit-optimize-contrast;
  1093â†’    image-rendering: crisp-edges;
  1094â†’  }
  1095â†’  
  1096â†’  .retina-optimize img {
  1097â†’    image-rendering: -webkit-optimize-contrast;
  1098â†’    image-rendering: crisp-edges;
  1099â†’  }
  1100â†’}
  1101â†’
  1102â†’/* Hover detection for touch devices */
  1103â†’@media (hover: none) {
  1104â†’  .hover-only {
  1105â†’    display: none !important;
  1106â†’  }
  1107â†’  
  1108â†’  .btn-base:hover {
  1109â†’    transform: none !important;
  1110â†’  }
  1111â†’  
  1112â†’  .card-base:hover {
  1113â†’    transform: none !important;
  1114â†’    box-shadow: var(--shadow-sm) !important;
  1115â†’  }
  1116â†’}
  1117â†’
  1118â†’/* Pointer device optimization */
  1119â†’@media (pointer: coarse) {
  1120â†’  /* Touch devices */
  1121â†’  .form-control-base {
  1122â†’    font-size: 16px; /* Prevents zoom on iOS */
  1123â†’    padding: var(--spacing-3);
  1124â†’  }
  1125â†’  
  1126â†’  .btn-base {
  1127â†’    padding: var(--spacing-3) var(--spacing-4);
  1128â†’  }
  1129â†’  
  1130â†’  select,
  1131â†’  input,
  1132â†’  textarea {
  1133â†’    font-size: 16px; /* Prevents zoom on iOS */
  1134â†’  }
  1135â†’}
  1136â†’
  1137â†’/* Safe area insets for devices with notches */
  1138â†’@supports (padding: max(0px)) {
  1139â†’  .safe-area-inset-top {
  1140â†’    padding-top: max(var(--spacing-4), env(safe-area-inset-top));
  1141â†’  }
  1142â†’  
  1143â†’  .safe-area-inset-bottom {
  1144â†’    padding-bottom: max(var(--spacing-4), env(safe-area-inset-bottom));
  1145â†’  }
  1146â†’  
  1147â†’  .safe-area-inset-left {
  1148â†’    padding-left: max(var(--spacing-4), env(safe-area-inset-left));
  1149â†’  }
  1150â†’  
  1151â†’  .safe-area-inset-right {
  1152â†’    padding-right: max(var(--spacing-4), env(safe-area-inset-right));
  1153â†’  }
  1154â†’}
  1155â†’
  1156â†’/* PWA display modes */
  1157â†’@media (display-mode: standalone) {
  1158â†’  .pwa-standalone {
  1159â†’    padding-top: var(--spacing-4);
  1160â†’  }
  1161â†’  
  1162â†’  .pwa-standalone .navbar {
  1163â†’    padding-top: env(safe-area-inset-top);
  1164â†’  }
  1165â†’}
  1166â†’
  1167â†’/* Connection-aware styles */
  1168â†’.slow-connection .glass-medium,
  1169â†’.slow-connection .glass-strong {
  1170â†’  backdrop-filter: none !important;
  1171â†’  -webkit-backdrop-filter: none !important;
  1172â†’  background: var(--color-surface-primary) !important;
  1173â†’}
  1174â†’
  1175â†’.slow-connection img {
  1176â†’  filter: blur(2px);
  1177â†’  transition: filter var(--transition-base);
  1178â†’}
  1179â†’
  1180â†’.slow-connection img.loaded {
  1181â†’  filter: none;
  1182â†’}
  1183â†’
  1184â†’/* Gesture feedback */
  1185â†’.gesture-feedback {
  1186â†’  position: relative;
  1187â†’  overflow: hidden;
  1188â†’}
  1189â†’
  1190â†’.gesture-feedback::after {
  1191â†’  content: '';
  1192â†’  position: absolute;
  1193â†’  top: 50%;
  1194â†’  left: 50%;
  1195â†’  width: 0;
  1196â†’  height: 0;
  1197â†’  border-radius: 50%;
  1198â†’  background: rgba(0, 0, 0, 0.1);
  1199â†’  transform: translate(-50%, -50%);
  1200â†’  transition: width 0.3s ease, height 0.3s ease;
  1201â†’  pointer-events: none;
  1202â†’}
  1203â†’
  1204â†’.gesture-feedback.active::after {
  1205â†’  width: 100px;
  1206â†’  height: 100px;
  1207â†’}
  1208â†’
  1209â†’/* Dark mode gesture feedback */
  1210â†’[data-theme="dark"] .gesture-feedback::after {
  1211â†’  background: rgba(255, 255, 255, 0.1);
  1212â†’}
  1213â†’
  1214â†’/* Mobile navigation improvements - removed unused mobile-nav-optimize styles that caused gray bar at bottom */
  1215â†’
  1216â†’/* Swipe gesture indicators */
  1217â†’.swipe-indicator {
  1218â†’  position: relative;
  1219â†’}
  1220â†’
  1221â†’.swipe-indicator::before {
  1222â†’  content: '';
  1223â†’  position: absolute;
  1224â†’  top: 50%;
  1225â†’  right: var(--spacing-4);
  1226â†’  width: 20px;
  1227â†’  height: 2px;
  1228â†’  background: var(--color-gray-400);
  1229â†’  transform: translateY(-50%);
  1230â†’  opacity: 0.5;
  1231â†’}
  1232â†’
  1233â†’.swipe-indicator::after {
  1234â†’  content: '';
  1235â†’  position: absolute;
  1236â†’  top: 50%;
  1237â†’  right: var(--spacing-3);
  1238â†’  width: 0;
  1239â†’  height: 0;
  1240â†’  border-left: 6px solid var(--color-gray-400);
  1241â†’  border-top: 4px solid transparent;
  1242â†’  border-bottom: 4px solid transparent;
  1243â†’  transform: translateY(-50%);
  1244â†’  opacity: 0.5;
  1245â†’}
  1246â†’
  1247â†’/* Loading states optimized for mobile */
  1248â†’.mobile-loading {
  1249â†’  display: flex;
  1250â†’  flex-direction: column;
  1251â†’  align-items: center;
  1252â†’  justify-content: center;
  1253â†’  min-height: 200px;
  1254â†’  padding: var(--spacing-8);
  1255â†’}
  1256â†’
  1257â†’.mobile-loading .loading-spinner {
  1258â†’  width: 40px;
  1259â†’  height: 40px;
  1260â†’  border: 3px solid var(--color-gray-200);
  1261â†’  border-top: 3px solid var(--color-primary-500);
  1262â†’  border-radius: 50%;
  1263â†’  animation: spin 1s linear infinite;
  1264â†’}
  1265â†’
  1266â†’.mobile-loading .loading-text {
  1267â†’  margin-top: var(--spacing-4);
  1268â†’  font-size: var(--font-size-sm);
  1269â†’  color: var(--color-text-secondary);
  1270â†’  text-align: center;
  1271â†’}
  1272â†’
  1273â†’/* Battery optimization styles */
  1274â†’.battery-optimize {
  1275â†’  animation-play-state: paused !important;
  1276â†’}
  1277â†’
  1278â†’.battery-optimize * {
  1279â†’  animation-play-state: paused !important;
  1280â†’  transition-duration: 0.1s !important;
  1281â†’}
  1282â†’
  1283â†’.battery-optimize .glass-medium,
  1284â†’.battery-optimize .glass-strong {
  1285â†’  backdrop-filter: none !important;
  1286â†’  -webkit-backdrop-filter: none !important;
  1287â†’  background: var(--color-surface-primary) !important;
  1288â†’}
  1289â†’
  1290â†’.battery-optimize .animate-pulse,
  1291â†’.battery-optimize .animate-bounce,
  1292â†’.battery-optimize .animate-spin {
  1293â†’  animation: none !important;
  1294â†’}
  1295â†’
  1296â†’/* Accessibility enhancements for mobile */
  1297â†’@media (max-width: 768px) {
  1298â†’  .mobile-a11y button,
  1299â†’  .mobile-a11y [role="button"],
  1300â†’  .mobile-a11y input,
  1301â†’  .mobile-a11y select {
  1302â†’    min-height: 44px;
  1303â†’    font-size: 16px;
  1304â†’  }
  1305â†’  
  1306â†’  .mobile-a11y .focus-ring:focus-visible {
  1307â†’    outline: 3px solid var(--color-primary-500);
  1308â†’    outline-offset: 2px;
  1309â†’  }
  1310â†’}

