import { computed, unref } from 'vue'
import { useAnimate, useMounted, type MaybeComputedElementRef } from '@vueuse/core'
import type { UseAnimateKeyframes, UseAnimateOptions } from '@vueuse/core'

export interface AnimationPreset {
  keyframes: UseAnimateKeyframes
  options: number | UseAnimateOptions
}

export interface UseAnimateElementOptions {
  preset?: keyof typeof animationPresets
  duration?: number
  easing?: string
  immediate?: boolean
  stagger?: number
  onComplete?: () => void
}

// Animation presets using Web Animations API keyframes
export const animationPresets = {
  slideUp: {
    keyframes: [
      { opacity: 0, transform: 'translateY(20px)' },
      { opacity: 1, transform: 'translateY(0px)' }
    ],
    options: {
      duration: 300,
      easing: 'cubic-bezier(0.34, 1.56, 0.64, 1)',
      fill: 'forwards'
    }
  } as AnimationPreset,

  slideDown: {
    keyframes: [
      { opacity: 0, transform: 'translateY(-20px)' },
      { opacity: 1, transform: 'translateY(0px)' }
    ],
    options: {
      duration: 300,
      easing: 'cubic-bezier(0.34, 1.56, 0.64, 1)',
      fill: 'forwards'
    }
  } as AnimationPreset,

  fadeIn: {
    keyframes: [
      { opacity: 0 },
      { opacity: 1 }
    ],
    options: {
      duration: 250,
      easing: 'cubic-bezier(0.16, 1, 0.3, 1)',
      fill: 'forwards'
    }
  } as AnimationPreset,

  scaleUp: {
    keyframes: [
      { opacity: 0, transform: 'scale(0.8)' },
      { opacity: 1, transform: 'scale(1.05)' },
      { opacity: 1, transform: 'scale(1)' }
    ],
    options: {
      duration: 400,
      easing: 'cubic-bezier(0.34, 1.56, 0.64, 1)',
      fill: 'forwards'
    }
  } as AnimationPreset,

  scaleDown: {
    keyframes: [
      { opacity: 1, transform: 'scale(1)' },
      { opacity: 0, transform: 'scale(0.8)' }
    ],
    options: {
      duration: 200,
      easing: 'cubic-bezier(0.7, 0, 0.84, 0)',
      fill: 'forwards'
    }
  } as AnimationPreset,

  staggerFadeIn: {
    keyframes: [
      { opacity: 0, transform: 'translateY(10px)' },
      { opacity: 1, transform: 'translateY(0px)' }
    ],
    options: {
      duration: 300,
      easing: 'cubic-bezier(0.16, 1, 0.3, 1)',
      fill: 'forwards'
    }
  } as AnimationPreset,

  popIn: {
    keyframes: [
      { opacity: 0, transform: 'scale(0.5) rotate(-10deg)' },
      { opacity: 1, transform: 'scale(1.05) rotate(2deg)' },
      { opacity: 1, transform: 'scale(1) rotate(0deg)' }
    ],
    options: {

/**
 * Animation Design Tokens
 *
 * Centralized configuration for responsive animation scaling.
 * All values are baseline measurements at 1440px viewport width.
 */

export const ANIMATION_TOKENS = {
  baseline: {
    viewport: 1440,
    containerHeight: 384,
    containerPadding: 16
  },

  breakpoints: {
    mobile: 375,
    tablet: 768,
    desktop: 1440,
    ultrawide: 1920
  },

  scaling: {
    minScale: 0.5,
    maxScale: 1.5,
    debounce: 100
  },

  theaters: {
    brainstorming: {
      orbRadius: 80,
      lightBulbOrbitRadius: 120,
      sparkleDistance: 45,
      cardFloatDistance: 80
    },
    workflow: {
      cardWidth: 160,
      cardHeight: 192,
      flyDistance: 500,
      arrowWidth: 200
    },
    enhancement: {
      svgWidth: 800,
      svgHeight: 600,
      beamRadius: 300,
      cardWidth: 128,
      cardHeight: 160
    },
    scheduling: {
      clockRadius: 112,
      postOrbitRadius: 200,
      clockWidth: 256,
      clockHeight: 256
    },
    analytics: {
      chartWidth: 600,
      chartHeight: 200,
      cardWidth: 280,
      cardHeight: 320
    },
    unifiedPosts: {
      flyDistanceX: 300,
      flyDistanceY: 200,
      cardWidth: 128,
      cardHeight: 144
    },
    messageHub: {
      inboxWidth: 224,
      inboxHeight: 224,
      messageFlightDistance: 100,
      platformIconDistance: 32
    }
  }
} as const

export type AnimationToken = typeof ANIMATION_TOKENS

/** @type {import('tailwindcss').Config} */
export default {
  darkMode: ["class", '[data-theme="dark"]'],
  content: ["./src/**/*.{astro,html,js,jsx,md,mdx,svelte,ts,tsx,vue}"],
  theme: {
    extend: {
      colors: {
        primary: {
          100: "#cce5e7",
          200: "#99cbd0",
          300: "#66b0b8",
          400: "#3396a1",
          500: "#007c89",
          600: "#00636e",
          700: "#004a52",
          800: "#003237",
          900: "#00191b",
        },
      },
    },
  },
  plugins: [],
};

