.label-position {
  top: min(min(-1.5vh, -1.5vw), -1.5rem);
}

/* Prose styles for documentation */
.prose {
  @apply text-gray-900 dark:text-gray-100;
}

.prose h1,
.prose h2,
.prose h3,
.prose h4,
.prose h5,
.prose h6 {
  @apply text-gray-900 dark:text-white font-semibold;
}

.prose h1 {
  @apply text-4xl sm:text-5xl leading-tight mt-12 mb-6;
}

.prose h2 {
  @apply text-3xl sm:text-4xl leading-snug mt-10 mb-5;
}

.prose h3 {
  @apply text-2xl sm:text-3xl leading-snug mt-8 mb-4;
}

.prose h4 {
  @apply text-xl sm:text-2xl leading-snug mt-6 mb-3;
}

.prose h5 {
  @apply text-lg leading-snug mt-5 mb-2;
}

.prose h6 {
  @apply text-base uppercase tracking-wide text-gray-600 dark:text-gray-400 mt-4 mb-2;
}

.prose a {
  @apply text-primary-600 dark:text-primary-400 hover:text-primary-700 dark:hover:text-primary-300 underline decoration-primary-300 underline-offset-2 transition-colors;
}


.prose p {
  @apply text-gray-800 dark:text-gray-200 leading-relaxed mt-4 mb-4;
}

.prose code {
  @apply bg-gray-100 dark:bg-gray-800 text-primary-700 dark:text-primary-300 px-1.5 py-0.5 rounded font-mono text-sm;
}

.prose pre {
  @apply bg-gray-100 dark:bg-gray-800 border border-gray-200 dark:border-gray-700 rounded-lg p-4 my-6 overflow-x-auto;
}

.prose pre code {
  @apply bg-transparent text-gray-900 dark:text-gray-100 px-0 py-0;
}

.prose strong {
  @apply text-gray-900 dark:text-white font-semibold;
}

.prose em {
  @apply text-gray-700 dark:text-gray-200;
}


.prose blockquote {
  @apply border-l-4 border-l-primary-500 dark:border-l-primary-400 text-gray-700 dark:text-gray-300 bg-gray-50 dark:bg-gray-800/50 px-5 py-4 rounded-r italic my-6;
}

.prose ul,
.prose ol {
  @apply my-5 pl-6;
}

.prose ul {
  @apply list-disc;
}

.prose ol {
  @apply list-decimal;
}

.prose li {
  @apply my-2;
}

.prose ul li,
.prose ol li {
  @apply text-gray-800 dark:text-gray-200;
}

.prose ul li::marker,
.prose ol li::marker {
  @apply text-primary-600 dark:text-primary-400;
}

.prose hr {
  @apply border-gray-300 dark:border-gray-700 my-10;
}

/* Table styles for documentation */
.prose table {
  @apply w-full border-collapse my-6;
}

.prose thead {
  @apply border-b-2 border-gray-300 dark:border-gray-700;
}

.prose th {
  @apply px-4 py-2 text-left font-semibold text-gray-900 dark:text-white;
}

.prose td {
  @apply px-4 py-2 border-t border-gray-200 dark:border-gray-700;
}

.prose tbody tr:hover {
  @apply bg-gray-50 dark:bg-gray-800;
}

.prose img,
.prose video,
.prose picture {
  @apply my-6 rounded-lg shadow-sm;
}

/* ===== GRADIENT UTILITY CLASSES ===== */

/* Hero/Landing Page Gradients - Layered radial approach */
.bg-gradient-hero {
  background:
    var(--gradient-hero-radial-1),
    var(--gradient-hero-radial-2),
    var(--gradient-hero-radial-3),
    var(--gradient-hero-radial-4),
    var(--gradient-hero-radial-5),
    var(--gradient-hero-base);
}

.bg-gradient-hero-vibrant {
  background: var(--gradient-hero-vibrant);
}

.bg-gradient-hero-subtle {
  background: var(--gradient-hero-subtle);
}

/* Accent Gradients */
.bg-gradient-accent-primary {
  background: var(--gradient-accent-primary);
}

.bg-gradient-accent-purple {
  background: var(--gradient-accent-purple);
}

.bg-gradient-accent-warm {
  background: var(--gradient-accent-warm);
}

/* Feature Section Gradients */
.bg-gradient-feature-light {
  background: var(--gradient-feature-light);
}

.bg-gradient-feature-card {
  background: var(--gradient-feature-card);
}

/* CTA/Interactive Gradients with Glow Effects */
.bg-gradient-cta {
  background: var(--gradient-cta);
  box-shadow: 0 0 20px rgba(0, 124, 137, 0.3);
  transition: all 0.3s ease;
}

.bg-gradient-cta:hover {
  background: var(--gradient-cta-hover);
  box-shadow: 0 0 40px rgba(0, 124, 137, 0.5);
  transform: translateY(-2px);
}

.bg-gradient-cta:active {
  transform: translateY(0);
  box-shadow: 0 0 30px rgba(0, 124, 137, 0.4);
}

/* Mesh/Complex Gradients */
.bg-gradient-mesh {
  background: var(--gradient-mesh);
}

/* Optional Mesh Overlay - Add subtle depth to any element */
.bg-gradient-mesh-overlay {
  position: relative;
}

.bg-gradient-mesh-overlay::before {
  content: '';
  position: absolute;
  inset: 0;
  background: var(--gradient-mesh-overlay);
  pointer-events: none;
  z-index: 1;
}

/* Text Gradients */
.text-gradient-primary {
  background: var(--gradient-accent-primary);
  -webkit-background-clip: text;
  background-clip: text;
  -webkit-text-fill-color: transparent;
}

.text-gradient-purple {
  background: var(--gradient-accent-purple);
  -webkit-background-clip: text;
  background-clip: text;
  -webkit-text-fill-color: transparent;
}

.text-gradient-warm {
  background: var(--gradient-accent-warm);
  -webkit-background-clip: text;
  background-clip: text;
  -webkit-text-fill-color: transparent;
}

/* Animated Gradients */
@keyframes gradientShift {
  0%, 100% {
    background-position: 0% 50%;
  }
  50% {
    background-position: 100% 50%;
  }
}

.bg-gradient-animated {
  background-size: 200% 200%;
  animation: gradientShift 8s ease infinite;
}

/* Responsive Gradient Utilities */
@media (max-width: 768px) {
  .bg-gradient-hero,
  .bg-gradient-hero-vibrant {
    /* Simpler gradients on mobile for performance */
    background: var(--gradient-hero-subtle);
  }
}

