    62â†’
    63â†’  /* Semantic Colors - Info */
    64â†’  --color-info-50: #eff6ff;
    65â†’  --color-info-100: #dbeafe;
    66â†’  --color-info-200: #bfdbfe;
    67â†’  --color-info-300: #93c5fd;
