---
import Navbar from "@vue/global/Navbar.vue";
import Footer from "@vue/global/Footer.vue";
import SessionInitializer from "@vue/global/SessionInitializer.vue";
import MetadataTags from "@components/global/MetadataTags.astro";
import ClarityInit from "@components/global/ClarityInit.astro";
import CookieGuard from "@vue/guards/CookieGuard.vue";
import { SITE_DATA } from "@/constants/siteData";

interface Props {
  title: string;
  navVariant?: 'default' | 'landing';
  footerVariant?: 'default' | 'landing';
}

const siteData = SITE_DATA;

const { title } = Astro.props;
---

<!doctype html>
<html lang="en">
  <head>
    <MetadataTags title={title} description={siteData.description} />
    <style is:global>
      html {
        --bg-color-default: white;
        --text-color-default: black;
        color: black;
        background-color: white;
        font-family: system-ui, sans-serif;
      }

      [data-theme="dark"] {
        --bg-color-default: #333;
        --text-color-default: white;
        color: white;
        background-color: #333;
      }

      code {
        font-family:
          Menlo,
          Monaco,
          Lucida Console,
          Liberation Mono,
          DejaVu Sans Mono,
          Bitstream Vera Sans Mono,
          Courier New,
          monospace;
      }
    </style>
    <ClarityInit />
  </head>
  <body>
    <Navbar client:load transition:persist variant={Astro.props.navVariant || 'default'}/>
    <slot />
    <Footer
      client:load
      transition:persist
      footerVariant={Astro.props.footerVariant || (Astro.props.navVariant === 'landing' ? 'landing' : 'default')}
    />
    <CookieGuard client:load transition:persist />
    <SessionInitializer client:load />

    <!-- Teleport Layer -->
    <div id="teleport-layer"></div>
  </body>
</html>

import { defineConfig } from "astro/config";
import vue from "@astrojs/vue";
import { env } from "./src/env";
import cloudflare from "@astrojs/cloudflare";

// VITE PLUGINS
import tsConfigPaths from "vite-tsconfig-paths";
// import node from "@astrojs/node";

import icon from "astro-icon";
import removeConsole from "vite-plugin-remove-console";
import tailwindcss from "@tailwindcss/vite";
// import sentry from "@sentry/astro";
import { nodePolyfills } from "vite-plugin-node-polyfills";

import partytown from "@astrojs/partytown";
import sitemap from "@astrojs/sitemap";

// https://astro.build/config
export default defineConfig({
  site: import.meta.env.DEV
    ? `http://localhost:${process.env.PORT || 6942}`
    : "https://socialaize.com",
  output: "server",
  adapter: cloudflare({
    imageService: "passthrough",
