input.content:
/**
 * Tailwind CSS Plugin: Component Base Styles
 *
 * Provides base component classes for consistent styling across the application.
 * These classes establish foundational patterns for forms, buttons, cards, and badges.
 *
 * Usage:
 * - .form-control-base: Base styling for all form inputs
 * - .btn-base: Base styling for all buttons
 * - .card-base: Base styling for all cards
 * - .badge-base: Base styling for all badges
 */

const plugin = require('tailwindcss/plugin')

module.exports = plugin(function({ addComponents, theme }) {
  addComponents({
    // ==========================================
    // FORM CONTROL BASE
    // ==========================================
    '.form-control-base': {
      width: '100%',
      padding: `${theme('spacing.2')} ${theme('spacing.4')}`,
      border: `1px solid var(--color-border-primary, ${theme('colors.neutral.300')})`,
      borderRadius: theme('borderRadius.lg'),
      backgroundColor: 'var(--color-surface-primary, #ffffff)',
      color: theme('colors.neutral.900'),
      fontSize: theme('fontSize.base[0]'),
      lineHeight: theme('fontSize.base[1].lineHeight'),
      transition: 'border-color 150ms ease, box-shadow 150ms ease',

      '&::placeholder': {
        color: theme('colors.neutral.400'),
      },

      '&:focus': {
        outline: 'none',
        borderColor: 'var(--color-border-focus, ' + theme('colors.primary.500') + ')',
        boxShadow: theme('boxShadow.focus-primary'),
      },

      '&:disabled': {
        opacity: '0.6',
        cursor: 'not-allowed',
        backgroundColor: theme('colors.neutral.100'),
        color: theme('colors.neutral.500'),
      },

      '&[aria-invalid="true"]': {
        borderColor: 'var(--color-border-error, ' + theme('colors.error.500') + ')',

        '&:focus': {
          boxShadow: theme('boxShadow.focus-error'),
        },
      },

      // Dark mode support
      '[data-theme="dark"] &': {
        backgroundColor: theme('colors.dark.surface.primary'),
        borderColor: theme('colors.dark.border.primary'),
        color: theme('colors.dark.text.primary'),

        '&::placeholder': {
          color: theme('colors.neutral.500'),
        },

        '&:disabled': {
          backgroundColor: theme('colors.dark.surface.secondary'),
          color: theme('colors.neutral.600'),
        },
      },
    },

    // ==========================================
    // BUTTON BASE
    // ==========================================
    '.btn-base': {
      display: 'inline-flex',
      alignItems: 'center',
      justifyContent: 'center',
      gap: theme('spacing.2'),
      fontWeight: theme('fontWeight.medium'),
      borderRadius: theme('borderRadius.lg'),
      transition: 'all 150ms ease',
      cursor: 'pointer',
      textDecoration: 'none',
      whiteSpace: 'nowrap',
      userSelect: 'none',

      '&:disabled, &[aria-disabled="true"]': {
        opacity: '0.6',
        pointerEvents: 'none',
        cursor: 'not-allowed',
      },

      '&:focus-visible': {
        outline: 'none',
        boxShadow: theme('boxShadow.focus-primary'),
      },

      // Size variants
      '&.btn-xs': {
        padding: `${theme('spacing.1')} ${theme('spacing.2')}`,
        fontSize: theme('fontSize.xs[0]'),
        lineHeight: theme('fontSize.xs[1].lineHeight'),
      },

      '&.btn-sm': {
        padding: `${theme('spacing.1.5')} ${theme('spacing.3')}`,
        fontSize: theme('fontSize.sm[0]'),
        lineHeight: theme('fontSize.sm[1].lineHeight'),
      },

      '&.btn-md': {
        padding: `${theme('spacing.2')} ${theme('spacing.4')}`,
        fontSize: theme('fontSize.base[0]'),
        lineHeight: theme('fontSize.base[1].lineHeight'),
      },

      '&.btn-lg': {
        padding: `${theme('spacing.2.5')} ${theme('spacing.6')}`,
        fontSize: theme('fontSize.lg[0]'),
        lineHeight: theme('fontSize.lg[1].lineHeight'),
      },

      '&.btn-xl': {
        padding: `${theme('spacing.3')} ${theme('spacing.8')}`,
        fontSize: theme('fontSize.xl[0]'),
        lineHeight: theme('fontSize.xl[1].lineHeight'),
      },
    },

    // ==========================================
    // CARD BASE
    // ==========================================
    '.card-base': {
      backgroundColor: 'var(--color-surface-primary, #ffffff)',
      border: `1px solid var(--color-border-primary, ${theme('colors.neutral.200')})`,
      borderRadius: theme('borderRadius.xl'),
      boxShadow: theme('boxShadow.sm'),
      transition: 'box-shadow 200ms ease, transform 200ms ease',

      '&.card-interactive': {
        cursor: 'pointer',

        '&:hover': {
          boxShadow: theme('boxShadow.md'),
          transform: 'translateY(-2px)',
        },

        '&:active': {
          transform: 'translateY(0)',
          boxShadow: theme('boxShadow.sm'),
        },
      },

      // Size variants
      '&.card-sm': {
        padding: theme('spacing.4'),
      },

      '&.card-md': {
        padding: theme('spacing.6'),
      },

      '&.card-lg': {
        padding: theme('spacing.8'),
      },

      // Dark mode support
      '[data-theme="dark"] &': {
        backgroundColor: theme('colors.dark.surface.primary'),
        borderColor: theme('colors.dark.border.primary'),
      },
    },

    // ==========================================
    // BADGE BASE
    // ==========================================
    '.badge-base': {
      display: 'inline-flex',
      alignItems: 'center',
      gap: theme('spacing.1'),
      padding: `${theme('spacing.1')} ${theme('spacing.2')}`,
      fontSize: theme('fontSize.xs[0]'),
      fontWeight: theme('fontWeight.medium'),
      textTransform: 'uppercase',
      borderRadius: theme('borderRadius.DEFAULT'),
      letterSpacing: theme('letterSpacing.wide'),
      lineHeight: theme('fontSize.xs[1].lineHeight'),
      whiteSpace: 'nowrap',

      // Size variants
      '&.badge-sm': {
        padding: `${theme('spacing.0.5')} ${theme('spacing.1.5')}`,
        fontSize: '0.625rem', // 10px
      },

      '&.badge-md': {
        padding: `${theme('spacing.1')} ${theme('spacing.2')}`,
        fontSize: theme('fontSize.xs[0]'),
      },

      '&.badge-lg': {
        padding: `${theme('spacing.1.5')} ${theme('spacing.3')}`,
        fontSize: theme('fontSize.sm[0]'),
      },
    },

    // ==========================================
    // MODAL BASE
    // ==========================================
    '.modal-backdrop': {
      position: 'fixed',
      inset: '0',
      backgroundColor: 'rgb(0 0 0 / 0.5)',
      backdropFilter: 'blur(4px)',
      zIndex: theme('zIndex.modal-backdrop'),
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'center',
      padding: theme('spacing.4'),
    },

    '.modal-panel': {
      backgroundColor: 'var(--color-surface-primary, #ffffff)',
      borderRadius: theme('borderRadius.2xl'),
      boxShadow: theme('boxShadow.2xl'),
      maxWidth: '90vw',
      maxHeight: '90vh',
      overflow: 'auto',
      position: 'relative',

      '[data-theme="dark"] &': {
        backgroundColor: theme('colors.dark.surface.primary'),
      },
    },

    // ==========================================
    // INPUT GROUP (Icon + Input pattern)
    // ==========================================
    '.input-group': {
      position: 'relative',
      width: '100%',

      '& .input-group-icon': {
        position: 'absolute',
        left: theme('spacing.3'),
        top: '50%',
        transform: 'translateY(-50%)',
        pointerEvents: 'none',
        color: theme('colors.neutral.400'),
      },

      '& .input-group-input': {
        paddingLeft: theme('spacing.10'),
      },

      '&:focus-within .input-group-icon': {
        color: theme('colors.primary.500'),
      },
    },
  })
})

