   357→/* ===== KEYFRAME ANIMATIONS ===== */
   358→@keyframes fadeIn {
   359→  from {
   360→    opacity: 0;
   361→  }
   362→  to {
   363→    opacity: 1;
   364→  }
   365→}
   366→
   367→@keyframes fadeOut {
   368→  from {
   369→    opacity: 1;
   370→  }
   371→  to {
   372→    opacity: 0;
   373→  }
   374→}
   375→
   376→@keyframes slideInUp {
   377→  from {
   378→    opacity: 0;
   379→    transform: translateY(1rem);
   380→  }
   381→  to {
   382→    opacity: 1;
   383→    transform: translateY(0);
   384→  }
   385→}
   386→
   387→@keyframes slideInDown {
   388→  from {
   389→    opacity: 0;
   390→    transform: translateY(-1rem);
   391→  }
   392→  to {
   393→    opacity: 1;
   394→    transform: translateY(0);
   395→  }
   396→}
   397→
   398→@keyframes scaleIn {
   399→  from {
   400→    opacity: 0;
   401→    transform: scale(0.9);
   402→  }
   403→  to {
   404→    opacity: 1;
   405→    transform: scale(1);
   406→  }
   407→}
   408→
   409→@keyframes scaleOut {
   410→  from {
   411→    opacity: 1;
   412→    transform: scale(1);
   413→  }
   414→  to {
   415→    opacity: 0;
   416→    transform: scale(0.9);
   417→  }
   418→}
   419→
   420→@keyframes pulse {
   421→  0%, 100% {
   422→    opacity: 1;
   423→  }
   424→  50% {
   425→    opacity: 0.5;
   426→  }
   427→}
   428→
   429→@keyframes bounce {
   430→  0%, 100% {
   431→    transform: translateY(-25%);
   432→    animation-timing-function: cubic-bezier(0.8, 0, 1, 1);
   433→  }
   434→  50% {
   435→    transform: none;
   436→    animation-timing-function: cubic-bezier(0, 0, 0.2, 1);
   437→  }
   438→}
   439→
   440→@keyframes spin {
   441→  to {
   442→    transform: rotate(360deg);
   443→  }
   444→}
   445→
   446→@keyframes ping {
   447→  75%, 100% {
   448→    transform: scale(2);
   449→    opacity: 0;
   450→  }
   451→}
