import { ref, computed, onMounted, onUnmounted, type Ref } from 'vue'

export interface ResponsiveDimensions {
  width: number
  height: number
  scale: number // Relative to baseline (1440px)
  aspectRatio: number
}

export interface ResponsiveContainerOptions {
  baselineWidth?: number // Default: 1440
  minScale?: number // Default: 0.5
  maxScale?: number // Default: 1.5
  debounce?: number // Default: 100ms
}

export function useResponsiveContainer(
  containerRef: Ref<HTMLElement | undefined>,
  options: ResponsiveContainerOptions = {}
) {
  const {
    baselineWidth = 1440,
    minScale = 0.5,
    maxScale = 1.5,
    debounce = 100
  } = options

  // Reactive dimensions
  const dimensions = ref<ResponsiveDimensions>({
    width: 0,
    height: 0,
    scale: 1,
    aspectRatio: 1
  })

  let resizeObserver: ResizeObserver | null = null
  let debounceTimer: NodeJS.Timeout | null = null

  // Update dimensions
  const updateDimensions = (entries: ResizeObserverEntry[]) => {
    if (debounceTimer) clearTimeout(debounceTimer)

    debounceTimer = setTimeout(() => {
      const entry = entries[0]
      if (!entry) return

      const { width, height } = entry.contentRect

      // Calculate scale factor relative to baseline
      const rawScale = width / baselineWidth
      const clampedScale = Math.max(minScale, Math.min(maxScale, rawScale))

      dimensions.value = {
        width,
        height,
        scale: clampedScale,
        aspectRatio: width / height
      }
    }, debounce)
  }

  // Setup observer
  onMounted(() => {
    if (!containerRef.value) return

    resizeObserver = new ResizeObserver(updateDimensions)
    resizeObserver.observe(containerRef.value)

    // Initial measurement
    const rect = containerRef.value.getBoundingClientRect()
    updateDimensions([{
      contentRect: rect,
      target: containerRef.value
    } as ResizeObserverEntry])
  })

  // Cleanup
  onUnmounted(() => {
    if (resizeObserver) {
      resizeObserver.disconnect()
    }
    if (debounceTimer) {
      clearTimeout(debounceTimer)
    }
  })

  // Helper: Scale a pixel value
  const scaleValue = computed(() => (baseValue: number) => {
    return baseValue * dimensions.value.scale
  })

  // Helper: Get responsive radius
  const getResponsiveRadius = computed(() => (baseRadius: number) => {
    return Math.round(scaleValue.value(baseRadius))
  })

  // Helper: Get responsive offset
  const getResponsiveOffset = computed(() => (baseOffset: { x: number; y: number }) => {
    return {
      x: Math.round(scaleValue.value(baseOffset.x)),
      y: Math.round(scaleValue.value(baseOffset.y))
    }
  })

  return {
    dimensions,
    scaleValue,
    getResponsiveRadius,
    getResponsiveOffset
  }
}

