    }
  }
}

@utility focus-ring-offset {
  &:focus,
  &:focus-visible {
    outline: none;
    box-shadow: var(--shadow-focus-primary), 0 0 0 4px rgba(255, 255, 255, 0.1);
  }

  [data-theme="dark"] & {
    &:focus,
    &:focus-visible {
      box-shadow: var(--shadow-focus-primary), 0 0 0 4px rgba(0, 0, 0, 0.2);
    }
  }
}

@utility focus-ring-inset {
  &:focus,
  &:focus-visible {
    outline: none;
    box-shadow: inset 0 0 0 2px var(--color-primary-500);
  }
}

@utility focus-visible-only {
  &:focus:not(:focus-visible) {
    outline: none;
    box-shadow: none;
  }

  &:focus-visible {
    outline: none;
    box-shadow: var(--shadow-focus-primary);
  }
}

@utility focus-within-ring {
  &:focus-within {
    outline: none;
    box-shadow: var(--shadow-focus-primary);
  }
}

@utility focus-none {
  &:focus,
  &:focus-visible {
    outline: none;
    box-shadow: none;
  }
}

/* ==========================================
 * ACCESSIBILITY UTILITIES
 * ========================================== */

@utility skip-link {
  position: absolute;
  left: -9999px;
  z-index: 9999;
  padding: var(--spacing-2) var(--spacing-4);
  background-color: var(--color-primary-600);
  color: #ffffff;
  border-radius: var(--radius-md);
  font-size: var(--font-size-sm);
  font-weight: var(--font-weight-medium);
  text-decoration: none;

  &:focus,
  &:focus-visible {
    left: var(--spacing-4);
    top: var(--spacing-4);
    outline: none;
    box-shadow: var(--shadow-focus-primary);
  }
}

/* ==========================================
 * PERFORMANCE & OPTIMIZATION UTILITIES
 * ========================================== */

@utility scrollbar-hidden {
  scrollbar-width: none;

  &::-webkit-scrollbar {
    display: none;
  }
}

@utility text-balance {
  text-wrap: balance;
}

@utility content-auto {
  content-visibility: auto;
}

@utility gpu-accelerate {
  transform: translateZ(0);
  backface-visibility: hidden;
  perspective: 1000px;
}
