    @apply font-display font-bold;
  }
}

/* Component patterns (reusable) */
@layer components {
  .btn {
    @apply px-4 py-2 rounded font-medium transition-colors;
  }

  .btn-primary {
    @apply btn bg-primary text-white hover:bg-primary-hover;
  }
