/* Landing page typography utilities */

:root {
  --font-size-display-2xl: clamp(3rem, 5vw, 4.5rem);  /* 48-72px hero */
  --font-size-display-xl: clamp(2.5rem, 4vw, 3.5rem); /* Section headings */
  --font-size-heading-card: clamp(1.875rem, 3vw, 2.25rem);
}

.heading-hero {
  font-size: var(--font-size-display-2xl);
  font-weight: 800;
  line-height: 1.1;
  letter-spacing: -0.025em;
  color: rgb(17 24 39); /* gray-900 */
}

html[data-theme="dark"] .heading-hero {
  color: rgb(255 255 255); /* white */
}

.heading-section {
  font-size: var(--font-size-display-xl);
  font-weight: 700;
  line-height: 1.1;
  letter-spacing: -0.025em;
  color: rgb(17 24 39); /* gray-900 */
}

html[data-theme="dark"] .heading-section {
  color: rgb(255 255 255); /* white */
}

.heading-card {
  font-size: var(--font-size-heading-card);
  font-weight: 600;
  line-height: 1.25;
  color: rgb(17 24 39); /* gray-900 */
}

html[data-theme="dark"] .heading-card {
  color: rgb(255 255 255); /* white */
}

.body-hero {
  font-size: 1.25rem;
  line-height: 1.75;
  color: rgb(75 85 99); /* gray-600 */
}

@media (min-width: 1024px) {
  .body-hero {
    font-size: 1.5rem;
  }
}

html[data-theme="dark"] .body-hero {
  color: rgb(209 213 219); /* gray-300 */
}

.body-large {
  font-size: 1.125rem;
  line-height: 1.75;
  color: rgb(75 85 99); /* gray-600 */
}

html[data-theme="dark"] .body-large {
  color: rgb(209 213 219); /* gray-300 */
}

.gradient-text-primary {
  background: linear-gradient(135deg, #007c89 0%, #7c3aed 100%);
  -webkit-background-clip: text;
  -webkit-text-fill-color: transparent;
  background-clip: text;
}

.gradient-text-light {
  background: linear-gradient(135deg, rgb(17 24 39) 0%, rgb(75 85 99) 100%); /* gray-900 to gray-600 for light mode */
  -webkit-background-clip: text;
  -webkit-text-fill-color: transparent;
  background-clip: text;
}

html[data-theme="dark"] .gradient-text-light {
  background: linear-gradient(135deg, white 0%, #d1d5db 100%);
  -webkit-background-clip: text;
  -webkit-text-fill-color: transparent;
  background-clip: text;
}

/**
 * Tailwind CSS Plugin: Component Base Styles
 *
 * Provides base component classes for consistent styling across the application.
 * These classes establish foundational patterns for forms, buttons, cards, and badges.
 *
 * Usage:
 * - .form-control-base: Base styling for all form inputs
 * - .btn-base: Base styling for all buttons
 * - .card-base: Base styling for all cards
 * - .badge-base: Base styling for all badges
 */

const plugin = require('tailwindcss/plugin')

module.exports = plugin(function({ addComponents, theme }) {
  addComponents({
    // ==========================================
    // FORM CONTROL BASE
    // ==========================================
    '.form-control-base': {
      width: '100%',
      padding: `${theme('spacing.2')} ${theme('spacing.4')}`,
      border: `1px solid var(--color-border-primary, ${theme('colors.neutral.300')})`,
      borderRadius: theme('borderRadius.lg'),
      backgroundColor: 'var(--color-surface-primary, #ffffff)',
      color: theme('colors.neutral.900'),
      fontSize: theme('fontSize.base[0]'),
      lineHeight: theme('fontSize.base[1].lineHeight'),
      transition: 'border-color 150ms ease, box-shadow 150ms ease',

      '&::placeholder': {
        color: theme('colors.neutral.400'),
      },

      '&:focus': {
        outline: 'none',
        borderColor: 'var(--color-border-focus, ' + theme('colors.primary.500') + ')',
        boxShadow: theme('boxShadow.focus-primary'),
      },

      '&:disabled': {
        opacity: '0.6',
        cursor: 'not-allowed',
        backgroundColor: theme('colors.neutral.100'),
        color: theme('colors.neutral.500'),
      },

      '&[aria-invalid="true"]': {
        borderColor: 'var(--color-border-error, ' + theme('colors.error.500') + ')',

        '&:focus': {
          boxShadow: theme('boxShadow.focus-error'),
        },
      },

      // Dark mode support
      '[data-theme="dark"] &': {
        backgroundColor: theme('colors.dark.surface.primary'),
        borderColor: theme('colors.dark.border.primary'),
        color: theme('colors.dark.text.primary'),

        '&::placeholder': {
          color: theme('colors.neutral.500'),
        },

        '&:disabled': {
          backgroundColor: theme('colors.dark.surface.secondary'),
          color: theme('colors.neutral.600'),
        },
      },
    },

    // ==========================================
    // BUTTON BASE
    // ==========================================
    '.btn-base': {
      display: 'inline-flex',
      alignItems: 'center',
      justifyContent: 'center',
      gap: theme('spacing.2'),
      fontWeight: theme('fontWeight.medium'),
      borderRadius: theme('borderRadius.lg'),
      transition: 'all 150ms ease',
      cursor: 'pointer',
      textDecoration: 'none',
      whiteSpace: 'nowrap',
      userSelect: 'none',

      '&:disabled, &[aria-disabled="true"]': {
        opacity: '0.6',
        pointerEvents: 'none',
        cursor: 'not-allowed',
      },

      '&:focus-visible': {
        outline: 'none',
        boxShadow: theme('boxShadow.focus-primary'),
      },

      // Size variants
      '&.btn-xs': {
        padding: `${theme('spacing.1')} ${theme('spacing.2')}`,
        fontSize: theme('fontSize.xs[0]'),
        lineHeight: theme('fontSize.xs[1].lineHeight'),
      },

      '&.btn-sm': {
        padding: `${theme('spacing.1.5')} ${theme('spacing.3')}`,
        fontSize: theme('fontSize.sm[0]'),
        lineHeight: theme('fontSize.sm[1].lineHeight'),
      },

      '&.btn-md': {
        padding: `${theme('spacing.2')} ${theme('spacing.4')}`,
        fontSize: theme('fontSize.base[0]'),
        lineHeight: theme('fontSize.base[1].lineHeight'),
      },

      '&.btn-lg': {
        padding: `${theme('spacing.2.5')} ${theme('spacing.6')}`,
        fontSize: theme('fontSize.lg[0]'),
        lineHeight: theme('fontSize.lg[1].lineHeight'),
      },

      '&.btn-xl': {
        padding: `${theme('spacing.3')} ${theme('spacing.8')}`,
        fontSize: theme('fontSize.xl[0]'),
        lineHeight: theme('fontSize.xl[1].lineHeight'),
      },
    },

    // ==========================================
    // CARD BASE
    // ==========================================
    '.card-base': {
      backgroundColor: 'var(--color-surface-primary, #ffffff)',
      border: `1px solid var(--color-border-primary, ${theme('colors.neutral.200')})`,
      borderRadius: theme('borderRadius.xl'),
      boxShadow: theme('boxShadow.sm'),
      transition: 'box-shadow 200ms ease, transform 200ms ease',

      '&.card-interactive': {
        cursor: 'pointer',

        '&:hover': {
          boxShadow: theme('boxShadow.md'),
          transform: 'translateY(-2px)',
        },

        '&:active': {
          transform: 'translateY(0)',
          boxShadow: theme('boxShadow.sm'),
        },
      },

      // Size variants
      '&.card-sm': {
        padding: theme('spacing.4'),
      },

      '&.card-md': {
        padding: theme('spacing.6'),
      },

      '&.card-lg': {
        padding: theme('spacing.8'),
      },

      // Dark mode support
      '[data-theme="dark"] &': {
        backgroundColor: theme('colors.dark.surface.primary'),
        borderColor: theme('colors.dark.border.primary'),
      },
    },

    // ==========================================
    // BADGE BASE
    // ==========================================
    '.badge-base': {
      display: 'inline-flex',
      alignItems: 'center',
      gap: theme('spacing.1'),
      padding: `${theme('spacing.1')} ${theme('spacing.2')}`,
      fontSize: theme('fontSize.xs[0]'),
      fontWeight: theme('fontWeight.medium'),
      textTransform: 'uppercase',
      borderRadius: theme('borderRadius.DEFAULT'),
      letterSpacing: theme('letterSpacing.wide'),
      lineHeight: theme('fontSize.xs[1].lineHeight'),
      whiteSpace: 'nowrap',

      // Size variants
      '&.badge-sm': {
        padding: `${theme('spacing.0.5')} ${theme('spacing.1.5')}`,
        fontSize: '0.625rem', // 10px
      },

      '&.badge-md': {
        padding: `${theme('spacing.1')} ${theme('spacing.2')}`,
        fontSize: theme('fontSize.xs[0]'),
      },

      '&.badge-lg': {
        padding: `${theme('spacing.1.5')} ${theme('spacing.3')}`,
        fontSize: theme('fontSize.sm[0]'),
      },
    },

    // ==========================================
    // MODAL BASE
    // ==========================================
    '.modal-backdrop': {
      position: 'fixed',
      inset: '0',
      backgroundColor: 'rgb(0 0 0 / 0.5)',
      backdropFilter: 'blur(4px)',
      zIndex: theme('zIndex.modal-backdrop'),
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'center',
      padding: theme('spacing.4'),
    },

    '.modal-panel': {
      backgroundColor: 'var(--color-surface-primary, #ffffff)',
      borderRadius: theme('borderRadius.2xl'),
      boxShadow: theme('boxShadow.2xl'),
      maxWidth: '90vw',
      maxHeight: '90vh',
      overflow: 'auto',
      position: 'relative',

      '[data-theme="dark"] &': {
        backgroundColor: theme('colors.dark.surface.primary'),
      },
    },

    // ==========================================
    // INPUT GROUP (Icon + Input pattern)
    // ==========================================
    '.input-group': {
      position: 'relative',
      width: '100%',

      '& .input-group-icon': {
        position: 'absolute',
        left: theme('spacing.3'),
        top: '50%',
        transform: 'translateY(-50%)',
        pointerEvents: 'none',
        color: theme('colors.neutral.400'),
      },

      '& .input-group-input': {
        paddingLeft: theme('spacing.10'),
      },

      '&:focus-within .input-group-icon': {
        color: theme('colors.primary.500'),
      },
    },
  })
})

/**
 * Tailwind CSS Plugin: Glass Morphism Effects
 *
 * Provides glassmorphism utility classes with three intensity levels.
 * Each level includes automatic dark mode support via [data-theme="dark"].
 *
 * Usage:
 * - .glass-subtle: Light glass effect (80% opacity, 8px blur)
 * - .glass-medium: Medium glass effect (90% opacity, 12px blur)
 * - .glass-strong: Strong glass effect (95% opacity, 16px blur)
 *
 * All variants include:
 * - Backdrop blur filter
 * - Semi-transparent background
 * - Subtle border for depth
 * - Dark mode support
 */

const plugin = require('tailwindcss/plugin')

module.exports = plugin(function({ addUtilities, theme }) {
  addUtilities({
    // ==========================================
    // GLASS SUBTLE (Light effect)
    // ==========================================
    '.glass-subtle': {
      background: 'rgba(255, 255, 255, 0.8)',
      backdropFilter: 'blur(8px)',
      WebkitBackdropFilter: 'blur(8px)', // Safari support
      border: '1px solid rgba(255, 255, 255, 0.2)',
      boxShadow: '0 4px 6px rgba(0, 0, 0, 0.05)',

      // Dark mode variant
      '[data-theme="dark"] &': {
        background: 'rgba(31, 41, 55, 0.8)',
        border: '1px solid rgba(55, 65, 81, 0.3)',
        boxShadow: '0 4px 6px rgba(0, 0, 0, 0.2)',
      },
    },

    // ==========================================
    // GLASS MEDIUM (Standard effect)
    // ==========================================
    '.glass-medium': {
      background: 'rgba(255, 255, 255, 0.9)',
      backdropFilter: 'blur(12px)',
      WebkitBackdropFilter: 'blur(12px)',
      border: '1px solid rgba(255, 255, 255, 0.3)',
      boxShadow: '0 8px 16px rgba(0, 0, 0, 0.1)',

      '[data-theme="dark"] &': {
        background: 'rgba(31, 41, 55, 0.9)',
        border: '1px solid rgba(55, 65, 81, 0.4)',
        boxShadow: '0 8px 16px rgba(0, 0, 0, 0.3)',
      },
    },

    // ==========================================
    // GLASS STRONG (Maximum effect)
    // ==========================================
    '.glass-strong': {
      background: 'rgba(255, 255, 255, 0.95)',
      backdropFilter: 'blur(16px)',
      WebkitBackdropFilter: 'blur(16px)',
      border: '1px solid rgba(255, 255, 255, 0.4)',
      boxShadow: '0 12px 24px rgba(0, 0, 0, 0.15)',

      '[data-theme="dark"] &': {
        background: 'rgba(31, 41, 55, 0.95)',
        border: '1px solid rgba(55, 65, 81, 0.5)',
        boxShadow: '0 12px 24px rgba(0, 0, 0, 0.4)',
      },
    },

    // ==========================================
    // GLASS CARD (Optimized for card containers)
    // ==========================================
    '.glass-card': {
      background: 'rgba(255, 255, 255, 0.85)',
      backdropFilter: 'blur(10px)',
      WebkitBackdropFilter: 'blur(10px)',
      border: '1px solid rgba(255, 255, 255, 0.25)',
      borderRadius: theme('borderRadius.xl'),
      boxShadow: theme('boxShadow.md'),
      padding: theme('spacing.6'),

      '[data-theme="dark"] &': {
        background: 'rgba(31, 41, 55, 0.85)',
        border: '1px solid rgba(55, 65, 81, 0.35)',
      },
    },

    // ==========================================
    // GLASS NAVBAR (Optimized for navigation)
    // ==========================================
    '.glass-navbar': {
      background: 'rgba(255, 255, 255, 0.75)',
      backdropFilter: 'blur(20px) saturate(180%)',
      WebkitBackdropFilter: 'blur(20px) saturate(180%)',
      borderBottom: '1px solid rgba(255, 255, 255, 0.2)',
      boxShadow: '0 2px 8px rgba(0, 0, 0, 0.05)',

      '[data-theme="dark"] &': {
        background: 'rgba(17, 24, 39, 0.75)',
        borderBottom: '1px solid rgba(55, 65, 81, 0.3)',
        boxShadow: '0 2px 8px rgba(0, 0, 0, 0.2)',
      },
    },

    // ==========================================
    // GLASS MODAL (Optimized for modal overlays)
    // ==========================================
    '.glass-modal': {
      background: 'rgba(255, 255, 255, 0.92)',
      backdropFilter: 'blur(16px)',
      WebkitBackdropFilter: 'blur(16px)',
      border: '1px solid rgba(255, 255, 255, 0.35)',
      borderRadius: theme('borderRadius.2xl'),
      boxShadow: theme('boxShadow.2xl'),

      '[data-theme="dark"] &': {
        background: 'rgba(17, 24, 39, 0.92)',
        border: '1px solid rgba(55, 65, 81, 0.45)',
      },
    },

    // ==========================================
    // UTILITY: Disable glass effect on low-end devices
    // ==========================================
    '@media (prefers-reduced-motion: reduce)': {
      '.glass-subtle, .glass-medium, .glass-strong, .glass-card, .glass-navbar, .glass-modal': {
        backdropFilter: 'none',
        WebkitBackdropFilter: 'none',
      },
    },

    // ==========================================
    // UTILITY: Battery optimization
    // ==========================================
    '@media (prefers-reduced-data: reduce)': {
      '.glass-subtle, .glass-medium, .glass-strong, .glass-card, .glass-navbar, .glass-modal': {
        backdropFilter: 'none',
        WebkitBackdropFilter: 'none',
        background: 'rgba(255, 255, 255, 0.98)',

        '[data-theme="dark"] &': {
          background: 'rgba(17, 24, 39, 0.98)',
        },
      },
    },
  })
})

/**
 * Tailwind CSS Plugin: Focus Ring Utilities
 *
 * Provides accessible focus ring utilities for keyboard navigation.
 * All focus rings follow WCAG 2.1 guidelines for visible focus indicators.
 *
 * Usage:
 * - .focus-ring: Standard primary color focus ring
 * - .focus-ring-error: Error state focus ring
 * - .focus-ring-success: Success state focus ring
 * - .focus-ring-info: Info state focus ring
 * - .focus-ring-offset: Focus ring with offset (for dark backgrounds)
 *
 * Features:
 * - 2px ring width for visibility
 * - Optional 2px offset for contrast
 * - Removes default browser outline
 * - Respects prefers-contrast: high
 * - Dark mode optimized
 */

const plugin = require('tailwindcss/plugin')

module.exports = plugin(function({ addUtilities, theme }) {
  addUtilities({
    // ==========================================
    // PRIMARY FOCUS RING (Default)
    // ==========================================
    '.focus-ring': {
      '&:focus, &:focus-visible': {
        outline: 'none',
        boxShadow: theme('boxShadow.focus-primary'),
        borderColor: theme('colors.primary.500'),
      },

      // High contrast mode enhancement
      '@media (prefers-contrast: high)': {
        '&:focus, &:focus-visible': {
          outline: `2px solid ${theme('colors.primary.600')}`,
          outlineOffset: '2px',
        },
      },
    },

    // ==========================================
    // ERROR FOCUS RING
    // ==========================================
    '.focus-ring-error': {
      '&:focus, &:focus-visible': {
        outline: 'none',
        boxShadow: theme('boxShadow.focus-error'),
        borderColor: theme('colors.error.500'),
      },

      '@media (prefers-contrast: high)': {
        '&:focus, &:focus-visible': {
          outline: `2px solid ${theme('colors.error.600')}`,
          outlineOffset: '2px',
        },
      },
    },

    // ==========================================
    // SUCCESS FOCUS RING
    // ==========================================
    '.focus-ring-success': {
      '&:focus, &:focus-visible': {
        outline: 'none',
        boxShadow: theme('boxShadow.focus-success'),
        borderColor: theme('colors.success.500'),
      },

      '@media (prefers-contrast: high)': {
        '&:focus, &:focus-visible': {
          outline: `2px solid ${theme('colors.success.600')}`,
          outlineOffset: '2px',
        },
      },
    },

    // ==========================================
    // INFO FOCUS RING
    // ==========================================
    '.focus-ring-info': {
      '&:focus, &:focus-visible': {
        outline: 'none',
        boxShadow: '0 0 0 3px rgba(59, 130, 246, 0.1)',
        borderColor: theme('colors.info.500'),
      },

      '@media (prefers-contrast: high)': {
        '&:focus, &:focus-visible': {
          outline: `2px solid ${theme('colors.info.600')}`,
          outlineOffset: '2px',
        },
      },
    },

    // ==========================================
    // FOCUS RING WITH OFFSET (for dark backgrounds)
    // ==========================================
    '.focus-ring-offset': {
      '&:focus, &:focus-visible': {
        outline: 'none',
        boxShadow: `${theme('boxShadow.focus-primary')}, 0 0 0 4px rgba(255, 255, 255, 0.1)`,
      },

      '[data-theme="dark"] &': {
        '&:focus, &:focus-visible': {
          boxShadow: `${theme('boxShadow.focus-primary')}, 0 0 0 4px rgba(0, 0, 0, 0.2)`,
        },
      },
    },

    // ==========================================
    // FOCUS RING INSET (for filled backgrounds)
    // ==========================================
    '.focus-ring-inset': {
      '&:focus, &:focus-visible': {
        outline: 'none',
        boxShadow: `inset 0 0 0 2px ${theme('colors.primary.500')}`,
      },
    },

    // ==========================================
    // FOCUS RING WARNING
    // ==========================================
    '.focus-ring-warning': {
      '&:focus, &:focus-visible': {
        outline: 'none',
        boxShadow: '0 0 0 3px rgba(245, 158, 11, 0.1)',
        borderColor: theme('colors.warning.500'),
      },

      '@media (prefers-contrast: high)': {
        '&:focus, &:focus-visible': {
          outline: `2px solid ${theme('colors.warning.600')}`,
          outlineOffset: '2px',
        },
      },
    },

    // ==========================================
    // FOCUS VISIBLE ONLY (no mouse focus)
    // ==========================================
    '.focus-visible-only': {
      '&:focus:not(:focus-visible)': {
        outline: 'none',
        boxShadow: 'none',
      },

      '&:focus-visible': {
        outline: 'none',
        boxShadow: theme('boxShadow.focus-primary'),
      },
    },

    // ==========================================
    // SKIP LINK (for accessibility navigation)
    // ==========================================
    '.skip-link': {
      position: 'absolute',
      left: '-9999px',
      zIndex: '9999',
      padding: `${theme('spacing.2')} ${theme('spacing.4')}`,
      backgroundColor: theme('colors.primary.600'),
      color: '#ffffff',
      borderRadius: theme('borderRadius.md'),
      fontSize: theme('fontSize.sm[0]'),
      fontWeight: theme('fontWeight.medium'),
      textDecoration: 'none',

      '&:focus, &:focus-visible': {
        left: theme('spacing.4'),
        top: theme('spacing.4'),
        outline: 'none',
        boxShadow: theme('boxShadow.focus-primary'),
      },
    },

    // ==========================================
    // FOCUS WITHIN (for container focus states)
    // ==========================================
    '.focus-within-ring': {
      '&:focus-within': {
        outline: 'none',
        boxShadow: theme('boxShadow.focus-primary'),
      },
    },

    // ==========================================
    // REMOVE FOCUS (use sparingly, ensure alternative indicator)
    // ==========================================
    '.focus-none': {
      '&:focus, &:focus-visible': {
        outline: 'none',
        boxShadow: 'none',
      },
    },
  })
})

