     1→<template>
     2→  <TutorialArrowInstance
     3→    v-for="target in targetElements"
     4→    :key="getElementKey(target)"
     5→    :target-element="target"
     6→    :message="message"
     7→    :icon="icon"
     8→    :is-showing="isShowing && !isSuspended"
     9→    :vertical-offset="verticalOffset"
    10→    :bounce="bounce"
    11→    :placement="placement"
    12→  />
    13→</template>
    14→
    15→<script setup lang="ts">
    16→import { ref, watch, onUnmounted, nextTick, shallowRef } from 'vue';
    17→import { useMounted, useIntervalFn, useEventListener } from '@vueuse/core';
    18→import type { Placement } from '@floating-ui/vue';
    19→import TutorialArrowInstance from './TutorialArrowInstance.vue';
    20→
    21→const MODAL_EVENT = 'ui:modal-visibility';
    22→
    23→interface Props {
    24→  /** CSS selector for the target element(s) to point to */
    25→  targetSelector: string;
    26→  /** Message to display in the tutorial */
    27→  message: string;
    28→  /** Icon to show alongside the message */
    29→  icon?: string;
    30→  /** Whether to show the tutorial arrow */
    31→  modelValue: boolean;
    32→  /** Auto-hide duration in milliseconds (0 = don't auto-hide) */
    33→  autoHideDuration?: number;
    34→  /** Vertical offset from target element in pixels */
    35→  verticalOffset?: number;
    36→  /** Whether to show bounce animation */
    37→  bounce?: boolean;
    38→  /** Placement of arrow relative to target */
    39→  placement?: Placement;
    40→}
    41→
    42→const props = withDefaults(defineProps<Props>(), {
    43→  icon: 'mdi:hand-pointing-up',
    44→  autoHideDuration: 5000,
    45→  verticalOffset: 40,
    46→  bounce: true,
    47→  placement: 'bottom'
    48→});
    49→
    50→const emit = defineEmits<{
    51→  'update:modelValue': [value: boolean];
    52→  'hidden': [];
    53→}>();
    54→
    55→const mounted = useMounted();
    56→const isShowing = ref(false);
    57→const targetElements = shallowRef<HTMLElement[]>([]);
    58→const autoHideTimer = ref<number | null>(null);
    59→const isSuspended = ref(false);
    60→let wasShowingBeforeSuspend = false;
    61→
    62→// WeakMap for stable element keys
    63→const elementKeyMap = new WeakMap<HTMLElement, string>();
    64→let keyCounter = 0;
    65→
    66→/**
    67→ * Get stable key for an element
    68→ */
    69→const getElementKey = (el: HTMLElement): string => {
    70→  if (!elementKeyMap.has(el)) {
    71→    elementKeyMap.set(el, `arrow-${++keyCounter}`);
    72→  }
    73→  return elementKeyMap.get(el)!;
    74→};
    75→
    76→/**
    77→ * Find all matching target elements
    78→ */
    79→const findTargets = (): HTMLElement[] => {
    80→  if (!mounted.value) return [];
    81→
    82→  const elements = Array.from(document.querySelectorAll(props.targetSelector)) as HTMLElement[];
    83→
    84→  if (elements.length > 0) {
    85→    console.log(`[TutorialArrow] ✅ Found ${elements.length} target element(s)`);
    86→  }
    87→
    88→  return elements;
    89→};
    90→
    91→/**
    92→ * Update target elements
    93→ */
    94→const updateTargets = async () => {
    95→  await nextTick();
    96→  const newTargets = findTargets();
    97→
    98→  if (newTargets.length > 0 && props.modelValue) {
    99→    targetElements.value = newTargets;
   100→    await nextTick();
   101→    isShowing.value = true;
   102→    startAutoHideTimer();
   103→  }
   104→};
   105→
   106→/**
   107→ * Poll for target elements (when active and targets not found)
   108→ */
   109→const { pause: pausePolling, resume: resumePolling } = useIntervalFn(() => {
   110→  if (props.modelValue && targetElements.value.length === 0) {
   111→    updateTargets();
   112→  }
   113→}, 100, { immediate: false });
   114→
   115→/**
   116→ * Start auto-hide timer
   117→ */
   118→const startAutoHideTimer = () => {
   119→  clearAutoHideTimer();
   120→  if (props.autoHideDuration > 0) {
   121→    autoHideTimer.value = window.setTimeout(() => {
   122→      hide();
   123→    }, props.autoHideDuration);
   124→  }
   125→};
   126→
   127→/**
   128→ * Clear auto-hide timer
   129→ */
   130→const clearAutoHideTimer = () => {
   131→  if (autoHideTimer.value !== null) {
   132→    clearTimeout(autoHideTimer.value);
   133→    autoHideTimer.value = null;
   134→  }
   135→};
   136→
   137→/**
   138→ * Hide the tutorial arrows
   139→ */
   140→const hide = () => {
   141→  console.log('[TutorialArrow] Hiding tutorial arrows');
   142→  isShowing.value = false;
   143→  emit('update:modelValue', false);
   144→  emit('hidden');
   145→  clearAutoHideTimer();
   146→};
   147→
   148→// Watch for modelValue changes
   149→watch(() => props.modelValue, async (newValue) => {
   150→  console.log('[TutorialArrow] modelValue changed to:', newValue);
   151→
   152→  if (newValue && mounted.value) {
   153→    console.log('[TutorialArrow] Attempting to show tutorial...');
   154→    await updateTargets();
   155→
   156→    if (targetElements.value.length === 0) {
   157→      console.log('[TutorialArrow] ⏳ Targets not found, starting polling...');
   158→      resumePolling();
   159→    }
   160→  } else if (!newValue) {
   161→    console.log('[TutorialArrow] Hiding tutorial');
   162→    pausePolling();
   163→    isShowing.value = false;
   164→    clearAutoHideTimer();
   165→    // Keep targetElements for efficient re-showing
   166→  }
   167→}, { immediate: true });
   168→
   169→// Watch for target selector changes
   170→watch(() => props.targetSelector, async () => {
   171→  console.log('[TutorialArrow] Target selector changed:', props.targetSelector);
   172→  if (props.modelValue) {
   173→    targetElements.value = [];
   174→    await updateTargets();
   175→  }
   176→});
   177→
   178→// Listen for modal visibility events to suspend/resume arrows (client-side only)
   179→if (typeof window !== 'undefined') {
   180→  useEventListener(window, MODAL_EVENT, (event: Event) => {
   181→    const customEvent = event as CustomEvent<{ isOpen: boolean }>;
   182→
   183→    if (customEvent.detail.isOpen) {
   184→      // Modal opened - suspend arrows
   185→      console.log('[TutorialArrow] Modal opened, suspending arrows', {
   186→        modelValue: props.modelValue,
   187→        isShowing: isShowing.value
   188→      });
   189→
   190→      // Track if tutorial was active (modelValue), not just if arrows were visible (isShowing)
   191→      // Arrows might be hidden by IntersectionObserver even when tutorial is active
   192→      if (props.modelValue) {
   193→        wasShowingBeforeSuspend = true;
   194→        clearAutoHideTimer();
   195→        if (isShowing.value) {
   196→          isShowing.value = false;
   197→        }
   198→        console.log('[TutorialArrow] Set wasShowingBeforeSuspend = true');
   199→      }
   200→      isSuspended.value = true;
   201→    } else {
   202→      // Modal closed - resume arrows
   203→      console.log('[TutorialArrow] Modal closed, resuming arrows', {
   204→        wasShowingBeforeSuspend,
   205→        modelValue: props.modelValue,
   206→        isSuspended: isSuspended.value
   207→      });
   208→
   209→      isSuspended.value = false;
   210→      if (wasShowingBeforeSuspend && props.modelValue) {
   211→        wasShowingBeforeSuspend = false;
   212→        console.log('[TutorialArrow] Calling updateTargets to restore arrows');
   213→        updateTargets();  // Rebind in case DOM shifted
   214→        // Timer will be restarted in updateTargets if needed
   215→      } else {
   216→        console.log('[TutorialArrow] NOT restoring arrows:', {
   217→          wasShowingBeforeSuspend,
   218→          modelValue: props.modelValue
   219→        });
   220→      }
   221→    }
   222→  });
   223→}
   224→
   225→// Clean up on unmount
   226→onUnmounted(() => {
   227→  pausePolling();
   228→  clearAutoHideTimer();
   229→});
   230→
   231→defineExpose({
   232→  hide
   233→});
   234→</script>
