     1→<template>
     2→  <div
     3→    :class="[
     4→      'rounded-lg p-4 border',
     5→      variantClasses,
     6→      dismissable && 'pr-12 relative'
     7→    ]"
     8→  >
     9→    <div class="flex items-start">
    10→      <Icon
    11→        v-if="icon"
    12→        :icon="icon"
    13→        :class="['flex-shrink-0 mr-3 mt-0.5', iconSizeClass]"
    14→      />
    15→      <div class="flex-1">
    16→        <h4 v-if="title" :class="['font-medium', titleColorClass]">
    17→          {{ title }}
    18→        </h4>
    19→        <p v-if="message" :class="['text-sm', messageColorClass, title && 'mt-1']">
    20→          {{ message }}
    21→        </p>
    22→        <slot />
    23→
    24→        <!-- Retry button -->
    25→        <button
    26→          v-if="retry"
    27→          @click="handleRetry"
    28→          class="mt-3 text-sm font-medium underline hover:no-underline transition-all"
    29→          :class="actionColorClass"
    30→        >
    31→          Try Again
    32→        </button>
    33→      </div>
    34→
    35→      <!-- Dismiss button -->
    36→      <button
    37→        v-if="dismissable"
    38→        @click="handleDismiss"
    39→        class="absolute top-3 right-3 rounded-full p-1 hover:bg-black/5 dark:hover:bg-white/5 transition-colors"
    40→        :class="dismissColorClass"
    41→        aria-label="Dismiss"
    42→      >
    43→        <Icon icon="mdi:close" class="w-4 h-4" />
    44→      </button>
    45→    </div>
    46→  </div>
    47→</template>
    48→
    49→<script setup lang="ts">
    50→import { computed } from 'vue';
    51→import { Icon } from '@iconify/vue';
    52→
    53→export type StatusBannerVariant = 'success' | 'error' | 'warning' | 'info';
    54→
    55→interface Props {
    56→  /**
    57→   * Banner variant/type
    58→   * @default 'info'
    59→   */
    60→  variant?: StatusBannerVariant;
    61→
    62→  /**
    63→   * Icon to display (optional, defaults based on variant)
    64→   */
    65→  icon?: string;
    66→
    67→  /**
    68→   * Banner title (optional)
    69→   */
    70→  title?: string;
    71→
    72→  /**
    73→   * Banner message (optional)
    74→   */
    75→  message?: string;
    76→
    77→  /**
    78→   * Icon size
    79→   * @default 'md'
    80→   */
    81→  iconSize?: 'sm' | 'md' | 'lg';
    82→
    83→  /**
    84→   * Whether the banner can be dismissed
    85→   * @default false
    86→   */
    87→  dismissable?: boolean;
    88→
    89→  /**
    90→   * Retry callback for error states
    91→   */
    92→  retry?: () => void | Promise<void>;
    93→}
    94→
    95→const props = withDefaults(defineProps<Props>(), {
    96→  variant: 'info',
    97→  iconSize: 'md',
    98→  dismissable: false
    99→});
   100→
   101→const emit = defineEmits<{
   102→  /**
   103→   * Emitted when dismiss button is clicked
   104→   */
   105→  dismiss: [];
   106→
   107→  /**
   108→   * Emitted when retry button is clicked
   109→   */
   110→  retry: [];
   111→}>();
   112→
   113→// Default icons for each variant
   114→const defaultIcons = {
   115→  success: 'mdi:check-circle',
   116→  error: 'mdi:alert-circle',
   117→  warning: 'mdi:alert',
   118→  info: 'mdi:information'
   119→};
   120→
   121→const iconToDisplay = computed(() => {
   122→  return props.icon || defaultIcons[props.variant];
   123→});
   124→
   125→// Variant-specific classes
   126→const variantClasses = computed(() => {
   127→  const variants = {
