    64→<script setup lang="ts">
    65→import { computed } from 'vue';
    66→import { Icon } from '@iconify/vue';
    67→import { useOnboardingFlow } from '@composables/useOnboardingFlow';
    68→import { useSubStepHelpers } from '@composables/useSubStepHelpers';
    69→import type { Platform } from '@/types/onboarding';
    70→
    71→interface PlatformOption {
    72→  value: string;
    73→  name: string;
    74→  icon: string;
    75→}
    76→
    77→interface Props {
    78→  /**
    79→   * List of platform options to display
    80→   */
    81→  platforms: PlatformOption[];
    82→
    83→  /**
    84→   * Currently selected platform(s)
    85→   * Can be a single value or array for multi-select
    86→   */
    87→  selected?: string | string[];
    88→
    89→  /**
    90→   * Custom disabled check function
    91→   * @param platform - Platform value to check
    92→   * @returns true if platform should be disabled
    93→   */
    94→  isDisabled?: (platform: string) => boolean;
    95→}
    96→
    97→const props = withDefaults(defineProps<Props>(), {
    98→  selected: () => [],
    99→  isDisabled: () => false
   100→});
   101→
   102→const emit = defineEmits<{
   103→  /**
   104→   * Emitted when a platform is selected
   105→   */
   106→  select: [platform: string];
   107→}>();
   108→
   109→const helpers = useSubStepHelpers();
   110→const { getConnectedCountForPlatform, accountsByPlatform } = useOnboardingFlow();
   111→
   112→// Normalize selected to always be an array for consistent handling
   113→const selectedPlatforms = computed(() => {
   114→  if (Array.isArray(props.selected)) {
   115→    return props.selected;
   116→  }
   117→  return props.selected ? [props.selected] : [];
   118→});
   119→
   120→// Create a reactive computed map of platform counts
   121→// This ensures component re-renders when accountsByPlatform changes (after cache refresh)
   122→const platformCounts = computed(() => {
   123→  // Explicitly depend on accountsByPlatform to track reactivity
   124→  const accounts = accountsByPlatform.value;
   125→
   126→  // Build map of all platform counts
   127→  const counts: Record<string, number> = {};
   128→  props.platforms.forEach(platform => {
   129→    counts[platform.value] = getConnectedCountForPlatform(platform.value);
   130→  });
   131→
   132→  return counts;
   133→});
   134→
   135→// Get connected count for a platform (now uses reactive computed)
   136→const getConnectedCount = (platform: string) => {
   137→  return platformCounts.value[platform] || 0;
   138→};
   139→
   140→// Get card classes based on selection state
   141→const getCardClasses = (platform: string) => {
   142→  const isSelected = selectedPlatforms.value.includes(platform);
   143→  const disabled = props.isDisabled(platform);
   144→
   145→  const baseClasses = [
   146→    'group relative p-6 border-2 rounded-2xl text-center',
   147→    'transition-all duration-300',
   148→    'focus:outline-none focus:ring-4 focus:ring-primary-500/30',
   149→    'overflow-hidden'
   150→  ];
   151→
   152→  if (disabled) {
   153→    return [
   154→      ...baseClasses,
   155→      'border-gray-300 dark:border-gray-600',
   156→      'bg-gray-100 dark:bg-gray-800/50',
   157→      'opacity-60 cursor-not-allowed'
   158→    ].join(' ');
   159→  }
   160→
   161→  if (isSelected) {
   162→    return [
   163→      ...baseClasses,
   164→      'border-primary-500 bg-gradient-to-br from-primary-50 to-purple-50',
   165→      'dark:from-primary-900/30 dark:to-purple-900/30',
   166→      'shadow-xl shadow-primary-500/20'
   167→    ].join(' ');
   168→  }
   169→
   170→  return [
   171→    ...baseClasses,
   172→    'border-gray-200 dark:border-gray-700',
   173→    'hover:border-primary-300 dark:hover:border-primary-600',
   174→    'hover:bg-gradient-to-br hover:from-primary-50/50 hover:to-purple-50/50',
   175→    'dark:hover:from-primary-900/10 dark:hover:to-purple-900/10',
   176→    'hover:scale-105 hover:shadow-lg cursor-pointer'
   177→  ].join(' ');
   178→};
   179→
   180→</script>
