  <div class="flex items-center gap-4 mb-6">
    <div class="theater-number">
      {{ number }}
    </div>
    <h3 class="text-2xl font-bold text-gray-900 dark:text-white">
      {{ title }}
    </h3>
  </div>
</template>

<script setup lang="ts">
defineProps<{
  number: string
  title: string
}>()
</script>

<style scoped>
.theater-number {
  width: 48px;
  height: 48px;
  background: linear-gradient(135deg, #007c89 0%, #7c3aed 100%);
  border-radius: 50%;
  display: flex;
  align-items: center;
  justify-content: center;
