import { useEventBus } from '@vueuse/core'

// Centralized debug control - must be explicitly enabled
const ANIMATION_DEBUG = typeof localStorage !== 'undefined' && localStorage.getItem('animation-debug') === 'true'

export const debugLog = (component: string, event: string, data?: any) => {
  if (ANIMATION_DEBUG) {
    console.log(`[${component}] ${event}`, data)
  }
}

export type AnimationBusEvents = {
  'orchestrate-start': { mode: 'rotation' | 'unison', cycleDuration: number }
  'orb-pulse': { intensity: number }
  'pathway-activate': { index: number, provider: string, delay: number }
  'spark-travel': { pathwayIndex: number, duration: number }
  'icon-pulse': { pathwayIndex: number, intensity: number }
  'bubble-cycle-start': undefined
  'animation-complete': { component: string, type: 'enter' | 'exit' }
  'stop-all': undefined
}

// Create individual event buses for each event type
const orchestrateStartBus = useEventBus('orchestrate-start')
const pathwayActivateBus = useEventBus('pathway-activate')
const bubbleCycleStartBus = useEventBus('bubble-cycle-start')
const stopAllBus = useEventBus('stop-all')

export const useAnimationBus = () => {
  return {
    on: (event: string, listener: (payload?: any) => void) => {
      switch (event) {
        case 'orchestrate-start':
          return orchestrateStartBus.on(listener)
        case 'pathway-activate':
          return pathwayActivateBus.on(listener)
        case 'bubble-cycle-start':
          return bubbleCycleStartBus.on(listener)
        case 'stop-all':
          return stopAllBus.on(listener)
        default:
          console.warn(`AnimationBus: Unknown event type '${event}'`)
          return () => {}
      }
    },

    emit: (event: string, payload?: any) => {
      switch (event) {
        case 'orchestrate-start':
          orchestrateStartBus.emit(payload)
          break
        case 'pathway-activate':
          pathwayActivateBus.emit(payload)
          break
        case 'bubble-cycle-start':
          bubbleCycleStartBus.emit(payload)
          break
        case 'stop-all':
          stopAllBus.emit(payload)
          break
        default:
          console.warn(`AnimationBus: Unknown event type '${event}'`)
      }
    },

    // Force stop all animations bypassing normal guards
    forceStopAll: () => {
      stopAllBus.emit(undefined)
    },

    reset: () => {
      orchestrateStartBus.reset()
      pathwayActivateBus.reset()
      bubbleCycleStartBus.reset()
      stopAllBus.reset()
    },

    // Cleanup method for component unmounting
    cleanup: () => {
      stopAllBus.emit(undefined)
      orchestrateStartBus.reset()
      pathwayActivateBus.reset()
      bubbleCycleStartBus.reset()
      stopAllBus.reset()
    }
  }
}

